--

INSERT INTO `hotel` (`id`, `title`, `content`, `status_hotel`, `slug`) VALUES
(2, 'KHÁCH SẠN THÚ CƯNG MYPET', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\"><strong>KH&Aacute;CH SẠN TH&Uacute; CƯNG TẠI MYVET &ndash; DỊCH VỤ KH&Aacute;CH SẠN H&Agrave;NG ĐẦU TẠI H&Agrave; NỘI</strong></span><br />\r\n<span style=\"font-size:18px\"><em>Với những người xem th&uacute; cưng l&agrave; bạn, l&agrave; một th&agrave;nh vi&ecirc;n của gia đ&igrave;nh th&igrave; việc người đ&oacute; chăm s&oacute;c sức khỏe, sắc đẹp cho ch&uacute;ng cũng l&agrave; điều ho&agrave;n to&agrave;n dễ hiểu.</em><br />\r\nThấu hiểu điều n&agrave;y,&nbsp;<strong>Myvet</strong>&nbsp;cung cấp dịch vụ kh&aacute;ch sạn, tr&ocirc;ng giữ ch&oacute; m&egrave;o chất lượng cao bao gồm:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Hệ thống ph&ograve;ng ri&ecirc;ng hiện đại, rộng r&atilde;i d&agrave;nh cho c&uacute;n, d&agrave;nh cho m&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Kh&ocirc;ng gian thoải m&aacute;i với hệ thống điều h&ograve;a ph&ugrave; hợp với nhiệt độ cơ thể th&uacute; cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Thức ăn cao cấp, đảm bảo dinh dưỡng d&agrave;nh ri&ecirc;ng cho từng lứa tuổi th&uacute; cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Trong suốt qu&aacute; tr&igrave;nh lưu tr&uacute; tại bệnh viện, được c&aacute;c b&aacute;c sĩ kiểm tra v&agrave; theo d&otilde;i h&agrave;ng ng&agrave;y</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Tất cả c&aacute;c ph&ograve;ng đều được trang bị hệ thống camera đảm bảo an ninh 24/24</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><img alt=\"Khách Sạn Chó Mèo | Trông Giữ Thú Cưng Giá Rẻ\" src=\"https://dogily.vn/wp-content/uploads/2020/06/phong-cho-cho-nho-tai-khach-san-cho-meo.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Với đội ngũ nh&acirc;n vi&ecirc;n c&oacute; tinh thần tr&aacute;ch nhiệm cao, Myvet lu&ocirc;n sẵn l&ograve;ng lắng nghe, tư vấn, b&ecirc;n cạnh bạn trong mọi vấn đề khi chăm s&oacute;c v&agrave; nu&ocirc;i dạy th&uacute; cưng.<br />\r\n<strong>Myvet</strong>&nbsp;đảm bảo th&uacute; cưng sẽ được chăm s&oacute;c tốt nhất với m&ocirc;i trường v&agrave; chế độ ăn uống, sinh hoạt hợp l&yacute;, khoa học.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><strong><em>Ngo&agrave;i dịch vụ cắt tỉa l&ocirc;ng cho th&uacute; cưng, Myvet c&ograve;n cung cấp c&aacute;c dịch vụ kh&aacute;c như:</em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Spa Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Ti&ecirc;m Ph&ograve;ng Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">&nbsp;Chụp X-Quang Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">&nbsp;Si&ecirc;u &Acirc;m Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Phẫu Thuật Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Triệt Sản Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">&nbsp;Xuất Nhập Cảnh Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Hỏa T&aacute;ng Th&uacute; Cưng</span></span></li>\r\n</ul>\r\n', 1, ''),
(5, 'Khách sạn MYPET', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>H&Aacute;CH SẠN TH&Uacute; CƯNG TẠI MYVET &ndash; DỊCH VỤ KH&Aacute;CH SẠN H&Agrave;NG ĐẦU TẠI H&Agrave; NỘI</strong><br />\r\n<em>Với những người xem th&uacute; cưng l&agrave; bạn, l&agrave; một th&agrave;nh vi&ecirc;n của gia đ&igrave;nh th&igrave; việc người đ&oacute; chăm s&oacute;c sức khỏe, sắc đẹp cho ch&uacute;ng cũng l&agrave; điều ho&agrave;n to&agrave;n dễ hiểu.</em><br />\r\nThấu hiểu điều n&agrave;y,&nbsp;<strong>Myvet</strong>&nbsp;cung cấp dịch vụ kh&aacute;ch sạn, tr&ocirc;ng giữ ch&oacute; m&egrave;o chất lượng cao bao gồm:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hệ thống ph&ograve;ng ri&ecirc;ng hiện đại, rộng r&atilde;i d&agrave;nh cho c&uacute;n, d&agrave;nh cho m&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kh&ocirc;ng gian thoải m&aacute;i với hệ thống điều h&ograve;a ph&ugrave; hợp với nhiệt độ cơ thể th&uacute; cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Thức ăn cao cấp, đảm bảo dinh dưỡng d&agrave;nh ri&ecirc;ng cho từng lứa tuổi th&uacute; cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Trong suốt qu&aacute; tr&igrave;nh lưu tr&uacute; tại bệnh viện, được c&aacute;c b&aacute;c sĩ kiểm tra v&agrave; theo d&otilde;i h&agrave;ng ng&agrave;y</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Tất cả c&aacute;c ph&ograve;ng đều được trang bị hệ thống camera đảm bảo an ninh 24/24</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><img alt=\"Khách Sạn Chó Mèo | Trông Giữ Thú Cưng Giá Rẻ\" src=\"https://dogily.vn/wp-content/uploads/2020/06/phong-cho-cho-nho-tai-khach-san-cho-meo.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Với đội ngũ nh&acirc;n vi&ecirc;n c&oacute; tinh thần tr&aacute;ch nhiệm cao, Myvet lu&ocirc;n sẵn l&ograve;ng lắng nghe, tư vấn, b&ecirc;n cạnh bạn trong mọi vấn đề khi chăm s&oacute;c v&agrave; nu&ocirc;i dạy th&uacute; cưng.<br />\r\n<strong>Myvet</strong>&nbsp;đảm bảo th&uacute; cưng sẽ được chăm s&oacute;c tốt nhất với m&ocirc;i trường v&agrave; chế độ ăn uống, sinh hoạt hợp l&yacute;, khoa học.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>Ngo&agrave;i dịch vụ cắt tỉa l&ocirc;ng cho th&uacute; cưng, Myvet c&ograve;n cung cấp c&aacute;c dịch vụ kh&aacute;c như:</em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Spa Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Ti&ecirc;m Ph&ograve;ng Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Chụp X-Quang Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Si&ecirc;u &Acirc;m Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Phẫu Thuật Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Triệt Sản Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Xuất Nhập Cảnh Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hỏa T&aacute;ng Th&uacute; Cưng</span></span></li>\r\n</ul>\r\n', 0, ''),
