--
-- Cấu trúc bảng cho bảng `news`
--

CREATE TABLE `news` (
  `id` int(11) NOT NULL,
  `title` varchar(255) NOT NULL,
  `content` longtext NOT NULL,
  `image` varchar(255) NOT NULL,
  `date` date NOT NULL,
  `status_news` int(11) NOT NULL,
  `slug` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `news`
--

INSERT INTO `news` (`id`, `title`, `content`, `image`, `date`, `status_news`, `slug`) VALUES
(1, 'Đi tìm địa chỉ chăm sóc thú cưng uy tín', '<p><span style=\"font-size:18px\">Nếu bạn đang t&igrave;m kiếm một địa chỉ chăm s&oacute;c th&uacute; cưng uy t&iacute;n tại H&agrave; Nội, chắc chắn bạn kh&ocirc;ng n&ecirc;n bỏ quả Myvet. Đ&acirc;y l&agrave; một trong những bệnh viện th&uacute; y h&agrave;ng đầu khu vực miền Bắc. Đầy đủ thiết bị v&agrave; cơ sở hạ tầng cao cấp cho kh&aacute;ch h&agrave;ng h&agrave;i l&ograve;ng khi trải nghiệm. Để t&igrave;m hiểu r&otilde; hơn về địa chỉ chăm s&oacute;c th&uacute; cưng uy t&iacute;n tại H&agrave; Nội, h&atilde;y xem ngay b&agrave;i viết dưới đ&acirc;y.</span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong>1. Ti&ecirc;u chuẩn để đ&aacute;nh gi&aacute; địa chỉ chăm s&oacute;c th&uacute; cưng uy t&iacute;n</strong></span></p>\r\n\r\n<p><em><span style=\"font-size:18px\">1.1 Chất lượng kh&aacute;m chữa bệnh</span></em></p>\r\n\r\n<p><span style=\"font-size:18px\">Để đ&aacute;nh gi&aacute; một ph&ograve;ng kh&aacute;m c&oacute; uy t&iacute;n hay kh&ocirc;ng, đầu ti&ecirc;n phải xem chất lượng kh&aacute;m chữa bệnh của họ. B&aacute;c sĩ chẩn đo&aacute;n đ&uacute;ng bệnh v&agrave; chữa bệnh đ&uacute;ng phương ph&aacute;p sẽ gi&uacute;p th&uacute; cưng nhanh ch&oacute;ng hồi phục sức khỏe. C&ograve;n đối với những nơi muốn nhiều tiền từ kh&aacute;ch h&agrave;ng, họ sẽ k&eacute;o d&agrave;i thời gian chữa trị l&acirc;u hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-2.jpg\"><img alt=\"\" height=\"500\" sizes=\"(max-width: 750px) 100vw, 750px\" src=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-2.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-2.jpg 750w, https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-2-600x400.jpg 600w\" width=\"750\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Địa chỉ chăm s&oacute;c th&uacute; cưng uy t&iacute;n h&agrave;ng đầu tại H&agrave; Nội</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Địa chỉ kh&aacute;m bệnh kh&ocirc;ng uy t&iacute;n thường sẽ chẩn đo&aacute;n mơ hồ về bệnh của th&uacute; cưng. Sau đ&oacute; sẽ tư vấn bạn n&ecirc;n mua nhiều loại thuốc để chữa trị. Tuy nhi&ecirc;n, sự kết hợp của c&aacute;c loại thuốc đ&oacute; sẽ kh&ocirc;ng tạo hiệu quả tốt cho sức khỏe của vật nu&ocirc;i. N&oacute; sẽ chỉ t&aacute;c động nhẹ đến bệnh v&agrave; l&acirc;u l&agrave;nh bệnh hơn. B&ecirc;n cạnh đ&oacute;, việc d&ugrave;ng nhiều thuốc l&ecirc;n cơ thể th&uacute; cưng c&oacute; thể g&acirc;y n&ecirc;n những phản ứng phụ ảnh hưởng đến sức khỏe.</span></p>\r\n\r\n<p><em><span style=\"font-size:18px\">1.2 Tr&igrave;nh độ chuy&ecirc;n m&ocirc;n của b&aacute;c sĩ</span></em></p>\r\n\r\n<p><span style=\"font-size:18px\">Ph&ograve;ng kh&aacute;m sở hữu đội ngũ y b&aacute;c sĩ c&oacute; tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cao sẽ tạo n&ecirc;n chất lượng kh&aacute;m chữa bệnh. Nhờ c&oacute; kinh nghiệm l&acirc;u d&agrave;i trong c&ocirc;ng việc, họ sẽ chẩn đo&aacute;n ch&iacute;nh x&aacute;c bệnh của th&uacute; cưng. B&ecirc;n cạnh đ&oacute;, họ rất chuy&ecirc;n nghiệp trong việc lựa chọn phương ph&aacute;p chữa bệnh. Sử dụng loại thuốc n&agrave;o cho hiệu quả, thực đơn ăn uống như thế n&agrave;o đều sẽ do b&aacute;c sĩ quyết định.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-3.jpg\"><img alt=\"\" height=\"477\" sizes=\"(max-width: 800px) 100vw, 800px\" src=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-3.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-3.jpg 800w, https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-3-600x358.jpg 600w, https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-3-768x458.jpg 768w\" width=\"800\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>B&aacute;c sĩ th&uacute; y c&oacute; tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cao tại Myvet</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">C&ugrave;ng với đ&oacute;, họ sẽ tư vấn cho người chủ n&ecirc;n chăm s&oacute;c th&uacute; cưng như thế n&agrave;o để ch&uacute;ng nhanh ch&oacute;ng l&agrave;nh bệnh. C&oacute; những lời khuy&ecirc;n về chế độ ăn uống, c&aacute;ch chăm s&oacute;c vật nu&ocirc;i khoa học trong cuộc sống h&agrave;ng ng&agrave;y. Với c&aacute;ch l&agrave;m việc như vậy, kh&aacute;ch h&agrave;ng n&agrave;o cũng sẽ h&agrave;i l&ograve;ng với chất lượng dịch vụ của ph&ograve;ng kh&aacute;m. Tạo cơ hội cho địa chỉ kh&aacute;m bệnh đ&oacute; ph&aacute;t triển tốt hơn về l&acirc;u về d&agrave;i.</span></p>\r\n\r\n<p><em><span style=\"font-size:18px\">1.3 Cơ sở vật chất hiện đại</span></em></p>\r\n\r\n<p><span style=\"font-size:18px\">Cơ sở vật chất cũng l&agrave; một yếu tố để đ&aacute;nh gi&aacute; chất lượng uy t&iacute;n của ph&ograve;ng kh&aacute;m. Việc đầu tư v&agrave;o hệ thống cơ sở vật chất ch&iacute;nh l&agrave; đầu tư v&agrave;o kh&aacute;ch h&agrave;ng. T&acirc;m l&yacute; chung của những người nu&ocirc;i th&uacute; cưng đều mong muốn &ldquo;con&rdquo; của m&igrave;nh sẽ được kh&aacute;m chữa bệnh trong m&ocirc;i trường tốt nhất. Do đ&oacute;, những địa chỉ sử dụng c&ocirc;ng nghệ kh&aacute;m chữa bệnh hiện đại thường được kh&aacute;ch h&agrave;ng đ&aacute;nh gi&aacute; rất cao.</span></p>\r\n\r\n<p><em><span style=\"font-size:18px\">1.4 Ch&iacute;nh s&aacute;ch chăm s&oacute;c kh&aacute;ch h&agrave;ng&nbsp;</span></em></p>\r\n\r\n<p><span style=\"font-size:18px\">Một ph&ograve;ng kh&aacute;m th&uacute; y uy t&iacute;n sẽ c&oacute; ch&iacute;nh s&aacute;ch chăm s&oacute;c kh&aacute;ch h&agrave;ng tốt. Họ sẽ giải quyết hợp l&yacute; v&agrave; nhanh ch&oacute;ng những c&acirc;u hỏi kh&aacute;ch h&agrave;ng đặt ra. Đặc biệt l&agrave; ch&iacute;nh s&aacute;ch chăm s&oacute;c sau khi sử dụng dịch vụ của kh&aacute;ch h&agrave;ng. Họ c&oacute; thể nhắn tin hoặc gọi điện thoại để trao đổi trực tiếp về sức khỏe của vật nu&ocirc;i thường xuy&ecirc;n. B&ecirc;n cạnh đ&oacute; kết hợp th&ecirc;m những khuyến m&atilde;i về dịch vụ cho kh&aacute;ch h&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong>2. Myvet&ndash; Địa chỉ chăm s&oacute;c th&uacute; cưng uy t&iacute;n h&agrave;ng đầu H&agrave; Nội</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi.jpg\"><img alt=\"\" height=\"386\" src=\"https://myvet.vn/wp-content/uploads/2021/03/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi.jpg\" width=\"600\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Myvet &ndash; địa chỉ ph&ograve;ng kh&aacute;m th&uacute; y chất lượng tại H&agrave; Nội</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Myvet l&agrave; một trong những bệnh viện th&uacute; y h&agrave;ng đầu tại khu vực H&agrave; Nội. Với nhiều năm hoạt động trong lĩnh vực chăm s&oacute;c th&uacute; cưng, Myvet đ&atilde; tạo được sự uy t&iacute;n chất lượng với đ&ocirc;ng đảo kh&aacute;ch h&agrave;ng. Họ tin tưởng lựa chọn Myvet l&agrave; nơi thăm kh&aacute;m v&agrave; chăm s&oacute;c vật nu&ocirc;i của m&igrave;nh.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">B&agrave;i viết tr&ecirc;n đ&atilde; cung cấp th&ecirc;m th&ocirc;ng tin về địa chỉ kh&aacute;m bệnh cho th&uacute; cưng uy t&iacute;n tại H&agrave; Nội. Nếu c&oacute; nhu cầu chăm s&oacute;c v&agrave; chữa trị bệnh cho vật nu&ocirc;i, bạn c&oacute; thể đến ngay với Myvet . Ch&uacute;ng t&ocirc;i cam kết sẽ mang đến chất lượng dịch vụ tốt nhất cho Qu&yacute; kh&aacute;ch h&agrave;ng!</span></p>\r\n', './image/dia-chi-cham-soc-thu-cung-uy-tin-ha-noi-3.jpg', '2022-12-05', 1, ''),
(2, 'Tầm quan trọng của việc tỉa lông cho thú cưng', '<p><span style=\"font-size:18px\">Đối với th&uacute; cưng, chăm s&oacute;c l&ocirc;ng l&agrave; một qu&aacute; tr&igrave;nh rất quan trọng. Việc tỉa l&ocirc;ng kh&ocirc;ng chỉ tạo n&ecirc;n vẻ ngo&agrave;i xuất sắc m&agrave; c&ograve;n t&aacute;c động lớn đến sức khỏe của ch&uacute;ng. Do đ&oacute;, nếu đang nu&ocirc;i th&uacute; cưng th&igrave; bạn n&ecirc;n bỏ ra thời gian để t&igrave;m hiểu th&ecirc;m về vai tr&ograve; của việc tỉa l&ocirc;ng để chăm s&oacute;c ch&uacute;ng tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong>1. V&igrave; sao n&ecirc;n tỉa l&ocirc;ng cho th&uacute; cưng?</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Cắt tỉa l&ocirc;ng cho ch&oacute; m&egrave;o đ&oacute;ng vai tr&ograve; rất quan trọng đối với sức khỏe v&agrave; sự ph&aacute;t triển của ch&uacute;ng. Kh&aacute;c với c&aacute;c con vật kh&aacute;c, ch&oacute; l&agrave; lo&agrave;i thải nhiệt th&ocirc;ng qua hai tuyến mồ h&ocirc;i tr&ecirc;n cơ thể. Ngo&agrave;i tuyến ở dưới lưỡi c&ograve;n c&oacute; tuyến mồ h&ocirc;i ở gan b&agrave;n ch&acirc;n của ch&oacute;. Do đ&oacute;, đối với những ch&uacute; ch&oacute; c&oacute; l&ocirc;ng qu&aacute; d&agrave;y ở gan b&agrave;n ch&acirc;n sẽ g&acirc;y b&iacute; tắc sự tho&aacute;t nhiệt. Đ&acirc;y ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y n&ecirc;n th&acirc;n nhiệt cao v&agrave; dễ bị bệnh v&agrave;o trời nắng n&oacute;ng của ch&oacute; m&egrave;o. Theo lời khuy&ecirc;n từ c&aacute;c b&aacute;c sĩ th&uacute; y, người chủ n&ecirc;n cắt tỉa l&ocirc;ng thường xuy&ecirc;n cho con cưng của m&igrave;nh để đảm bảo sức khỏe của c&aacute;c b&eacute;.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Đặc biệt l&agrave; v&agrave;o thời tiết nắng n&oacute;ng, bạn n&ecirc;n ch&uacute; &yacute; về l&ocirc;ng của ch&oacute; m&egrave;o v&agrave; tỉa gọn cho ch&uacute;ng.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><strong><a href=\"https://myvet.vn/wp-content/uploads/2021/03/nhung-luu-y-nen-nho-khi-cat-tia-long.jpg\"><img alt=\"\" height=\"486\" sizes=\"(max-width: 624px) 100vw, 624px\" src=\"https://myvet.vn/wp-content/uploads/2021/03/nhung-luu-y-nen-nho-khi-cat-tia-long.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/03/nhung-luu-y-nen-nho-khi-cat-tia-long.jpg 624w, https://myvet.vn/wp-content/uploads/2021/03/nhung-luu-y-nen-nho-khi-cat-tia-long-600x467.jpg 600w\" width=\"624\" /></a></strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Ch&oacute; x&ugrave; cắt tỉa l&ocirc;ng cực đ&aacute;ng y&ecirc;u</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Hiện nay c&oacute; rất nhiều bệnh viện th&uacute; y c&oacute; dịch vụ cắt tỉa l&ocirc;ng cho vật cưng. Nếu bạn qu&aacute; bận rộn th&igrave; c&oacute; thể đăng k&yacute; lịch h&agrave;ng th&aacute;ng để c&aacute;c b&eacute; được chăm s&oacute;c tốt nhất. Nhờ đ&oacute; m&agrave; th&uacute; cưng của bạn lu&ocirc;n khỏe mạnh, sạch sẽ, v&agrave; tiết kiệm được thời gian l&agrave;m việc cho ch&iacute;nh m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong>2. Lợi &iacute;ch của việc tỉa l&ocirc;ng cho th&uacute; cưng</strong></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>2.1 Ph&ograve;ng tr&aacute;nh c&aacute;c loại bệnh về da v&agrave; l&ocirc;ng</em>&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">Như mọi người đ&atilde; biết, kh&iacute; hậu của Việt Nam l&agrave; nhiệt đới gi&oacute; m&ugrave;a ẩm n&ecirc;n thời tiết thường nắng n&oacute;ng k&eacute;o d&agrave;i. Đ&acirc;y ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y n&ecirc;n t&igrave;nh trạng bết l&ocirc;ng, bệnh tật cho th&uacute; cưng. Do đ&oacute;, bạn n&ecirc;n cắt tỉa l&ocirc;ng thường xuy&ecirc;n cho ch&uacute;ng, đặc biệt ch&uacute; &yacute; hơn khi v&agrave;o m&ugrave;a n&oacute;ng ẩm.</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/cat-tia-long-cho.jpg\"><img alt=\"\" height=\"385\" src=\"https://myvet.vn/wp-content/uploads/2021/03/cat-tia-long-cho.jpg\" width=\"512\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Cắt tỉa l&ocirc;ng v&agrave; tắm rửa th&uacute; cưng rất hot</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Tương tự như chế độ dinh dưỡng, việc cắt tỉa l&ocirc;ng cực kỳ quan trọng trong qu&aacute; tr&igrave;nh sinh trưởng của th&uacute; cưng. Nếu để l&ocirc;ng qu&aacute; d&agrave;i v&agrave; b&iacute;t tắc lỗ ch&acirc;n l&ocirc;ng sẽ dễ xảy da c&aacute;c bệnh về da như ngứa r&aacute;t, nổi sảy hay bị k&yacute; sinh tr&ugrave;ng. Đặc biệt, cắt tỉa l&ocirc;ng ngắn lại sẽ tiết kiệm được thời gian l&agrave;m kh&ocirc; l&ocirc;ng, sạch l&ocirc;ng của con vật sau khi tắm rửa.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">2.2&nbsp;<em>Tạo n&ecirc;n vẻ đẹp ngoại h&igrave;nh xuất sắc cho th&uacute; cưng</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngo&agrave;i ph&ograve;ng tr&aacute;nh c&aacute;c bệnh tật, việc cắt tỉa l&ocirc;ng mang &yacute; nghĩa to lớn đối với vẻ đẹp của th&uacute; cưng. Bạn c&oacute; thể thoải m&aacute;i tạo kiểu l&ocirc;ng mới cho ch&uacute;ng theo &yacute; bạn muốn, theo những kiểu hot trend độc lạ. Nhờ đ&oacute;, th&uacute; cưng của bạn sẽ trở n&ecirc;n đẹp hơn, kh&aacute;c biệt hơn so với giống lo&agrave;i của m&igrave;nh.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/cat-tia-long-cho-meo.jpg\"><img alt=\"\" height=\"818\" sizes=\"(max-width: 800px) 100vw, 800px\" src=\"https://myvet.vn/wp-content/uploads/2021/03/cat-tia-long-cho-meo.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/03/cat-tia-long-cho-meo.jpg 800w, https://myvet.vn/wp-content/uploads/2021/03/cat-tia-long-cho-meo-600x614.jpg 600w, https://myvet.vn/wp-content/uploads/2021/03/cat-tia-long-cho-meo-768x785.jpg 768w\" width=\"800\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><em>Ch&oacute; l&ocirc;ng x&ugrave; đang y&ecirc;u</em></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Ngo&agrave;i ra, việc tỉa l&ocirc;ng cho ch&oacute; m&egrave;o c&ograve;n đảm bảo sự sạch sẽ của ch&uacute;ng trong ng&ocirc;i nh&agrave;. Nếu để l&ocirc;ng qu&aacute; d&agrave;i th&igrave; n&oacute; rất dễ rụng v&agrave; ảnh hưởng đến vệ sinh của ng&ocirc;i nh&agrave;. Đặc biệt l&agrave; nếu nh&agrave; c&oacute; em b&eacute; nhỏ m&agrave; để ch&oacute; m&egrave;o l&ocirc;ng qu&aacute; d&agrave;i th&igrave; nếu v&ocirc; &yacute; rụng giữa nh&agrave; sẽ rất nguy hiểm cho b&eacute;.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:18px\">B&agrave;i viết n&agrave;y đ&atilde; cung cấp th&ecirc;m th&ocirc;ng tin về tầm quan trọng của việc tỉa l&ocirc;ng th&uacute; cưng. Bạn n&ecirc;n x&acirc;y dựng một thời gian biểu d&agrave;nh ri&ecirc;ng cho th&uacute; cưng để nhắc nhở lịch cắt tỉa l&ocirc;ng cũng như c&aacute;c dịch vụ kh&aacute;c. Nếu c&oacute; nhu cầu l&agrave;m đẹp cho vật nu&ocirc;i của m&igrave;nh, bạn c&oacute; thể li&ecirc;n hệ ngay với Bệnh viện th&uacute; cảnh Myvet để được tư vấn tốt nhất!</span></p>\r\n', './image/nhung-luu-y-nen-nho-khi-cat-tia-long.jpg', '2022-12-05', 1, ''),
(3, 'Hướng dẫn cách phòng bệnh thú cưng tốt nhất', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">C&oacute; lẽ sẽ c&oacute; rất nhiều người đồng &yacute; với &yacute; kiến cho rằng chăm s&oacute;c th&uacute; cưng bị bệnh l&agrave; một việc rất kh&oacute; khăn. Người chủ thường phải tốn thời gian chăm nom v&agrave; c&oacute; thể mất số tiền lớn để chữa trị. Do đ&oacute;, để bảo vệ th&uacute; cưng nh&agrave; m&igrave;nh lu&ocirc;n khỏe mạnh th&igrave; mọi người n&ecirc;n bỏ t&uacute;i ngay hướng dẫn c&aacute;ch ph&ograve;ng bệnh th&uacute; cưng tốt nhất của Myvet.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">1. Vệ sinh thường xuy&ecirc;n khu vực sinh hoạt của th&uacute; cưng</span></strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Điều lưu &yacute; đầu ti&ecirc;n trong chăm nu&ocirc;i th&uacute; cưng đ&oacute; l&agrave; bạn phải giữ m&ocirc;i trường sống của n&oacute; lu&ocirc;n lu&ocirc;n sạch sẽ. Chỗ ở của ch&oacute; m&egrave;o l&agrave; nơi m&agrave; c&aacute;c loại vi khuẩn c&oacute; thể dễ d&agrave;ng ẩn nấp v&agrave; nguy cơ l&acirc;y bệnh cho ch&uacute;ng bất cứ l&uacute;c n&agrave;o. Vậy n&ecirc;n bạn cần l&ecirc;n lịch dọn vệ sinh khu vực sống của th&uacute; cưng thường xuy&ecirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><img alt=\"\" height=\"500\" sizes=\"(max-width: 800px) 100vw, 800px\" src=\"https://dreampet.com.vn/wp-content/uploads/2021/02/huong-dan-phong-benh-thu-cung-2.jpg\" srcset=\"https://dreampet.com.vn/wp-content/uploads/2021/02/huong-dan-phong-benh-thu-cung-2.jpg 800w, https://dreampet.com.vn/wp-content/uploads/2021/02/huong-dan-phong-benh-thu-cung-2-300x188.jpg 300w, https://dreampet.com.vn/wp-content/uploads/2021/02/huong-dan-phong-benh-thu-cung-2-768x480.jpg 768w, https://dreampet.com.vn/wp-content/uploads/2021/02/huong-dan-phong-benh-thu-cung-2-600x375.jpg 600w\" width=\"800\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><em>Vệ sinh thường xuy&ecirc;n nơi ở cho ch&oacute; m&egrave;o</em></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Ch&uacute; &yacute;, bạn phải đảm bảo c&aacute;c loại cốc ăn, ch&eacute;n uống nước của vật nu&ocirc;i được rửa sạch h&agrave;ng ng&agrave;y. Việc n&agrave;y sẽ ph&ograve;ng tr&aacute;nh được c&aacute;c bệnh về đường ruột, ti&ecirc;u chảy v&agrave; giun s&aacute;n. B&ecirc;n cạnh đ&oacute;, chỗ ngủ cũng phải qu&eacute;t dọn c&aacute;c sợi l&ocirc;ng bị rụng hoặc r&aacute;c bẩn để loại bỏ ngay c&aacute;c loại k&yacute; sinh g&acirc;y bệnh.</span></span></p>\r\n\r\n<p><strong><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">2. Chế độ ăn uống khoa học</span></span></strong></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Đối với sức khỏe của th&uacute; cưng, chế độ ăn uống thật sự rất quan trọng. Bạn n&ecirc;n t&igrave;m hiểu kỹ về đặc điểm dạ d&agrave;y th&uacute; cưng của m&igrave;nh v&agrave; xem n&oacute; c&oacute; bị dị ứng với th&agrave;nh phần n&agrave;o kh&ocirc;ng. Từ đ&oacute;, bạn sẽ l&ecirc;n thực đơn 3 bữa mỗi ng&agrave;y cho b&eacute; cưng. Gi&uacute;p ch&uacute;ng ăn ngon miệng v&agrave; bổ sung đầy đủ c&aacute;c dưỡng chất cần thiết cho cơ thể. Tr&aacute;nh trường hợp cho ăn qu&aacute; nhiều, cho ăn kh&ocirc;ng đủ dinh dưỡng khiến ch&uacute;ng bị mắc c&aacute;c bệnh về dạ d&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Muốn x&acirc;y dựng chế độ ăn uống khoa học cho th&uacute; cưng, bạn c&oacute; thể nhờ đến sự tư vấn của c&aacute;c b&aacute;c sĩ của bệnh viện Myvet. Với tr&igrave;nh độ chuy&ecirc;n m&ocirc;n cao v&agrave; gi&agrave;u kinh nghiệm chăm s&oacute;c sức khỏe th&uacute; cưng, họ chắc chắn sẽ l&agrave;m bạn h&agrave;i l&ograve;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">3. Ti&ecirc;m ph&ograve;ng đầy đủ c&aacute;c loại bệnh</span></strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Một trong những biện ph&aacute;p ph&ograve;ng bệnh tốt nhất cho th&uacute; cưng ch&iacute;nh l&agrave; ti&ecirc;m ph&ograve;ng đầy đủ vacxin. C&aacute;c loại bệnh thường gặp ở ch&oacute; m&egrave;o như bệnh dại, vi&ecirc;m phế quản h&ocirc; hấp,&hellip; c&oacute; thể ph&ograve;ng được bằng thuốc. Đặc biệt, c&aacute;c loại bệnh ở th&uacute; cưng c&ograve;n c&oacute; thể l&acirc;y qua cơ thể người tiếp x&uacute;c, n&ecirc;n bạn cần ch&uacute; &yacute; bảo vệ ch&iacute;nh m&igrave;nh.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/huong-dan-phong-benh-thu-cung.jpg\"><img alt=\"\" height=\"511\" sizes=\"(max-width: 768px) 100vw, 768px\" src=\"https://myvet.vn/wp-content/uploads/2021/03/huong-dan-phong-benh-thu-cung.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/03/huong-dan-phong-benh-thu-cung.jpg 768w, https://myvet.vn/wp-content/uploads/2021/03/huong-dan-phong-benh-thu-cung-600x399.jpg 600w\" width=\"768\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><em>Ti&ecirc;m vacxin ph&ograve;ng bệnh cho th&uacute; cưng</em></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Khi đăng k&yacute; chăm s&oacute;c sức khỏe th&uacute; cưng tại bệnh viện Myvet, th&uacute; cưng của bạn sẽ nhận được dịch vụ thăm kh&aacute;m tốt nhất. Trung t&acirc;m sẽ l&ecirc;n lịch ti&ecirc;m ph&ograve;ng bệnh v&agrave; nhắc nhở chủ nh&acirc;n mang th&uacute; cưng đến lịch hẹn. Đảm bảo b&eacute; cưng nh&agrave; bạn sẽ lu&ocirc;n lu&ocirc;n khỏe mạnh khi đồng h&agrave;nh c&ugrave;ng Myvet.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">4. Lựa chọn bệnh viện th&uacute; y uy t&iacute;n để chăm s&oacute;c th&uacute; cưng</span></strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Chọn một bệnh viện th&uacute; y chất lượng uy t&iacute;n để hỗ trợ chăm s&oacute;c th&uacute; cưng rất quan trọng. H&agrave;ng ng&agrave;y bận rộn l&agrave;m việc, bạn sẽ kh&ocirc;ng c&oacute; thời gian để massage hay tắm rửa cho th&uacute; cưng. Hoặc đi c&ocirc;ng t&aacute;c d&agrave;i ng&agrave;y kh&ocirc;ng c&oacute; người quan t&acirc;m ch&uacute;ng th&igrave; ph&ograve;ng kh&aacute;m th&uacute; cưng c&oacute; thể gi&uacute;p bạn ho&agrave;n th&agrave;nh mọi việc. Họ sẽ chăm s&oacute;c ch&uacute;ng theo những kinh nghiệm tuyệt vời v&agrave; đảm bảo được sự khỏe mạnh như mong muốn của chủ nh&acirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/huong-dan-phong-benh-thu-cung-3.jpg\"><img alt=\"\" height=\"384\" src=\"https://myvet.vn/wp-content/uploads/2021/03/huong-dan-phong-benh-thu-cung-3.jpg\" width=\"512\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><em>Chăm s&oacute;c vật nu&ocirc;i tốt nhất</em></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Bệnh viện th&uacute; y Myvet sẽ l&agrave; lựa chọn s&aacute;ng suốt nhất của những người nu&ocirc;i th&uacute; cưng. Với kinh nghiệm chăm s&oacute;c chuẩn cao cấp đ&atilde; nhận được nhiều phản hồi t&iacute;ch cực từ những kh&aacute;ch h&agrave;ng đ&atilde; đến với Myvet. Ch&uacute;ng t&ocirc;i tự tin sẽ đồng h&agrave;nh bảo vệ th&uacute; cưng nh&agrave; bạn tốt nhất.</span></span></p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:563.487px; top:2536.2px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>\r\n', './image/huong-dan-phong-benh-thu-cung-3.jpg', '2022-12-04', 1, ''),
(4, 'Chăm sóc chó sơ sinh như thế nào cho đúng???', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><em><strong>Nếu bạn kh&ocirc;ng triệt sản con ch&oacute; c&aacute;i của bạn, bạn c&oacute; thể sẽ sở hữu một đ&agrave;n ch&oacute; con ở nh&agrave; khi con ch&oacute; của bạn chửa đẻ.&nbsp;Mặc d&ugrave; ch&oacute; con dễ thương v&agrave; ngoan ngo&atilde;n, nhưng c&oacute; rất nhiều điều phải ch&uacute; &yacute; để ch&uacute;ng c&oacute; thể ph&aacute;t triển tốt nhất trong hai tuần đầu.&nbsp;Đ&acirc;y l&agrave; một số điều cần biết nếu bạn phải chăm s&oacute;c những ch&uacute; c&uacute;n sơ sinh của m&igrave;nh.</strong></em></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><strong>Tạo ổ thoải m&aacute;i v&agrave; ấm &aacute;p cho ch&oacute; mẹ v&agrave; ch&oacute; con</strong></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Ch&oacute; mẹ v&agrave; con ch&oacute; cần phải c&oacute; một nơi ri&ecirc;ng tư để chăm s&oacute;c nhau.&nbsp;Một chiếc ổ với một tấm sưởi ấm nhỏ, chăn, gối sẽ cung cấp cho ch&uacute;ng sự ấm &aacute;p v&agrave; thoải m&aacute;i.&nbsp;Trong thời gian tới, trong ổ c&oacute; thể sẽ xuất hiện ẩm ướt do nước tiểu hoặc r&aacute;c thải, do đ&oacute; chăn, gối cần được thay đổi h&agrave;ng ng&agrave;y.&nbsp;Sự sạch sẽ l&agrave; ch&igrave;a kh&oacute;a để duy tr&igrave; một m&ocirc;i trường l&agrave;nh mạnh cho cả ch&oacute; con v&agrave; ch&oacute; mẹ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><img alt=\"\" height=\"418\" src=\"https://myvet.vn/wp-content/uploads/2021/03/cham-soc-cho-con-moi-de.jpg\" width=\"600\" /></span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Cho ch&oacute; con tiếp x&uacute;c với mẹ</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Ch&oacute; mẹ l&agrave; nguồn duy tr&igrave; sự sống cho ch&oacute; con ngay khi ch&uacute;ng trước v&agrave; sau khi được sinh ra. Ch&oacute; con kh&ocirc;ng được t&aacute;ch ra khỏi mẹ trong bất kỳ trường hợp n&agrave;o trong khoảng thời gian quan trọng n&agrave;y.&nbsp;Ch&oacute; con mới ch&agrave;o đời n&ecirc;n ch&oacute; mẹ sẽ d&agrave;nh thời gian để nghỉ ngơi v&agrave; chăm s&oacute;c&nbsp; những ch&uacute; c&uacute;n của m&igrave;nh kh&ocirc;ng bị quấy rầy. Ch&oacute; mẹ cung cấp chất dinh dưỡng v&agrave; kh&aacute;ng thể từ sữa của m&igrave;nh cho ch&oacute; con để th&uacute;c đẩy một hệ miễn dịch khỏe mạnh v&agrave; ph&aacute;t triển.&nbsp;Việc cho ch&oacute; con b&uacute; b&igrave;nh thường được thực hiện mỗi lần hai đến ba giờ đối với ch&oacute; cho đến khi ch&uacute;ng &iacute;t nhất 4 tuần tuổi v&agrave; c&oacute; thể ăn được thức ăn kh&aacute;c.&nbsp;Con ch&oacute; con từ bốn đến t&aacute;m tuần c&oacute; thể ăn hỗn hợp thức ăn kh&ocirc; v&agrave; sữa v&agrave; sau đ&oacute; ch&uacute;ng c&oacute; thể chuyển sang thức ăn đặc sau thời gian đ&oacute;.&nbsp;Nếu ch&oacute; mẹ kh&ocirc;ng thể nu&ocirc;i con th&igrave; khuy&ecirc;n bạn n&ecirc;n li&ecirc;n hệ với b&aacute;c sỹ th&uacute; y để thay thế cho sữa mẹ.</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Quan s&aacute;t dấu hiệu cảnh b&aacute;o bệnh tật</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Thời gian n&agrave;y ch&uacute;ng kh&ocirc;ng thể l&agrave;m bất cứ điều g&igrave; một m&igrave;nh.&nbsp;Nếu bạn nhận thấy bất kỳ h&agrave;nh vi bất thường n&agrave;o của ch&uacute;ng, bạn n&ecirc;n li&ecirc;n hệ với b&aacute;c sĩ th&uacute; y ngay để được tư vấn v&agrave; điều trị vấn đề trước khi qu&aacute; muộn.&nbsp;Một số điều cảnh b&aacute;o cần lưu &yacute; bao gồm n&ocirc;n, k&ecirc;u qu&aacute; nhiều, ti&ecirc;u chảy, ch&oacute; mẹ kh&ocirc;ng cho b&uacute;, hoặc nếu con ch&oacute; con kh&ocirc;ng được ch&oacute; mẹ chăm s&oacute;c c&ugrave;ng với những con ch&oacute; kh&aacute;c trong đ&agrave;n</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/03/cho-bi-sot.jpg\"><img alt=\"\" height=\"320\" src=\"https://myvet.vn/wp-content/uploads/2021/03/cho-bi-sot.jpg\" width=\"500\" /></a></span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Li&ecirc;n hệ với b&aacute;c sĩ th&uacute; y khi ch&oacute; sơ sinh c&oacute; biểu hiện kh&aacute;c thường</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">B&aacute;c sĩ th&uacute; y sẽ y&ecirc;u cầu bạn theo d&otilde;i ch&oacute; con v&agrave; cung cấp một khởi đầu l&agrave;nh mạnh cho ch&uacute;ng bởi v&igrave; c&oacute; rất nhiều mối nguy hiểm m&agrave; người nu&ocirc;i chưa biết.&nbsp;C&aacute;c con ch&oacute; con kh&ocirc;ng thể nghe, nh&igrave;n thấy, hoặc thậm ch&iacute; tự đ&agrave;o thải m&igrave;nh khi ch&uacute;ng được sinh ra v&agrave; ch&uacute;ng cũng c&oacute; thể bị ốm do một số bệnh v&agrave; vấn đề sức khoẻ.&nbsp;Hỏi b&aacute;c sĩ th&uacute; y của bạn để được hướng dẫn c&aacute;ch chăm s&oacute;c cho ch&oacute; con khi ch&uacute;ng được sinh ra.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Như một lưu &yacute; chung, tự nu&ocirc;i (nu&ocirc;i bộ kh&ocirc;ng cần sữa mẹ) ở nh&agrave; kh&ocirc;ng được khuyến c&aacute;o v&igrave; ch&oacute; con dễ bị tổn thương bởi một số bệnh tật v&agrave; ch&uacute;ng c&oacute; thể chết nếu ch&uacute;ng kh&ocirc;ng được chăm s&oacute;c đ&uacute;ng c&aacute;ch.&nbsp;Ch&oacute; con phải được ở lại với mẹ trong &iacute;t nhất mười hai tuần để gi&uacute;p ch&uacute;ng ph&aacute;t triển v&agrave; ph&aacute;t triển trước khi ch&uacute;ng được nhận nu&ocirc;i.&nbsp;V&igrave; ch&oacute; con l&agrave; những sinh vật tinh tế cần được ch&uacute; &yacute; rất nhiều, giữ li&ecirc;n lạc v&agrave; nhờ sự tư vấn của b&aacute;c sĩ th&uacute; y để đảm bảo sức khoẻ của ch&uacute;ng.</span></span></p>\r\n', './image/cho-bi-sot.jpg', '2022-12-06', 1, ''),
(7, 'Một số mẹo chăm sóc thú cưng', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>1. Cho ch&oacute;, m&egrave;o hoạt động ngo&agrave;i trời thường xuy&ecirc;n:&nbsp;</strong>Giữ cho ch&oacute;, m&egrave;o của bạn lu&ocirc;n được hoạt động v&agrave; giải tr&iacute; sẽ đảm bảo rằng cơ thể, t&acirc;m tr&iacute; v&agrave; sự th&ocirc;ng minh của ch&uacute;ng được ph&aacute;t triển. Trong khi đi dạo, ch&uacute;ng sẽ học được c&aacute;ch giao tiếp với đồng loại trong khu vực sinh sống. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"2. Vuốt ve, quan tâm thú cưng: Hãy quan tâm, chăm sóc thú cưng của bạn bằng cách vuốt ve chúng mỗi ngày. Một lưu ý để thú cưng giảm nguy cơ mắc một số bệnh và “bỏ nhà ra đi” tìm kiếm bạn đời (đặc biệt với mèo) là bạn nên nuôi chúng từ khi còn nhỏ.\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-2.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>2. Vuốt ve, quan t&acirc;m th&uacute; cưng từ khi c&ograve;n nhỏ:&nbsp;</strong>H&atilde;y quan t&acirc;m, chăm s&oacute;c ch&oacute;, m&egrave;o của bạn bằng c&aacute;ch vuốt ve ch&uacute;ng mỗi ng&agrave;y. Một lưu &yacute; để th&uacute; cưng giảm nguy cơ mắc một số bệnh v&agrave; &ldquo;bỏ nh&agrave; ra đi&rdquo; t&igrave;m kiếm bạn đời (đặc biệt với m&egrave;o) l&agrave; bạn n&ecirc;n nu&ocirc;i ch&uacute;ng từ khi c&ograve;n nhỏ. Đặc biệt khi th&uacute; cưng mang thai, h&atilde;y chăm s&oacute;c ch&uacute;ng v&agrave; con của ch&uacute;ng đ&uacute;ng c&aacute;ch. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"3. Mát-xa cho thú cưng: Bắt đầu bằng cách mát-xa cơ thể nhẹ nhàng , vì điều này có thể làm nên điều kỳ diệu cho mèo của bạn. Nó có thể làm giảm lo lắng và thư giãn các cơ của mèo sau một ngày chạy nhảy, chơi đùa và chạy theo những con bọ nhỏ.\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-3.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>3. M&aacute;t-xa:</strong>&nbsp;Bắt đầu bằng c&aacute;ch m&aacute;t-xa cơ thể nhẹ nh&agrave;ng , điều n&agrave;y c&oacute; thể gi&uacute;p th&uacute; cưng của bạn cải thiện t&acirc;m trạng phần n&agrave;o. N&oacute; c&oacute; thể l&agrave;m giảm lo lắng v&agrave; thư gi&atilde;n c&aacute;c cơ của ch&oacute;, m&egrave;o sau một ng&agrave;y chạy nhảy, chơi đ&ugrave;a. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"4. Đánh răng: Cũng giống như chúng ta, thú cưng của chúng ta cần được chăm sóc răng miệng. Thật không dễ dàng như bạn nghĩ vì thú cưng của chúng ta đánh răng là việc cuối cùng trong danh sách việc cần làm hàng ngày của chúng. Bạn có thể thử 7 ngày-răng-chải thách thức . Đây có thể là một quá trình lâu dài và đòi hỏi rất nhiều sự kiên nhẫn, và thú cưng của bạn thậm chí có thể kháng cự lúc đầu. Nhưng nếu bạn không thành công, có những lựa chọn thay thế khác , như chế độ ăn kiêng nha khoa theo toa hoặc nhai. Ảnh: Brightside\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-4.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>4. Đ&aacute;nh răng:</strong>&nbsp;Th&uacute; cưng của ch&uacute;ng ta cần được chăm s&oacute;c răng miệng. Đ&acirc;y c&oacute; thể l&agrave; một qu&aacute; tr&igrave;nh l&acirc;u d&agrave;i v&agrave; đ&ograve;i hỏi rất nhiều sự ki&ecirc;n nhẫn v&agrave; th&uacute; cưng của bạn thậm ch&iacute; c&oacute; thể kh&aacute;ng cự l&uacute;c đầu. Nhưng nếu bạn kh&ocirc;ng th&agrave;nh c&ocirc;ng c&oacute; thể t&igrave;m đến cơ sở th&uacute; y để được hỗ trợ, c&oacute; chế độ ăn uống ph&ugrave; hợp cho răng miệng của ch&uacute;ng. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Một nghiên cứu được thực hiện trên 50.000 con chó đã kết luận rằng nhóm chó được cho ăn ít hơn 25% so với những con chó trong nhóm còn lại dẫn đến 2 loại khác nhau: Chó gầy và chó thừa cân. Những con chó gầy sống lâu hơn trung bình 18-24 tháng so với đồng loại của chúng.  Điều này cũng áp dụng cho mèo - nếu mèo tự chủ thì việc cho ăn tự do cũng không sao, nhưng nếu mèo thèm ăn, thích ăn vặt và tăng cân thì tốt hơn là bạn nên giám sát chúng. Đảm bảo luôn theo dõi mọi thay đổi về cân nặng của chúng.\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-5-01.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>5. Quan s&aacute;t những g&igrave; th&uacute; cưng ăn:</strong>&nbsp;Một nghi&ecirc;n cứu được thực hiện tr&ecirc;n 50.000 con ch&oacute; đ&atilde; kết luận rằng nh&oacute;m ch&oacute; được cho ăn &iacute;t hơn 25% so với những con ch&oacute; trong nh&oacute;m c&ograve;n lại dẫn đến 2 loại kh&aacute;c nhau: Ch&oacute; gầy v&agrave; ch&oacute; thừa c&acirc;n. Những con ch&oacute; gầy sống l&acirc;u hơn trung b&igrave;nh 18-24 th&aacute;ng so với đồng loại của ch&uacute;ng. Điều n&agrave;y cũng &aacute;p dụng cho m&egrave;o - nếu m&egrave;o tự chủ th&igrave; việc cho ăn tự do cũng kh&ocirc;ng sao, nhưng nếu m&egrave;o th&egrave;m ăn, th&iacute;ch ăn vặt v&agrave; tăng c&acirc;n th&igrave; tốt hơn l&agrave; bạn n&ecirc;n gi&aacute;m s&aacute;t ch&uacute;ng. Đảm bảo lu&ocirc;n theo d&otilde;i mọi thay đổi về c&acirc;n nặng của ch&uacute;ng</span></span></p>\r\n', './image/meo-nuoi-thu-cung-5-01.jpg', '2022-12-08', 1, ''),
(8, 'Mẹo chăm sóc thú cưng P2', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Brightside đ&atilde; bật m&iacute; 5 mẹo gi&uacute;p&nbsp;<a href=\"https://laodong.vn/suc-khoe/thoi-quen-ngu-chung-hon-cho-meo-cuc-nguy-hiem-voi-suc-khoe-cua-ban-888137.ldo\" target=\"_self\" title=\"chó mèo\">ch&oacute; m&egrave;o</a>&nbsp;của bạn lu&ocirc;n khỏe mạnh, vui vẻ v&agrave; sống l&acirc;u.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>1. Cho ch&oacute;, m&egrave;o hoạt động ngo&agrave;i trời thường xuy&ecirc;n:&nbsp;</strong>Giữ cho ch&oacute;, m&egrave;o của bạn lu&ocirc;n được hoạt động v&agrave; giải tr&iacute; sẽ đảm bảo rằng cơ thể, t&acirc;m tr&iacute; v&agrave; sự th&ocirc;ng minh của ch&uacute;ng được ph&aacute;t triển. Trong khi đi dạo, ch&uacute;ng sẽ học được c&aacute;ch giao tiếp với đồng loại trong khu vực sinh sống. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"2. Vuốt ve, quan tâm thú cưng: Hãy quan tâm, chăm sóc thú cưng của bạn bằng cách vuốt ve chúng mỗi ngày. Một lưu ý để thú cưng giảm nguy cơ mắc một số bệnh và “bỏ nhà ra đi” tìm kiếm bạn đời (đặc biệt với mèo) là bạn nên nuôi chúng từ khi còn nhỏ.\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-2.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>2. Vuốt ve, quan t&acirc;m th&uacute; cưng từ khi c&ograve;n nhỏ:&nbsp;</strong>H&atilde;y quan t&acirc;m, chăm s&oacute;c ch&oacute;, m&egrave;o của bạn bằng c&aacute;ch vuốt ve ch&uacute;ng mỗi ng&agrave;y. Một lưu &yacute; để th&uacute; cưng giảm nguy cơ mắc một số bệnh v&agrave; &ldquo;bỏ nh&agrave; ra đi&rdquo; t&igrave;m kiếm bạn đời (đặc biệt với m&egrave;o) l&agrave; bạn n&ecirc;n nu&ocirc;i ch&uacute;ng từ khi c&ograve;n nhỏ. Đặc biệt khi th&uacute; cưng mang thai, h&atilde;y chăm s&oacute;c ch&uacute;ng v&agrave; con của ch&uacute;ng đ&uacute;ng c&aacute;ch. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"3. Mát-xa cho thú cưng: Bắt đầu bằng cách mát-xa cơ thể nhẹ nhàng , vì điều này có thể làm nên điều kỳ diệu cho mèo của bạn. Nó có thể làm giảm lo lắng và thư giãn các cơ của mèo sau một ngày chạy nhảy, chơi đùa và chạy theo những con bọ nhỏ.\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-3.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>3. M&aacute;t-xa:</strong>&nbsp;Bắt đầu bằng c&aacute;ch m&aacute;t-xa cơ thể nhẹ nh&agrave;ng , điều n&agrave;y c&oacute; thể gi&uacute;p th&uacute; cưng của bạn cải thiện t&acirc;m trạng phần n&agrave;o. N&oacute; c&oacute; thể l&agrave;m giảm lo lắng v&agrave; thư gi&atilde;n c&aacute;c cơ của ch&oacute;, m&egrave;o sau một ng&agrave;y chạy nhảy, chơi đ&ugrave;a. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"4. Đánh răng: Cũng giống như chúng ta, thú cưng của chúng ta cần được chăm sóc răng miệng. Thật không dễ dàng như bạn nghĩ vì thú cưng của chúng ta đánh răng là việc cuối cùng trong danh sách việc cần làm hàng ngày của chúng. Bạn có thể thử 7 ngày-răng-chải thách thức . Đây có thể là một quá trình lâu dài và đòi hỏi rất nhiều sự kiên nhẫn, và thú cưng của bạn thậm chí có thể kháng cự lúc đầu. Nhưng nếu bạn không thành công, có những lựa chọn thay thế khác , như chế độ ăn kiêng nha khoa theo toa hoặc nhai. Ảnh: Brightside\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-4.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>4. Đ&aacute;nh răng:</strong>&nbsp;Th&uacute; cưng của ch&uacute;ng ta cần được chăm s&oacute;c răng miệng. Đ&acirc;y c&oacute; thể l&agrave; một qu&aacute; tr&igrave;nh l&acirc;u d&agrave;i v&agrave; đ&ograve;i hỏi rất nhiều sự ki&ecirc;n nhẫn v&agrave; th&uacute; cưng của bạn thậm ch&iacute; c&oacute; thể kh&aacute;ng cự l&uacute;c đầu. Nhưng nếu bạn kh&ocirc;ng th&agrave;nh c&ocirc;ng c&oacute; thể t&igrave;m đến cơ sở th&uacute; y để được hỗ trợ, c&oacute; chế độ ăn uống ph&ugrave; hợp cho răng miệng của ch&uacute;ng. Ảnh: Brightside </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Một nghiên cứu được thực hiện trên 50.000 con chó đã kết luận rằng nhóm chó được cho ăn ít hơn 25% so với những con chó trong nhóm còn lại dẫn đến 2 loại khác nhau: Chó gầy và chó thừa cân. Những con chó gầy sống lâu hơn trung bình 18-24 tháng so với đồng loại của chúng.  Điều này cũng áp dụng cho mèo - nếu mèo tự chủ thì việc cho ăn tự do cũng không sao, nhưng nếu mèo thèm ăn, thích ăn vặt và tăng cân thì tốt hơn là bạn nên giám sát chúng. Đảm bảo luôn theo dõi mọi thay đổi về cân nặng của chúng.\" src=\"https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/7/10/929101/Meo-Nuoi-Thu-Cung-5-01.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>5. Quan s&aacute;t những g&igrave; th&uacute; cưng ăn:</strong>&nbsp;Một nghi&ecirc;n cứu được thực hiện tr&ecirc;n 50.000 con ch&oacute; đ&atilde; kết luận rằng nh&oacute;m ch&oacute; được cho ăn &iacute;t hơn 25% so với những con ch&oacute; trong nh&oacute;m c&ograve;n lại dẫn đến 2 loại kh&aacute;c nhau: Ch&oacute; gầy v&agrave; ch&oacute; thừa c&acirc;n. Những con ch&oacute; gầy sống l&acirc;u hơn trung b&igrave;nh 18-24 th&aacute;ng so với đồng loại của ch&uacute;ng. Điều n&agrave;y cũng &aacute;p dụng cho m&egrave;o - nếu m&egrave;o tự chủ th&igrave; việc cho ăn tự do cũng kh&ocirc;ng sao, nhưng nếu m&egrave;o th&egrave;m ăn, th&iacute;ch ăn vặt v&agrave; tăng c&acirc;n th&igrave; tốt hơn l&agrave; bạn n&ecirc;n gi&aacute;m s&aacute;t ch&uacute;ng. Đảm bảo lu&ocirc;n theo d&otilde;i mọi thay đổi về c&acirc;n nặng của ch&uacute;ng</span></span></p>\r\n', './image/meo-nuoi-thu-cung-5-01.jpg', '2022-12-09', 0, '');
INSERT INTO `news` (`id`, `title`, `content`, `image`, `date`, `status_news`, `slug`) VALUES
(9, 'Cấp cứu cho chó khi bị trúng gió thì phải làm sao?', '<h2><strong><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nguy&ecirc;n nh&acirc;n khiến m&egrave;o bị cảm lạnh</span></span></strong></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị cảm lạnh c&oacute; thể do virus hoặc m&egrave;o bị tr&uacute;ng gi&oacute;, nhiễm lạnh. Khi cơ thể của m&egrave;o con yếu, thể trạng sức khỏe kh&ocirc;ng tốt c&oacute; thể tạo ra cơ hội cho vi khuẩn, virus tấn c&ocirc;ng. Sau khi x&acirc;m nhập v&agrave;o cơ thể những đối tượng n&agrave;y sẽ l&agrave;m giảm sự miễn dịch của m&egrave;o v&agrave; g&acirc;y ra hiện tượng ốm, m&egrave;o bị cảm c&uacute;m, cảm lạnh.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị lạnh do sự thay đổi đột ngột của thời tiết. Cơ thể của những người bạn bốn ch&acirc;n n&agrave;y kh&oacute; m&agrave; th&iacute;ch nghi được khi thời tiết s&aacute;ng nắng, chiều mưa v&agrave; lu&ocirc;n ẩm ướt. Một trường hợp kh&aacute;c cũng c&oacute; thể khiến m&egrave;o bị cảm lạnh. Cụ thể như l&agrave; do tắm nước lạnh, tắm xong kh&ocirc;ng được sấy kh&ocirc;, m&egrave;o bị tr&uacute;ng gi&oacute;, m&egrave;o bị lạnh do nằm ngủ dưới nền nh&agrave;&hellip; Trong một số trường hợp, nhiễm tr&ugrave;ng thứ cấp c&oacute; thể ph&aacute;t triển, c&oacute; thể dẫn đến vi&ecirc;m phổi .</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Dấu hiệu khi m&egrave;o bị cảm lạnh</span></span></h2>\r\n\r\n<p><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Những ch&uacute; m&egrave;o bị cảm lạnh c&oacute; thể được ph&aacute;t hiện dễ d&agrave;ng khi quan s&aacute;t. Một số triệu trứng hay bắt gặp nhất như:</span></span></strong></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">To&agrave;n th&acirc;n run rẩy, tr&ocirc;ng rất mệt mỏi.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị tr&uacute;ng gi&oacute; lạnh nằm im một chỗ, lười di chuyển v&agrave; &iacute;t vận động.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Biếng ăn hoặc bỏ bữa.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị lạnh da t&aacute;i nhợt nhạt, c&oacute; biểu hiện n&ocirc;n, th&acirc;n nhiệt hạ thấp.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">L&ocirc;ng dựng, mắt lờ đờ, hắt hơi li&ecirc;n tục.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Trong một số trường hợp,&nbsp;<a href=\"https://www.petmart.vn/cach-chua-meo-bi-tieu-chay-tai-nha-khoi-hoan-toan\">m&egrave;o bị ti&ecirc;u chảy&nbsp;</a>, ni&ecirc;m mạc mắt v&agrave; miệng yếu. Để xử l&yacute; kịp thời tốt nhất bạn n&ecirc;n đưa m&egrave;o cưng tới c&aacute;c ph&ograve;ng kh&aacute;m, bệnh viện&nbsp;<a href=\"https://www.petmart.vn/hoi-dap-thu-y-mien-phi\" title=\"thú y\">th&uacute; y</a>&nbsp;để kiểm tra. Một số x&eacute;t nghiệm cơ bản c&oacute; thể gi&uacute;p bạn biết r&otilde; hơn về t&igrave;nh trạng sức khỏe của m&egrave;o con.</span></span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chẩn đo&aacute;n m&egrave;o con bị cảm lạnh</span></span></strong></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị cảm lạnh c&oacute; thể xảy ra ở bất kỳ giống m&egrave;o n&agrave;o. Bao gồm cả&nbsp;<a href=\"https://www.petmart.vn/meo-canh\" title=\"mèo cảnh\">m&egrave;o cảnh</a>&nbsp;như&nbsp;<a href=\"https://www.petmart.vn/meo-anh-long-ngan\">m&egrave;o Anh l&ocirc;ng ngắn&nbsp;</a>, m&egrave;o Xi&ecirc;m, m&egrave;o tai cụp&hellip; v&agrave; cả m&egrave;o ta. Nhưng phổ biến nhất ở m&egrave;o con hoặc m&egrave;o trưởng th&agrave;nh chưa được ti&ecirc;m ph&ograve;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nếu bạn nghi ngờ ch&uacute; m&egrave;o bị lạnh, bạn n&ecirc;n lu&ocirc;n gọi b&aacute;c sĩ th&uacute; y v&agrave; sắp xếp một cuộc hẹn. Nếu bạn muốn biết chắc chắn rằng m&egrave;o bị cảm lạnh thay v&igrave; dị ứng, k&iacute;ch th&iacute;ch h&ocirc; hấp, nhiễm tr&ugrave;ng m&atilde;n t&iacute;nh v&agrave; khối u l&agrave;nh t&iacute;nh hoặc ung thư cũng. V&igrave; những bệnh n&agrave;y c&oacute; thể g&acirc;y ra c&aacute;c triệu chứng giống như việc m&egrave;o bị cảm lạnh.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Một cuộc kiểm tra thể chất kỹ lưỡng c&oacute; thể chẩn đo&aacute;n m&egrave;o c&oacute; bị nhiễm tr&ugrave;ng đường h&ocirc; hấp hay kh&ocirc;ng. X&eacute;t nghiệm m&aacute;u, chụp X-quang v&agrave; c&aacute;c x&eacute;t nghiệm chẩn đo&aacute;n kh&aacute;c c&oacute; thể cần thiết để x&aacute;c định nguy&ecirc;n nh&acirc;n cơ bản khiến m&egrave;o bị lạnh. Sau đ&oacute; c&oacute; kế hoạch điều trị ph&ugrave; hợp.</span></span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">M&egrave;o bị cảm lạnh uống thuốc g&igrave;?</span></span></strong></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nếu con m&egrave;o của bạn đ&atilde; được chẩn đo&aacute;n bị nhiễm vi khuẩn, b&aacute;c sĩ th&uacute; y c&oacute; thể sẽ k&ecirc; toa thuốc kh&aacute;ng sinh. Nếu m&egrave;o bị cảm lạnh do nhiễm virus sẽ được điều trị th&ocirc;ng hệ thống miễn dịch của m&egrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nếu m&egrave;o bị lạnh dẫn tới bỏ ăn hoặc bị mất nước, n&oacute; c&oacute; thể phải nhập viện để được điều trị. C&oacute; thể truyền dịch, hỗ trợ dinh dưỡng để gi&uacute;p m&egrave;o phục hồi nhanh ch&oacute;ng. Điều trị kịp thời l&agrave; c&aacute;ch tốt nhất để bảo vệ t&iacute;nh mạng cho những ch&uacute; m&egrave;o bị cảm lạnh.</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Chăm s&oacute;c v&agrave; ph&ograve;ng tr&aacute;nh m&egrave;o bị cảm lạnh</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Khi m&egrave;o của bạn trở về nh&agrave;, h&atilde;y tiếp tục sử dụng bất kỳ loại thuốc hoặc liệu ph&aacute;p n&agrave;o kh&aacute;c theo chỉ dẫn của b&aacute;c sĩ th&uacute; y. Việc&nbsp;<a href=\"https://www.petmart.vn/danh-muc/meo/cham-soc-meo\" title=\"chăm sóc mèo\">chăm s&oacute;c m&egrave;o</a>&nbsp;v&ocirc; c&ugrave;ng quan trọng. Đầu ti&ecirc;n, h&atilde;y gi&uacute;p m&egrave;o cưng l&agrave;m ấm cơ thể.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Bạn c&oacute; thể sử dụng đ&egrave;n sưởi khi m&egrave;o bị lạnh. L&oacute;t ổ, nh&agrave;&nbsp;<a href=\"https://www.petmart.vn/danh-muc/meo/dem-meo\" title=\"đệm cho mèo\">đệm cho m&egrave;o</a>&nbsp;&nbsp;đặt ở những nơi k&iacute;n gi&oacute;. Pha một ch&uacute;t nước gừng ấm để gi&uacute;p ch&uacute;ng tăng nhiệt độ cơ thể l&ecirc;n. Sử dụng thuốc theo sự hướng dẫn của b&aacute;c sĩ th&uacute; y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Để tr&aacute;nh cho m&egrave;o bị cảm lạnh bạn n&ecirc;n c&oacute; chế độ chăm s&oacute;c cẩn thận. Đặc biệt l&agrave; ti&ecirc;m ph&ograve;ng cho m&egrave;o đầy đủ để tăng sức đề kh&aacute;ng v&agrave; miễn dịch. Thường xuy&ecirc;n đi kiểm tra sức khỏe theo định kỳ tại c&aacute;c c&oacute; sở y tế uy t&iacute;n. Dưới đ&acirc;y l&agrave; một số vấn đề kh&aacute;c bạn cần lưu &yacute;:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cho m&egrave;o ăn uống đầy đủ dinh dưỡng để c&oacute; một cơ thể khỏe mạnh.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kh&ocirc;ng tắm cho m&egrave;o bằng nước lạnh. Sau khi tắm xong phải lấy khăn lau v&agrave; sấy kh&ocirc; l&ocirc;ng.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cho m&egrave;o cưng nằm ở nơi ấm &aacute;p.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Trong thời tiết lạnh, khi đi ra ngo&agrave;i cần mặc&nbsp;<a href=\"https://www.petmart.vn/danh-muc/meo/phu-kien-meo/quan-ao-meo\" title=\"quần áo cho mèo\">quần &aacute;o cho m&egrave;o</a>&nbsp;&nbsp;ấm để chống lạnh. Tr&aacute;nh cho m&egrave;o bị tr&uacute;ng gi&oacute; khi nhiệt độ ngo&agrave;i trời thấp.</span></span></li>\r\n</ul>\r\n\r\n<h2><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nguy&ecirc;n nh&acirc;n m&egrave;o bị tr&uacute;ng gi&oacute; đột ngột</span></span></strong></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị tr&uacute;ng gi&oacute; c&oacute; thể khiến cho th&acirc;n nhiệt của m&egrave;o giảm mạnh đột ngột. Sau đ&oacute; g&acirc;y ra những biến chứng nguy hiểm. Trong số đ&oacute; c&oacute; đột quỵ, l&agrave; t&igrave;nh trạng sức khỏe rất nguy hiểm đối với m&egrave;o. Đột quỵ khiến m&egrave;o tử vong nhanh ch&oacute;ng chỉ sau v&agrave;i giờ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị tr&uacute;ng gi&oacute; sốt, hạ th&acirc;n nhiệt khi thời tiết r&eacute;t đậm m&ugrave;a đ&ocirc;ng v&agrave; đầu xu&acirc;n. L&uacute;c n&agrave;y cơ thể m&egrave;o buộc phải &ldquo;đốt ch&aacute;y&rdquo; năng lượng dự trữ dẫn đến hạ đường huyết đột ngột. Trung khu điều h&ograve;a th&acirc;n nhiệt kh&ocirc;ng thể tự điều chỉnh. Dẫn đến th&acirc;n nhiệt của m&egrave;o giảm mạnh, m&egrave;o bị sốc v&agrave; suy sụp do trụy tim mạch.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o trưởng th&agrave;nh thường mắc bệnh khi đi ra ngo&agrave;i trời v&agrave;o m&ugrave;a lạnh. Đối với m&egrave;o sơ sinh, m&egrave;o bị lạnh l&agrave; do m&egrave;o mẹ &ldquo;qu&ecirc;n&rdquo; kh&ocirc;ng chăm con, để con một m&igrave;nh hay bị lẫn lộn trong mớ khăn giẻ l&oacute;t ổ đẻ, m&egrave;o con đ&oacute;i sữa.</span></span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c triệu chứng của m&egrave;o bị tr&uacute;ng gi&oacute;</span></span></strong></h2>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">M&egrave;o bị tr&uacute;ng gi&oacute; hạ nhiệt đột ngột c&oacute; nguy cơ đột quỵ khi c&oacute; c&aacute;c triệu chứng:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Mệt mỏi, bơ phờ, đi lại yếu ớt, xi&ecirc;u vẹo rồi ngủ lịm.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kh&ocirc;ng c&ograve;n phản xạ nhận biết v&agrave; &acirc;u yếm với chủ.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nhiệt độ khi m&egrave;o bị lạnh tại trực tr&agrave;ng tụt xuống rất thấp 36,2- 36&deg;C.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nhịp tim v&agrave; tần số h&ocirc; hấp của m&egrave;o bị tr&uacute;ng gi&oacute; giảm v&agrave; yếu dần.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sau khi m&egrave;o bị tr&uacute;ng gi&oacute; rơi v&agrave;o t&igrave;nh trạng h&ocirc;n m&ecirc; l&agrave; rất nguy hiểm. L&uacute;c n&agrave;y m&egrave;o kh&ocirc;ng đi lại được, k&ecirc;u yếu ớt , khản giọng, mất tiếng. M&iacute; mắt kh&eacute;p nhỏ tr&ocirc;ng tựa như bị l&aacute;c, h&agrave;m cứng dần, to&agrave;n th&acirc;n co cứng. Đặc biệt đ&ocirc;i tai m&egrave;o bị lạnh, ỉa đ&aacute;i kh&ocirc;ng chủ động, gi&atilde;n đồng tử mắt v&agrave; chết.</span></span></p>\r\n\r\n<h2><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cấp cứu cho m&egrave;o bị tr&uacute;ng gi&oacute;</span></span></strong></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">L&agrave;m ấm cơ thể khẩn cấp bằng cho m&egrave;o bị lạnh bằng c&aacute;ch ủ chăn, &aacute;o. Đưa m&egrave;o v&agrave;o trong nh&agrave; nơi k&iacute;n gi&oacute;, c&oacute; l&ograve; sười ấm. C&oacute; thể d&ugrave;ng t&uacute;i chườm ấm để ủ cho m&egrave;o. Nếu m&egrave;o bị lạnh do ướt nước th&igrave; ng&acirc;m ngay v&agrave;o nước ấm. Chờ một l&uacute;c để l&agrave;m ấm cơ thể m&egrave;o rồi lau kh&ocirc; bằng khăn tắm, sấy kh&ocirc; ngay bộ l&ocirc;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cẩn thận d&ugrave;ng m&aacute;y sấy t&oacute;c hoặc sưởi cho m&egrave;o bị lạnh c&oacute; thể l&agrave;m bỏng da. Với m&egrave;o sơ sinh, tốt nhất l&agrave; ủ m&egrave;o v&agrave;o cơ thể, da người cho đến khi m&egrave;o con khỏe lại. Cứ sau 10 ph&uacute;t lại kiểm tra nhiệt độ trực tr&agrave;ng cho tới khi th&acirc;n nhiệt đạt tới tr&ecirc;n 38&deg;C.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Khi m&egrave;o bị tr&uacute;ng gi&oacute; tỉnh lại, đi lại được, h&agrave;m đ&atilde; mềm, c&oacute; phản xạ nuốt, cần cho uống ngay đường nước Gluose. Nếu kh&ocirc;ng c&oacute; đường Glucose c&oacute; thể d&ugrave;ng đường ăn (Saccaroze) hoặc mật ong thay thế. Đưa đi kh&aacute;m b&aacute;c sỹ th&uacute; y để kiểm tra nhịp tim, đường huyết. Bổ sung dịch v&agrave; điện giải, năng lượng cần thiết cho m&egrave;o bị lạnh, hạ nhiệt đột ngột.</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nguy&ecirc;n nh&acirc;n m&egrave;o bị cảm c&uacute;m</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị cảm c&uacute;m thường do một hoặc hai loại virus g&acirc;y ra. Sau khi bị nhiễm bệnh, m&egrave;o thải ra c&aacute;c hạt virus trong nước mũi, nước mắt v&agrave; nước bọt. Mặc d&ugrave; m&egrave;o bị bệnh l&agrave; nguồn l&acirc;y nhiễm lớn nhất, nhưng mộ số con m&egrave;o khỏe mạnh lại mang virus.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Những ch&uacute; m&egrave;o mang virus kh&ocirc;ng mắc bệnh nhưng c&oacute; thể l&agrave;m l&acirc;y nhiễm sang những con m&egrave;o kh&aacute;c. C&aacute;c hạt virus c&oacute; thể tồn tại đến một tuần trong m&ocirc;i trường. M&egrave;o khỏe mạnh tuy kh&ocirc;ng c&oacute; triệu chứng, nhưng vẫn c&oacute; thể truyền bệnh cho những m&egrave;o kh&aacute;c.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Mầm bệnh dạng hạt nếu ph&ugrave; hợp với m&ocirc;i trường th&igrave; c&oacute; thể tồn tại trong 1 tuần. V&igrave; thế ngay cả kh&ocirc;ng tiếp x&uacute;c trực tiếp với m&egrave;o con kh&aacute;c vẫn c&oacute; thể truyền bệnh. Mầm bệnh c&oacute; thể l&acirc;y lan ở trong b&aacute;t ăn,&nbsp;<a href=\"https://www.petmart.vn/danh-muc/meo/do-choi-meo\" title=\"đồ chơi cho mèo\">đồ chơi cho m&egrave;o</a>&nbsp;&nbsp;v&agrave; tr&ecirc;n cả quần &aacute;o của người.</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&aacute;c loại virus khiến m&egrave;o bị cảm c&uacute;m</span></span></h2>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị c&uacute;m co Virus Calicillin</span></span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Virus khiến m&egrave;o bị cảm c&uacute;m n&agrave;y tồn tại ở nhiều dạng hơi kh&aacute;c nhau, được gọi l&agrave; c&aacute;c chủng. Ti&ecirc;m&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%E1%BA%AFc-xin\" rel=\"noopener\" target=\"_blank\">vacxin</a>&nbsp;chống lại Calicillin rất kh&oacute; khăn. V&igrave; n&oacute; l&agrave; virut c&uacute;m ở người, m&agrave; vacxin kh&ocirc;ng thể bao gồm tất cả c&aacute;c chủng. V&igrave; vậy n&oacute; kh&ocirc;ng được bảo vệ một c&aacute;ch đầy đủ nhất.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&aacute;c triệu chứng m&egrave;o bị cảm c&uacute;m nhẹ hơn v&agrave; đặc trưng g&acirc;y lo&eacute;t miệng. M&egrave;o mang mầm bệnh li&ecirc;n tục thải ra c&aacute;c hạt virus. Do đ&oacute; ch&uacute;ng dễ d&agrave;ng x&aacute;c định hơn. Nhiều ch&uacute; m&egrave;o c&oacute; thể loại bỏ ho&agrave;n to&agrave;n virus sau 1 &ndash; 2 năm.</span></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị c&uacute;m do Virus Herpes</span></span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Chỉ c&oacute; một chủng v&igrave; vậy ti&ecirc;m vacxin mang lại hiệu quả hơn. Tuy nhi&ecirc;n, triệu chứng m&egrave;o bị c&uacute;m lại nặng hơn v&agrave; c&oacute; nhiều khả năng g&acirc;y lo&eacute;t mắt. Sau khi bị nhiễm Herpes, nhiều &yacute; kiến cho rằng tất cả m&egrave;o đều trở th&agrave;nh mầm bệnh. Ch&uacute;ng tạo ra virus trong nước mắt, nước bọt v&agrave; dịch tiết mũi.</span></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&aacute;c loại vi khuẩn kh&aacute;c</span></span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Vi khuẩn Bordetella Bronchiseptica dường như cũng ảnh hưởng đến phổi khiến m&egrave;o bị c&uacute;m. N&oacute; thường c&oacute; thể được điều trị th&agrave;nh c&ocirc;ng bằng kh&aacute;ng sinh.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Vi khuẩn Chlamydophila Felis g&acirc;y ra đau mắt, đỏ, chảy nước mắt, đ&ocirc;i khi khiến m&egrave;o bị c&uacute;m nhẹ. Một số loại kh&aacute;ng sinh c&oacute; hiệu quả v&agrave; c&oacute; sẵn vacxin để ti&ecirc;m ph&ograve;ng.</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Triệu chứng m&egrave;o bị c&uacute;m</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị cảm c&uacute;m cũng giống như người c&oacute; thể g&acirc;y ra chảy nước mũi v&agrave; chảy nước mắt. Hơn nữa sẽ đi k&egrave;m với đau họng. C&aacute;c triệu chứng kh&aacute;c bao gồm đau cơ hoặc khớp, vi&ecirc;m lo&eacute;t miệng. Chảy nước d&atilde;i kh&ocirc;ng kiểm so&aacute;t được, sốt, mất tiếng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o bị cảm c&uacute;m khi đ&atilde; trưởng th&agrave;nh kh&ocirc;ng phải l&agrave; việc nghi&ecirc;m trọng. Chỉ l&agrave; ốm yếu n&ecirc;n sớm ph&aacute;t hiện những triệu chứng c&oacute; thể chữa trị được. M&egrave;o bị cảm lạnh khi c&ograve;n nhỏ lại cực k&igrave; nguy hiểm. N&oacute; c&oacute; thể dẫn đến tử vọng.</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Điều trị cho m&egrave;o bị c&uacute;m</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hi&ecirc;n nay chưa c&oacute; thuốc kh&aacute;ng virus cho m&egrave;o bị c&uacute;m h&agrave;ng ng&agrave;y. Kh&aacute;ng sinh c&oacute; thể c&oacute; hiệu quả v&igrave; giống như c&uacute;m ở người. Virus c&oacute; thể l&agrave;m tổn thương đường thở qua mũi để vi khuẩn x&acirc;m nhập v&agrave;o trong. Dẫn đến c&aacute;c biến chứng khiến m&egrave;o bị cảm c&uacute;m ph&aacute;t triển th&agrave;nh m&egrave;o bị vi&ecirc;m phổi. Thuốc kh&aacute;ng sinh sẽ tăng sức đề kh&aacute;ng kh&ocirc;ng co virus tấn c&ocirc;ng v&agrave; x&acirc;m nhập s&acirc;u v&agrave;o b&ecirc;n trong.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">T&igrave;nh trạng nghẹt mũi v&agrave; lo&eacute;t miệng c&oacute; thể khiến m&egrave;o bị c&uacute;m kh&ocirc;ng ăn được. Thậm ch&iacute; uống nước cũng kh&ocirc;ng thể, dẫn đến mất nước. N&oacute; rất nguy hiểm với m&egrave;o con mới sinh. Trong thời gian m&egrave;o bị cảm c&uacute;m n&ecirc;n cho ch&uacute;ng ăn thức ăn băm nhỏ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Gợi &yacute; n&ecirc;n d&ugrave;ng c&aacute; m&ograve;i, g&agrave; nước hoặc c&aacute;c loại&nbsp;<a href=\"https://www.petmart.vn/danh-muc/meo/thuc-an-meo\" title=\"thức ăn cho mèo\">thức ăn cho m&egrave;o</a>&nbsp;&nbsp;bị cảm c&uacute;m được b&aacute;n tại Pet Mart. Nếu m&egrave;o con kh&ocirc;ng thể ăn cần đến chăm s&oacute;c ở trung t&acirc;m y tế. Khuyến kh&iacute;ch cho m&egrave;o bị cảm c&uacute;m uống nước, chất lỏng c&oacute; thể l&agrave;m lo&atilde;ng chất nhầy ở mắt, mũi v&agrave; sử dụng nước muối để l&agrave;m sạch.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hơi nước n&oacute;ng cũng c&oacute; thể l&agrave;m lo&atilde;ng chất nhầy. V&igrave; vậy khi bạn tắm bằng nước n&oacute;ng n&ecirc;n để m&egrave;o con ở trong ph&ograve;ng tắm. Hoặc cũng c&oacute; thể x&ocirc;ng hơi bằng khăn n&oacute;ng c&oacute; thể dầu Olbas.</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Ti&ecirc;m vacxin ph&ograve;ng cho m&egrave;o bị cảm c&uacute;m</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&oacute; rất nhiều chủng virut kh&aacute;c nhau khiến m&egrave;o bị c&uacute;m. V&agrave; cũng giống như bệnh c&uacute;m ở người, vacxin kh&ocirc;ng c&oacute; hiệu quả đối với tất cả ch&uacute;ng. Tuy nhi&ecirc;n, 2 liều vacxin ban đầu l&agrave; rất cần thiết. Tiếp theo l&agrave; thuốc tăng cường th&ocirc;ng thường. Bạn n&ecirc;n tham khảo &yacute; kiến ​​b&aacute;c sĩ th&uacute; y của bạn để biết th&ecirc;m chi tiết.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Ngay cả những ch&uacute; m&egrave;o được ti&ecirc;m ph&ograve;ng cũng c&oacute; thể mang mầm bệnh m&agrave; kh&ocirc;ng c&oacute; bất kỳ triệu chứng n&agrave;o. Ch&uacute;ng c&oacute; thể l&acirc;y nhiễm cho những con m&egrave;o kh&aacute;c. M&egrave;o con ban đầu nhận được một số miễn dịch từ mẹ của ch&uacute;ng, nhưng khi ch&uacute;ng gi&agrave; đi, điều n&agrave;y mất dần.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&egrave;o mẹ bị nhiễm bệnh c&oacute; thể l&acirc;y nhiễm cho m&egrave;o con m&agrave; kh&ocirc;ng thể hiện bệnh. M&egrave;o bị c&uacute;m hoặc trở th&agrave;nh m&egrave;o mang mầm bệnh kh&ocirc;ng c&oacute; triệu chứng. C&oacute; thể mất đến 2 tuần để c&aacute;c dấu hiệu c&uacute;m xuất hiện.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Do đ&oacute;, l&yacute; do khiến vacxin cho m&egrave;o bị c&uacute;m thất bại l&agrave; ch&uacute;ng đ&atilde; bị nhiễm bệnh tại thời điểm ti&ecirc;m vacxin.Giống như tất cả những loại vacxin kh&aacute;c, nếu sau khi bị truyền nhiễm mới&nbsp;<a href=\"https://www.petmart.vn/tiem-phong-cho-meo-de-phong-ngua-4-benh-nguy-hiem\">ti&ecirc;m ph&ograve;ng cho m&egrave;o&nbsp;</a>&nbsp;th&igrave; kh&ocirc;ng c&oacute; hiệu quả.</span></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cẩn trọng khi chăm s&oacute;c m&egrave;o theo đ&agrave;n</span></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nơi d&acirc;n cư c&oacute; nhiều m&egrave;o con sinh sống như trong trạm cứu hộ m&egrave;o nếu m&egrave;o bị c&uacute;m sẽ rất nghi&ecirc;m trọng. Trong m&ocirc;i trường như vậy ph&ograve;ng ngừa sự x&acirc;m nhập l&agrave; rất phức tạp, cần phối hợp với bệnh viện th&uacute; y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sự đảm bảo tối thiểu đối với tất cả những m&egrave;o con mới đến cho d&ugrave; khỏe mạnh cũng phải c&aacute;ch ly &iacute;t nhất 2 tuần mới được. Ch&uacute;ng v&agrave; nh&oacute;m m&egrave;o trước đ&oacute; phải được ti&ecirc;m ph&ograve;ng c&uacute;m. Cần phải lưu &yacute; v&igrave; một giọt nước tiểu cũng c&oacute; thể truyền bệnh. V&igrave; thế tốt nhất n&ecirc;n để nơi đi vệ sinh của m&egrave;o mẹ v&agrave; m&egrave;o con c&aacute;ch xa nhau. Đến khi m&egrave;o con khỏe mạnh v&agrave; c&oacute; thể đến nh&agrave; mới.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nếu c&oacute; m&egrave;o bị c&uacute;m truyền nhiễm, phải để ch&uacute;ng kh&ocirc;ng c&oacute; bất cứ tiếp x&uacute;c n&agrave;o với những ch&uacute; m&egrave;o kh&aacute;c. Bạn cần phải học thật kỹ c&aacute;c c&aacute;ch chăm s&oacute;c m&egrave;o n&agrave;y để ph&ograve;ng tr&aacute;nh cho m&egrave;o cưng của m&igrave;nh.</span></span></p>\r\n', './image/dau-hieu-cho-bi-sot-min.jpg', '2022-12-08', 1, ''),
(10, 'Hướng dẫn sơ cứu vết thương bị chó/mèo cắn?', '<h2><strong><span style=\"font-size:18px\">1. Sơ cứu vết thương bị ch&oacute;/m&egrave;o cắn</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Để chăm s&oacute;c vết thương do động vật nhỏ cắn phải dựa v&agrave;o mức độ nghi&ecirc;m trọng của vết cắn:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đối với vết thương kh&ocirc;ng g&acirc;y r&aacute;ch da: n&ecirc;n</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\">Rửa kỹ vết thương bằng x&agrave; ph&ograve;ng v&agrave; nước.</span></li>\r\n	<li><span style=\"font-size:16px\">C&oacute; thể b&ocirc;i thuốc kh&aacute;ng sinh (dạng cream hoặc thuốc mỡ) l&ecirc;n vết thương để ph&ograve;ng ngừa.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\">Đối với vết thương g&acirc;y r&aacute;ch da: n&ecirc;n</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\">Rửa kỹ vết thương bằng x&agrave; ph&ograve;ng v&agrave; nước, ấn nhẹ l&ecirc;n vết thương để cho &iacute;t m&aacute;u chảy ra, gi&uacute;p loại bỏ &iacute;t vi tr&ugrave;ng.</span></li>\r\n	<li><span style=\"font-size:16px\">B&ocirc;i thuốc kh&aacute;ng sinh l&ecirc;n vết thương</span></li>\r\n	<li><span style=\"font-size:16px\">Băng vết cắn bằng băng v&ocirc; tr&ugrave;ng.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\">Đối với vết thương g&acirc;y chảy m&aacute;u</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\">Đắp một miếng gạc l&ecirc;n vết thương v&agrave; nhẹ nh&agrave;ng ấn xuống để ngăn m&aacute;u chảy.</span></li>\r\n	<li><span style=\"font-size:16px\">B&ocirc;i thuốc kh&aacute;ng sinh l&ecirc;n vết thương</span></li>\r\n	<li><span style=\"font-size:16px\">Băng vết cắn bằng băng v&ocirc; tr&ugrave;ng.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\">Tất cả những vết thương do ch&oacute; m&egrave;o cắn đều cần được theo d&otilde;i c&aacute;c dấu hiệu nhiễm tr&ugrave;ng (sưng, n&oacute;ng, đỏ, chạm v&agrave;o đau...) cho đến khi ho&agrave;n to&agrave;n l&agrave;nh lặn.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">2. Bị ch&oacute; m&egrave;o cắn khi n&agrave;o cần tới gặp b&aacute;c sĩ?</span></span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngo&agrave;i việc<strong>&nbsp;sơ cứu m&egrave;o cắn</strong>&nbsp;tại nh&agrave; tạm thời, bạn cũng cần đến gặp b&aacute;c sĩ trong một v&agrave;i trường hợp sau:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Con ch&oacute; cắn bạn kh&ocirc;ng r&otilde; đ&atilde; được&nbsp;<a href=\"https://www.vinmec.com/vi/vac-xin/kien-thuc-tiem-chung/tiem-phong-vac-xin-ngua-dai-co-nguy-hiem-khong/\"><strong>ti&ecirc;m vaccine dại</strong></a>&nbsp;chưa hoặc c&oacute; biểu hiện thất thường c&oacute; vẻ bị bệnh</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vết thương kh&ocirc;ng ngừng chảy m&aacute;u hoặc g&acirc;y ra đau nhiều. Vết thương s&acirc;u để lộ xương, tổn thương g&acirc;n hoặc cơ.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vết thương c&oacute; biểu hiện nhiễm tr&ugrave;ng (sưng tấy đỏ, n&oacute;ng, rỉ dịch hoặc mủ...) r&ograve; rỉ mủ hoặc chất lỏng</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cũng n&ecirc;n t&igrave;m kiếm sự chăm s&oacute;c y tế nếu bạn:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong 10 năm qua bạn chưa&nbsp;<a href=\"https://www.vinmec.com/vi/vac-xin/tu-van-tiem-chung/co-can-tiem-ngua-uon-van-khi-da-tiem-vac-xin-tdap-khong-/\"><strong>ti&ecirc;m ngừa uốn v&aacute;n</strong></a>&nbsp;hoặc trong v&ograve;ng 5 năm đối với trường hợp vết cắn s&acirc;u hoặc bẩn.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; biểu hiện sốt, mệt mỏi, rối loạn &yacute; thức hoặc ngất</span></span></li>\r\n</ul>\r\n\r\n<h2><strong><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">3. Chủ động ti&ecirc;m vaccine ph&ograve;ng dại</span></span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://www.vinmec.com/vi/vac-xin/kien-thuc-tiem-chung/10-su-ve-benh-dai/\"><strong>Bệnh dại</strong></a>&nbsp;l&agrave; một t&igrave;nh trạng c&oacute; khả năng g&acirc;y tử vong, nhưng ho&agrave;n to&agrave;n c&oacute; thể ngăn ngừa được khi nạn nh&acirc;n được can thiệp y tế ph&ugrave; hợp ngay lập tức.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Những trường hợp cần ti&ecirc;m vaccin ph&ograve;ng dại:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&oacute; cắn c&oacute; dấu hiệu của bệnh dại, chẳng hạn như hoạt động thất thường hoặc s&ugrave;i bọt m&eacute;p, bạn n&ecirc;n ti&ecirc;m vắc xin ph&ograve;ng bệnh dại.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngay cả trong trường hợp, bạn hoặc b&aacute;c sĩ của bạn lo lắng về khả năng bạn c&oacute; thể bị nhiễm bệnh dại khi bị ch&oacute; cắn.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vaccin ti&ecirc;m ngừa dại: t&ugrave;y loại vaccine c&oacute; những ph&aacute;c đồ ti&ecirc;m kh&aacute;c nhau. Tại Bệnh viện Vinmec đang sử dụng thuốc ngừa dại Verorab (0.5mL/liều) theo ph&aacute;c đồ Essen.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đối tượng chưa ti&ecirc;m dự ph&ograve;ng: ti&ecirc;m 05 mũi Verorab (0.5ml/liều) v&agrave;o c&aacute;c ng&agrave;y 0, 3, 7, 14, 28. Đối với trường hợp phơi nhiễm độ III cần phối hợp ti&ecirc;m Immunoglobulin dại kết hợp.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đối tượng đ&atilde; ti&ecirc;m dự ph&ograve;ng trong 5 năm gần đ&acirc;y: ti&ecirc;m 02 liều v&agrave;o ng&agrave;y 0 v&agrave; 3.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đối tượng đ&atilde; ti&ecirc;m dự ph&ograve;ng kh&ocirc;ng đều hay qu&aacute; 5 năm: ti&ecirc;m 05 mũi v&agrave;o c&aacute;c ng&agrave;y 0,3,7,14,28 v&agrave; c&oacute; thể ti&ecirc;m th&ecirc;m Immunoglobulin.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hy vọng với những chia sẻ tr&ecirc;n đ&acirc;y sẽ gi&uacute;p bạn c&oacute; th&ecirc;m được kiến thức về c&aacute;ch sơ cứu ch&oacute;/ m&egrave;o cắn cũng như việc chủ động trong việc ti&ecirc;m vacxin để ph&ograve;ng ngừa sao cho tốt nhất.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Để đặt lịch kh&aacute;m tại viện, Qu&yacute; kh&aacute;ch vui l&ograve;ng bấm số&nbsp;<a href=\"https://www.vinmec.com/vi/tin-tuc/thong-tin-suc-khoe/suc-khoe-tong-quat/huong-dan-so-cuu-vet-thuong-bi-chomeo-can/#\"><strong>HOTLINE</strong></a>&nbsp;hoặc đặt lịch trực tiếp&nbsp;<a href=\"https://www.vinmec.com/vi/tin-tuc/thong-tin-suc-khoe/suc-khoe-tong-quat/huong-dan-so-cuu-vet-thuong-bi-chomeo-can/#\"><strong>TẠI Đ&Acirc;Y</strong></a>. Tải v&agrave; đặt lịch kh&aacute;m tự động tr&ecirc;n&nbsp;<a href=\"https://www.vinmec.com/myvinmec/\" target=\"_blank\">ứng dụng MyVinmec</a>&nbsp;để được&nbsp;<strong>giảm ngay 15% ph&iacute; kh&aacute;m</strong>&nbsp;khi đặt hẹn kh&aacute;m lần đầu tr&ecirc;n to&agrave;n hệ thống Vinmec (&aacute;p dụng từ 17/10 - 31/12/2022). Qu&yacute; kh&aacute;ch cũng c&oacute; thể quản l&yacute;, theo d&otilde;i lịch v&agrave; đặt hẹn&nbsp;<a href=\"https://www.vinmec.com/vi/telehealth/\" target=\"_blank\">tư vấn từ xa qua video</a>&nbsp;với c&aacute;c b&aacute;c sĩ Vinmec mọi l&uacute;c mọi nơi ngay tr&ecirc;n ứng dụng.</span></span></p>\r\n', './image/20190524-033820-939863-ava2-15543722288911.max-1800x1800.jpg', '2022-12-08', 1, ''),
(11, 'Xử lý khi chó bị ngộ độc phải làm sao?', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng ta biết rằng vật nu&ocirc;i ăn phải chất độc hoặc chất c&oacute; độc t&iacute;nh l&agrave; một điều hết sức b&igrave;nh thường. Nếu th&uacute; cưng của bạn c&oacute; những h&agrave;nh vi bất thường, bạn đ&atilde; chứng kiến l&agrave; th&uacute; cưng của bạn ăn phải chất độc th&igrave; cần đưa đến b&aacute;c sĩ th&uacute; y ngay lập tức để điều trị.&nbsp;</span></span><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ocirc;ng thường khi ch&oacute; m&egrave;o ăn phải chất độc hoặc nhiễm phải nọc độc thường g&acirc;y ra những hiện tượng to&agrave;n th&acirc;n v&agrave; đặc biệt nhất l&agrave; rối loạn đường ruột, thường xuất hiện c&aacute;c triệu chứng như: N&ocirc;n mửa, ti&ecirc;u chảy, bại liệt, giảm th&acirc;n nhiệt, đ&ocirc;i khi c&oacute; triệu chứng cũng như bệnh dại.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" height=\"400\" src=\"https://thuyprocare.com/upload/images/bc8-1280x640.jpeg\" width=\"800\" /></span></span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a name=\"_fc588yp1ug4a\"></a><strong>Một số loại bệnh ngộ độc tr&ecirc;n ch&oacute; m&egrave;o m&agrave; bạn n&ecirc;n biết</strong></span></span></h2>\r\n\r\n<h3><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a name=\"_676eylewt5zj\"></a>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Bệnh ngộ độc do rắn cắn</strong></span></span></h3>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Biểu hiện của ngộ độc do rắn cắn l&agrave; c&aacute;c vết cắn nhỏ sưng l&ecirc;n rất nhanh, đỏ, bầm đen, tim đập nhanh, mạch gấp, ni&ecirc;m mạc mắt đỏ. Ch&oacute; m&egrave;o ủ rũ, n&ocirc;n mửa, kh&oacute; cử động, kh&aacute;t nước, ti&ecirc;u chảy,... Sau 6-12 giờ nếu kh&ocirc;ng được chữa trị kịp thời sẽ dần yếu đi v&agrave; chết.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Sơ cứu hiệu quả nhất với bệnh ngộ độc do rắn cắn l&agrave;:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">+&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T&igrave;m ngay vết cắn để nặn m&aacute;u lấy nọc độc ra</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">+&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nếu vết cắn ở bốn ch&acirc;n, buộc gar&ocirc; ph&iacute;a tr&ecirc;n</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">+&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đưa ch&oacute; đến ph&ograve;ng mạch th&uacute; &yacute; để điều trị</span></span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a name=\"_12929rlky59y\"></a>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Bệnh ngộ độc thuốc chuột</strong></span></span></h3>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c loại thuốc chuột như hợp chất thủy ng&acirc;n sunfua kẽm, clorua bari...trộn v&agrave;o thức ăn để diệt chuột, ch&oacute; ăn phải bị ngộ độc. Biểu hiện chảy d&atilde;i, n&ocirc;n mửa, ti&ecirc;u chảy, c&oacute; khi ph&acirc;n lẫn m&aacute;u, bắp thịt nh&atilde;o, suy tim, co giật, t&ecirc; liệt. Ch&oacute; c&oacute; thể chết ngay trong những giờ đầu.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Phương ph&aacute;p điều trị bệnh ngộ độc thuốc chuột</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&aacute;c sĩ sử dụng phương ph&aacute;p ti&ecirc;m Atropin, Vitamin K, Vitamin C để thuốc giảm t&aacute;c dụng, chậm hấp thụ qua ruột v&agrave; ph&ograve;ng ngừa t&aacute;c dụng g&acirc;y lo&atilde;ng m&aacute;u, chảy m&aacute;u của thuốc chuột. Để ch&oacute; nằm nơi k&iacute;n, tho&aacute;ng m&aacute;t, c&oacute; đệm l&oacute;t chỗ ch&oacute; nằm. Nếu ch&oacute; bị hạ nhiệt phải n&acirc;ng nhiệt bằng c&aacute;ch sưởi, ủ ấm, sấy hoặc bằng thuốc.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng ta c&oacute; thể sử dụng phương ph&aacute;p g&acirc;y n&ocirc;n để tống hết c&aacute;c chất đang chứa b&ecirc;n trong dạ d&agrave;y l&agrave; một c&aacute;ch nhanh nhất, an to&agrave;n nhất bằng c&aacute;ch cấp Calcium nhanh qua tĩnh mạch th&igrave; c&oacute; sẽ &oacute;i ra trong v&agrave;i ph&uacute;t ngay sau khi cấp thuốc.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Dịch truyền:</strong>&nbsp;Lactate ringer, Glucose 5%, 10%, 30% truyền cho đến khi c&aacute;c dấu hiệu nguy hiểm giảm.</span></span></p>\r\n\r\n<h3><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a name=\"_azg2rtac1tf0\"></a>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Bệnh ngộ độc muối ăn</strong></span></span></h3>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thường khi th&uacute; cưng của bạn ăn thịt c&aacute; muối rửa kh&ocirc;ng sạch muối hoặc ăn thức ăn c&oacute; nhiều muối th&igrave; ch&oacute; của bạn sẽ c&oacute; biểu hiện suy nhược, miệng chảy nước d&atilde;i nhiều bọt, n&ocirc;n mửa, ti&ecirc;u chảy ph&acirc;n c&oacute; lẫn m&aacute;u, kh&aacute;t nước, kh&oacute; thở, thần kinh hưng phấn, c&oacute; l&uacute;c dữ tợn v&agrave; hung h&atilde;n. L&uacute;c n&agrave;y, th&uacute; cưng của bạn c&oacute; lẽ đ&atilde; bị ngộ độc muối ăn v&agrave; nếu để bệnh ngộ độc nặng hơn ch&oacute; sẽ c&oacute; những triệu chứng như ủ rũ, mạch yếu, mất cảm gi&aacute;c, run, co giật v&agrave; liệt.</span></span></p>\r\n\r\n<h2><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a name=\"_7spo7w2ccuda\"></a><strong>Biện ph&aacute;p tốt nhất khi ch&oacute; m&egrave;o bị bệnh ngộ độc</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Để th&uacute; cưng của bạn c&oacute; cơ hội sống tốt th&igrave; khi ph&aacute;t hiện thấy ch&oacute; m&egrave;o c&oacute; biểu hiện của bệnh ngộ độc th&igrave; h&atilde;y th&ocirc;ng b&aacute;o ngay cho b&aacute;c sĩ th&uacute; y. Phương ph&aacute;p điều trị bệnh ngộ độc tr&ecirc;n ch&oacute; m&egrave;o l&agrave; đặc hiệu đối với chất độc c&oacute; li&ecirc;n quan v&agrave; c&aacute;c triệu chứng. Cho n&ecirc;n khi th&uacute; cưng vừa nuốt chửng chất độc, việc điều trị c&oacute; thể thực hiện ngay bằng c&aacute;ch n&ocirc;n mửa, g&acirc;y n&ocirc;n bằng cấp Calcium qua tĩnh mạch sẽ gi&uacute;p th&uacute; cưng n&ocirc;n &oacute;i c&aacute;c chất chứa trong ruột. Hơn nữa, c&aacute;c b&aacute;c sĩ th&uacute; y sẽ cho bạn biết c&oacute; n&ecirc;n g&acirc;y n&ocirc;n hay kh&ocirc;ng th&igrave; phải dựa v&agrave;o t&igrave;nh h&igrave;nh thực tế tr&ecirc;n ch&oacute; m&egrave;o của bạn. Lưu &yacute; ch&uacute;ng ta kh&ocirc;ng tự &yacute; xử l&yacute; nếu kh&ocirc;ng c&oacute; chuy&ecirc;n m&ocirc;n sẽ hại cho th&uacute; cưng của bạn.C&aacute;c b&aacute;c sĩ th&uacute; y sẽ thực hiện phương ph&aacute;p xử l&yacute; kịp thời v&agrave; t&ugrave;y thuộc thể trạng của từng th&uacute; cưng. V&igrave; vậy, khi th&uacute; cưng bạn mắc phải bệnh ngộ độc th&igrave; h&atilde;y li&ecirc;n hệ ngay đến hotline: 0909 836 777 để được b&aacute;c sĩ th&uacute; y Procare chăm s&oacute;c tại nh&agrave; hoặc hướng dẫn phương ph&aacute;p sơ cứu nhanh nhất để cứu mạng sống th&uacute; cưng bạn nhanh nhất.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&uacute; Y Procare dịch vụ chăm s&oacute;c v&agrave; điều trị c&aacute;c bệnh thường gặp ở ch&oacute; m&egrave;o hiệu quả - nhanh nhất - an to&agrave;n!</span></span></p>\r\n', './image/khi-cho-meo-an-phai-ba-phai-lam-sao.jpg', '2022-12-15', 1, '');
INSERT INTO `news` (`id`, `title`, `content`, `image`, `date`, `status_news`, `slug`) VALUES
(12, 'Xử lý khi chó bị hóc xương???', '<p><span style=\"font-size:16px\">C&aacute;ch chữa ch&oacute; h&oacute;c xương đ&atilde; bao giờ l&agrave;m cho bạn đau đầu v&agrave; kh&oacute; khăn? Ch&oacute; c&oacute; thể nhai mọi thứ: từ xương g&agrave;, xương lợn tới cả những t&uacute;i đựng thức ăn của ch&uacute;ng. Tuy c&oacute; h&agrave;m răng nhọn chuy&ecirc;n để cắn x&eacute; thịt nhưng đ&ocirc;i khi ch&oacute; vẫn bị h&oacute;c.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Đ&acirc;y kh&ocirc;ng phải l&agrave; t&igrave;nh trạng nguy hiểm như những bệnh g&acirc;y ra bởi c&aacute;c lo&agrave;i virus hoặc vi khuẩn kh&aacute;c nhưng c&oacute; thể g&acirc;y nguy hiểm tới t&iacute;nh mạng của ch&oacute;.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"cách chữa chó hóc xương\" height=\"530\" sizes=\"(max-width: 730px) 100vw, 730px\" src=\"https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-2.jpg\" srcset=\"https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-2.jpg 730w, https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-2-300x218.jpg 300w\" width=\"730\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Khi ch&oacute; bị h&oacute;c dị vật, dị vật sẽ chặn&nbsp;<a href=\"https://petshopsaigon.vn/tin-tuc/cho-bi-so-mui\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>đường h&ocirc; hấp của ch&oacute;</strong></a>. V&igrave; vậy biểu hiện khi bị h&oacute;c của ch&oacute; kh&aacute; hạn chế.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nếu ch&oacute; nh&agrave; bạn kh&oacute; thở, thở hổn hển, chảy nước d&atilde;i, n&ocirc;n mửa v&agrave; hoảng loạn, h&atilde;y mở miệng ch&oacute; ra v&agrave; xem liệu c&oacute; thể lấy dị vật mắc ở họng ch&oacute; ra kh&ocirc;ng.&nbsp;</span></p>\r\n\r\n<h2><span style=\"font-size:16px\">Nguy&ecirc;n nh&acirc;n l&agrave;m ch&oacute; bị h&oacute;c xương (h&oacute;c dị vật)</span></h2>\r\n\r\n<hr />\r\n<p><span style=\"font-size:16px\">Đầu ti&ecirc;n, h&atilde;y cố gắng b&igrave;nh tĩnh nếu thấy ch&oacute; m&igrave;nh đang bị h&oacute;c. V&igrave; bạn l&agrave; người duy nhất c&oacute; thể gi&uacute;p đỡ c&aacute;ch chữa ch&oacute; h&oacute;c xương n&ecirc;n h&atilde;y giữ c&aacute;i đầu lạnh v&agrave; ph&acirc;n t&iacute;ch vấn đề một c&aacute;ch hợp l&yacute; nh&eacute;.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một số nguy&ecirc;n nh&acirc;n phổ biến l&agrave;m ch&oacute; h&oacute;c dị vật bao gồm:&nbsp;</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><strong>Xương:&nbsp;</strong>Đừng cho ch&oacute; ăn xương thừa d&ugrave; cho ch&oacute; c&oacute; muốn ăn tới mức n&agrave;o. Những mảnh xương nhỏ c&oacute; thể mắc kẹt trong kh&iacute; quản của ch&uacute;ng. Hơn nữa, c&aacute;c cạnh xương sắc c&oacute; thể l&agrave;m cổ họng ch&uacute;ng tổn thương.</span></li>\r\n	<li><span style=\"font-size:16px\"><strong><a href=\"https://petshopsaigon.vn/danh-muc/do-choi-cho-cho\" rel=\"noopener noreferrer\" target=\"_blank\">Đồ chơi</a></strong><strong>:</strong>&nbsp;Những loại đồ chơi nhai hoặc những quả b&oacute;ng nhỏ cũng c&oacute; thể mắc kẹt trong cổ họng của ch&oacute;. Ch&oacute; ở mọi độ tuổi c&oacute; thể bị h&oacute;c đồ chơi, nhưng những con ch&oacute; con &ldquo;tăng động&rdquo; c&oacute; nguy cơ mắc cao hơn. H&atilde;y đảm bảo đồ chơi d&agrave;nh cho ch&oacute; c&oacute; k&iacute;ch thước đủ lớn để kh&ocirc;ng l&agrave;m ch&uacute;ng bị nghẹt thở.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"cách chữa chó hóc xương\" height=\"530\" sizes=\"(max-width: 730px) 100vw, 730px\" src=\"https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-3.jpg\" srcset=\"https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-3.jpg 730w, https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-3-300x218.jpg 300w\" width=\"730\" /></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><strong>Gậy v&agrave; c&aacute;c mảnh đồ đạc kh&aacute;c:&nbsp;</strong>Nếu bạn để ch&oacute; chơi b&ecirc;n ngo&agrave;i nhưng kh&ocirc;ng gi&aacute;m s&aacute;t, ch&uacute;ng c&oacute; thể sẽ nuốt phải những đồ vật lạ v&agrave; l&agrave;m kh&iacute; quản bị nghẹt. Nếu ch&oacute; nh&agrave; bạn gặm đồ đạc khi ch&uacute;ng đang mọc răng, những mảnh đồ đạc nhọn sẽ mắc v&agrave;o cổ họng của ch&uacute;ng. Bạn c&oacute; thể bảo vệ ch&oacute; v&agrave; đồ đạc của m&igrave;nh bằng c&aacute;ch bọc hết nội thất v&agrave;o cho tới khi ch&oacute; mọc răng xong.</span></li>\r\n	<li><span style=\"font-size:16px\"><strong>Tr&agrave;n dịch kh&iacute; quản:</strong>&nbsp;Những giống ch&oacute; nhỏ như Shih Tzu, Poodle v&agrave; Pug c&oacute; nguy cơ bị tr&agrave;n dịch kh&iacute; quản cao. Khi mắc phải t&igrave;nh trạng n&agrave;y, ch&oacute; sẽ thở hổn hển v&agrave; kh&ograve; kh&egrave;. Nếu kh&ocirc;ng được điều trị ngay lập tức, ch&oacute; c&oacute; thể tử vong.</span></li>\r\n	<li><span style=\"font-size:16px\"><strong>V&ograve;ng cổ, d&acirc;y x&iacute;ch qu&aacute; chặt:&nbsp;</strong>Một nguy&ecirc;n nh&acirc;n m&agrave; đa số chủ nu&ocirc;i thường phớt lờ đ&oacute; l&agrave; v&ograve;ng cổ hoặc d&acirc;y x&iacute;ch qu&aacute; chật cũng c&oacute; thể l&agrave;m ch&oacute; bị nghẹn. V&igrave; vậy h&atilde;y nới lỏng v&ograve;ng cổ v&agrave; d&acirc;y x&iacute;ch của ch&oacute; ra để tr&aacute;nh l&agrave;m cổ v&agrave; thực quản của ch&uacute;ng bị thương.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Xem th&ecirc;m:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><a href=\"https://petshopsaigon.vn/tin-tuc/cach-chua-cho-an-phai-ba\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>C&aacute;ch chữa ch&oacute; ăn phải bả, thuốc diệt chuột</strong></a></span></li>\r\n	<li><span style=\"font-size:16px\"><a href=\"https://petshopsaigon.vn/tin-tuc/cho-bi-yeu-chan-sau\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>C&aacute;ch chữa ch&oacute; bị yếu ch&acirc;n sau</strong></a></span></li>\r\n	<li><span style=\"font-size:16px\"><a href=\"https://petshopsaigon.vn/tin-tuc/cho-bi-benh-duong-ruot\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>C&aacute;ch chữa ch&oacute; bị bệnh đường ruột</strong></a></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><img alt=\"cách chữa chó hóc xương\" height=\"530\" sizes=\"(max-width: 730px) 100vw, 730px\" src=\"https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-4.jpg\" srcset=\"https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-4.jpg 730w, https://petshopsaigon.vn/wp-content/uploads/2020/08/cach-chua-cho-hoc-xuong-4-300x218.jpg 300w\" width=\"730\" /></span></p>\r\n\r\n<hr />\r\n<h2><span style=\"font-size:16px\">Dấu hiệu ch&oacute; bị h&oacute;c xương</span></h2>\r\n\r\n<hr />\r\n<p><span style=\"font-size:16px\">Trước khi t&igrave;m hiểu về c&aacute;ch chữa ch&oacute; h&oacute;c xương, bạn cần t&igrave;m hiểu về dấu hiệu nhận biết trước.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Những dấu hiệu biểu hiện ch&oacute; bị h&oacute;c xương thường dễ nhầm lẫn với những t&igrave;nh trạng kh&aacute;c do &acirc;m thanh của những hiện tượng n&agrave;y kh&aacute; giống nhau.&nbsp;</span></p>\r\n', './image/phai-lam-gi-khi-cho-bi-hoc-xuong-3.jpg', '2022-12-18', 0, ''),
(13, 'Hướng dẫn chăm sóc cún con đúng cách cho người mới', '<p><strong><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chăm s&oacute;c sức khỏe, ti&ecirc;m ph&ograve;ng bệnh</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; bước đặc biệt quan trọng trong c&aacute;ch chăm s&oacute;c ch&oacute; con mới đẻ. C&oacute; 2 c&aacute;ch ph&ograve;ng bệnh m&agrave; bạn cần quan t&acirc;m l&agrave; ti&ecirc;m vaccine v&agrave; tẩy giun</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ti&ecirc;m Vaccine:</strong></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong giai đoạn mới sinh chăm s&oacute;c ch&oacute; con rất quan trọng,&nbsp;<a href=\"https://www.chotot.com/tags/mua-ban-cho/cho-con\" rel=\"noopener\" target=\"_blank\">ch&oacute; con</a>&nbsp;c&oacute; lượng kh&aacute;ng thể rất thấp n&ecirc;n dễ mắc c&aacute;c bệnh nguy hiểm, cho n&ecirc;n ti&ecirc;m ph&ograve;ng l&agrave; bước thiết yếu. C&oacute; 3 loại, ph&ograve;ng 3 bệnh, 5 bệnh v&agrave; 7 bệnh. N&ecirc;n chọn loại 5 hoặc 7 bệnh v&igrave; hiệu quả ph&ograve;ng bệnh cao hơn rất nhiều. Loại 5 bệnh sẽ ph&ograve;ng c&aacute;c bệnh: Care, Pravo, vi&ecirc;m gan truyền nhiễm, ph&oacute; c&uacute;m, ho cũi ch&oacute;, trong đ&oacute; 2 bệnh nguy hiểm v&agrave; dễ gặp nhất l&agrave; Care v&agrave; Pravo. Ngo&agrave;i ra, loại 7 bệnh c&oacute; th&ecirc;m Leptospria v&agrave; Coronavirus.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&oacute; con khoảng 3 tuần tuổi n&ecirc;n bắt đầu ti&ecirc;m mũi đầu ti&ecirc;n, 6 tuần ti&ecirc;m mũi thứ 2. Th&ocirc;ng thường, đến mũi thứ 2 l&agrave; c&oacute; thể ngưng nhưng nếu muốn chắc chắn hơn th&igrave; c&oacute; thể ti&ecirc;m tiếp mũi thứ 3 v&agrave;o 9 th&aacute;ng tuổi. Khoảng 12 tuần tuổi c&oacute; thể ti&ecirc;m ph&ograve;ng dại v&agrave; mỗi năm một lần. Bạn c&oacute; thể mang c&uacute;n tới c&aacute;c cơ sở th&uacute; y để ti&ecirc;m, gi&aacute; của mỗi mũi dao động từ 120 &ndash; 200 ngh&igrave;n đồng.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/tiem-vaccine-cho-cho.jpg\"><img alt=\"Tiêm vaccine đúng cách để phòng bệnh cho chó\" height=\"399\" src=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/cach-cham-soc-cho-con-mau-lon-khoe-manh-cho-nguoi-moi-nuoi-cho-13481.jpg\" width=\"600\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Ti&ecirc;m vaccine, tẩy giun đ&uacute;ng c&aacute;ch để ph&ograve;ng bệnh cho ch&oacute;</em></span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Tẩy giun s&aacute;n:</strong></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch chăm s&oacute;c ch&oacute; con đầu ti&ecirc;n l&agrave; tẩy giun cho ch&oacute; con cần phải thực hiện từ sớm v&agrave; thường xuy&ecirc;n theo chu kỳ với từng độ tuổi nhất định. Với ch&oacute; dưới 6 th&aacute;ng tuổi, n&ecirc;n tẩy giun cho ch&oacute; mỗi th&aacute;ng 1 lần, ri&ecirc;ng 4 lần đầu ti&ecirc;n tẩy 2 tuần 1 lần, bắt đầu từ 2 tuần tuổi (2 tuần, 4 tuần, 6 tuần, 8 tuần). Ch&oacute; tr&ecirc;n 6 th&aacute;ng tuổi cứ 3 &ndash; 4 th&aacute;ng 1 lần cho đến khi được 1 tuổi th&igrave; chỉ cần lặp lại mỗi năm 1 lần.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chế độ dinh dưỡng trong c&aacute;ch chăm s&oacute;c ch&oacute; con</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch chăm s&oacute;c ch&oacute; con ph&aacute;t triển đầy đủ v&agrave; khỏe mạnh th&igrave; khẩu phần ăn của ch&uacute;ng phải cung cấp đầy đủ c&aacute;c chất dinh dưỡng, năng lượng như: protein, chất b&eacute;o, tinh bột, vitamin, kho&aacute;ng chất. Tuy nhi&ecirc;n, kh&ocirc;ng phải ở giai đoạn n&agrave;o lượng thức ăn cũng giống nhau.</span></span></p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dưới 3 tuần tuổi l&agrave; giai đoạn ch&oacute; con cần được b&uacute; mẹ, v&igrave; vậy phải cung cấp chế độ ăn đầy đủ dinh dưỡng cho ch&oacute; mẹ để chăm s&oacute;c ch&oacute; con. Sau 4 &ndash; 5 ng&agrave;y đầu mới sinh b&uacute; mẹ ho&agrave;n to&agrave;n, c&oacute; thể cho c&uacute;n uống th&ecirc;m sữa ấm.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sau 3 tuần tuổi c&oacute; thể cho ch&oacute; ăn ch&aacute;o lo&atilde;ng, nấu chung với thịt băm v&agrave; rau xay nhuyễn, đến khi 1 th&aacute;ng bạn c&oacute; thể giảm dần lượng nước v&agrave; cho ch&uacute;ng l&agrave;m quen với việc ăn cơm.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Giai đoạn từ 1 &ndash; 2 th&aacute;ng tuổi cho ăn 5 bữa 1 ng&agrave;y đủ chất dinh dưỡng v&agrave; giảm dần dần c&ograve;n 2 bữa một ng&agrave;y khi ch&oacute; con được 3 th&aacute;ng.</span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi đủ 4 th&aacute;ng tuổi ch&uacute;ng c&oacute; thể ăn như ch&oacute; trưởng th&agrave;nh.</span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/cach-cham-soc-cho-con-moi-sinh.jpg\"><img alt=\"Cho ăn uống đúng cách theo độ tuôi và thể trạng\" height=\"365\" src=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/cach-cham-soc-cho-con-moi-sinh.jpg\" width=\"600\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Cho ăn uống đ&uacute;ng c&aacute;ch, đủ dinh dưỡng theo độ tuổi v&agrave; thể trạng</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Những thức ăn tuyệt đối cấm với ch&oacute; con:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dưới 3 th&aacute;ng tuổi kh&ocirc;ng được cho ch&oacute; gặm xương v&igrave; ch&uacute;ng kh&ocirc;ng thể ti&ecirc;u h&oacute;a được v&agrave; rất dễ bị h&oacute;c. Tr&aacute;nh để thức ăn qu&aacute; n&oacute;ng, qu&aacute; lạnh, qu&aacute; nhiều chất b&eacute;o v&agrave; đặc biệt kiểm tra thức ăn kh&ocirc;ng bị &ocirc;i, thiu. Kh&ocirc;ng n&ecirc;n cho ch&uacute;ng ăn c&aacute;c loại thức ăn như gan, phổi b&ograve; v&agrave; heo v&igrave; kh&ocirc;ng đảm bảo an to&agrave;n. Ngo&agrave;i ra, kh&ocirc;ng để c&uacute;n của bạn ăn qu&aacute; no trong mỗi bữa.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đảm bảo m&ocirc;i trường sống cho ch&oacute; con</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cần đảm bảo nơi ở của ch&oacute; con lu&ocirc;n được sạch sẽ, kh&ocirc; tho&aacute;ng v&agrave; ấm, l&oacute;t một lớp vải vừa đủ dưới chỗ nằm,&nbsp;<a href=\"https://www.chotot.com/tags/mua-ban-thu-cung-khac-phu-kien/chuong-cho\" rel=\"noopener\" target=\"_blank\">chuồng ch&oacute;</a>. Những ng&agrave;y đầu kh&ocirc;ng được để ch&uacute;ng nằm điều h&ograve;a hoặc nằm trước quạt v&igrave; rất dễ bị cảm lạnh. Thường xuy&ecirc;n vệ sinh nơi ở v&agrave; thay lớp vải l&oacute;t. Lưu &yacute; trong 2 tuần đầu ti&ecirc;n kh&ocirc;ng tắm cho c&uacute;n m&agrave; chỉ cần lau nhẹ nh&agrave;ng bằng vải/ khăn ướt. Khi được một th&aacute;ng tuổi, bạn n&ecirc;n bắt đầu dạy ch&uacute;ng c&aacute;ch đi vệ sinh, việc n&agrave;y hết sức quan trọng trong c&aacute;ch chăm s&oacute;c ch&oacute; con v&igrave; nếu để l&acirc;u ch&uacute;ng sẽ đi vệ sinh lung tung trong nh&agrave; rất bất tiện.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/cach-cham-soc-cho-con-mau-lon-khoe-manh-cho-nguoi-moi-nuoi-cho-13484-e1539245953533.jpg\"><img alt=\"Môi trường sạch sẽ khi nuôi husky\" height=\"320\" src=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/cach-cham-soc-cho-con-mau-lon-khoe-manh-cho-nguoi-moi-nuoi-cho-13484-e1539245953533.jpg\" width=\"600\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Đảm bảo m&ocirc;i trường sống lu&ocirc;n sạch sẽ, thường xuy&ecirc;n chơi đ&ugrave;a c&ugrave;ng ch&oacute; con</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngo&agrave;i m&ocirc;i trường sống, người nu&ocirc;i ch&oacute; cũng cần phải quan t&acirc;m đến sự ph&aacute;t triển t&acirc;m l&yacute; ở th&uacute; cưng. Đặc biệt với những b&eacute; mới t&aacute;ch mẹ, n&ecirc;n thường xuy&ecirc;n vuốt ve v&agrave; chơi với ch&uacute;ng. Nếu c&uacute;n của bạn c&oacute; dấu hiệu ủ rũ qu&aacute; l&acirc;u th&igrave; rất dễ dẫn đến bỏ ăn, bị bệnh.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Huấn luyện ch&oacute; con tự lập v&agrave; nghe lời</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Để việc chăm s&oacute;c ch&oacute; con trở n&ecirc;n dễ d&agrave;ng hơn, bạn n&ecirc;n huấn luyện ch&oacute;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c giai đoạn ph&aacute;t triển của ch&oacute; con v&agrave; những lưu &yacute;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch chăm s&oacute;c ch&oacute; con mới đẻ</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&oacute; con mới sinh cần sự chăm s&oacute;c đặc biệt từ mẹ v&agrave; cần b&uacute; sữa mẹ sau khi ch&agrave;o đời. C&aacute;ch chăm s&oacute;c ch&oacute; con mới sinh kh&ocirc;ng cần qu&aacute; phức tạp, bạn chỉ cần chuẩn bị ổ đẻ đủ ấm &aacute;p nhưng kh&ocirc;ng qu&aacute; rườm r&agrave; tr&aacute;nh ch&oacute; sơ sinh chui v&agrave;o những g&oacute;c khuất của ổ đẻ kh&ocirc;ng chui ra được v&agrave; khuất tầm mắt của ch&oacute; mẹ.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&oacute; con mới sinh chưa mở mắt v&agrave; rất dễ bị tổn thương, tuần đầu ti&ecirc;n&nbsp; c&aacute;c b&eacute; ấy chỉ ngủ v&agrave; b&uacute; mẹ. L&uacute;c n&agrave;y c&aacute;c cơ quan chức năng chưa được ph&aacute;t triển đầy đủ, chỉ c&oacute; thể ngửi đ&aacute;nh hơi mẹ v&agrave; b&ograve; t&igrave;m mẹ. Thời gian n&agrave;y ch&oacute; mẹ sẽ thường xuy&ecirc;n vệ sinh sạch sẽ cho ch&oacute; con bằng c&aacute;ch liếm sạch sẽ cho ch&oacute; sơ sinh từ đầu tới ch&acirc;n cho tới khi ch&oacute; con lớn, tự đi lại v&agrave; biết tự đi vệ sinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch chăm ch&oacute; con 1 th&aacute;ng tuổi</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khoảng thời gian mới sinh cho tới khi ch&oacute; con đạt 1 th&aacute;ng tuổi, chỉ n&ecirc;n để ch&oacute; con b&uacute; sữa mẹ ho&agrave;n to&agrave;n để hệ ti&ecirc;u h&oacute;a mạnh khỏe v&agrave; ph&aacute;t triển ho&agrave;n, hệ ti&ecirc;u h&oacute;a của ch&oacute; con l&uacute;c n&agrave;y vẫn c&ograve;n yếu n&ecirc;n kh&ocirc;ng n&ecirc;n cho ch&oacute; con ăn thức ăn kh&oacute; ti&ecirc;u h&oacute;a.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&oacute; con l&uacute;c n&agrave;y cũng đ&atilde; mở mắt v&agrave; cứng c&aacute;p hơn trong việc đi lại, v&agrave; tự t&igrave;m chỗ vệ sinh. Bạn cũng n&ecirc;n để &yacute; hướng dẫn khu vực ri&ecirc;ng cho ch&oacute; c&oacute; thể tự đ&aacute;nh hơi v&agrave; đi về sinh đ&uacute;ng chỗ nếu như nh&agrave; bạn c&oacute; kh&ocirc;ng gian nhỏ, chung cư, căn hộ&hellip;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">N&ecirc;n đem ch&oacute; con ch&iacute;ch ngừa mũi đầu ti&ecirc;n v&agrave;o l&uacute;c đạt 1 th&aacute;ng tuổi để ph&ograve;ng tr&aacute;nh c&aacute;c bệnh nguy hiểm cho ch&oacute; con.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch chăm s&oacute;c ch&oacute; con 2 th&aacute;ng tuổi</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Từ l&uacute;c ch&oacute; con 1 th&aacute;ng tuổi đến khi đạt 2 th&aacute;ng tuổi, ch&oacute; con đ&atilde; mở mắt ho&agrave;n to&agrave;n v&agrave; cứng c&aacute;p hơn, chạy nhảy vui đ&ugrave;a nhiều hơn. Giai đoạn n&agrave;y ch&oacute; con cũng đ&atilde; bắt đầu mọc răng v&igrave; thế l&uacute;c b&uacute; mẹ sẽ cắn v&agrave;o n&uacute;m v&uacute; ch&oacute; mẹ, sẽ c&oacute; trường hợp ch&oacute; mẹ đau sẽ h&ugrave; cắn lại ch&oacute; con.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&uacute;c n&agrave;y cũng l&agrave; l&uacute;c bạn n&ecirc;n t&aacute;ch mẹ cho đ&agrave;n ch&oacute; con, v&agrave; bắt đầu tập cho ch&oacute; con uống sữa ấm, đồ ăn kh&ocirc; ng&acirc;m mềm hoặc ăn ch&aacute;o lo&atilde;ng với nước xương hầm. Hạn chế kh&ocirc;ng cho ch&oacute; con ăn đồ ăn dầu mỡ, đồ ăn của người sẽ g&acirc;y kh&oacute; ti&ecirc;u, kh&ocirc;ng cho ăn đồ cứng qu&aacute; hoặc ăn xương, sẽ g&acirc;y h&oacute;c. L&uacute;c n&agrave;y cũng l&agrave; thười gian bạn n&ecirc;n đem ch&oacute; con ti&ecirc;m ngừa th&ecirc;m c&aacute;c mũi c&ograve;n lại.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch chăm s&oacute;c ch&oacute; con 3-6 th&aacute;ng tuổi</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Độ tuổi n&agrave;y ch&oacute; con đ&atilde; mọc răng v&agrave; thường xuy&ecirc;n nhai gặm, cắn ph&aacute; v&igrave; độ tuổi n&agrave;y kh&aacute; hiếu động. Với những ch&uacute; ch&oacute; hay chạy đi chơi, c&oacute; thể ch&uacute;ng sẽ c&ocirc;ng những vật dụng ch&uacute;ng gặm về nh&agrave;. Đ&acirc;y cũng l&agrave; l&yacute; do khiến ch&oacute; con dễ mắc phải c&aacute;c bệnh về đường ruột v&igrave; tiếp x&uacute;c những mầm bệnh tiềm ẩn những nơi ch&oacute; con tiếp x&uacute;c. Độ tuổi n&agrave;y bạn c&oacute; thể mua đồ chơi để những ch&uacute; ch&oacute; con c&oacute; thể vui chơi, gặm cắn thay v&igrave; cắn ph&aacute; đồ đạc trong nh&agrave;.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&uacute;c n&agrave;y hệ ti&ecirc;u h&oacute;a của ch&oacute; con đang dần được ph&aacute;t triển ho&agrave;n thiện ho&agrave;n to&agrave;n, đ&atilde; c&oacute; thể tập ăn cơm, thức ăn hạt từ th&aacute;ng thứ 3-4. B&ecirc;n cạnh đ&oacute; bạn cũng c&oacute; thể bổ sung th&ecirc;m kho&aacute;ng chất bằng thực phẩm bổ sung kho&aacute;ng, canxi như xương gặm, b&aacute;nh thưởng,&hellip; Đ&acirc;y cũng l&agrave; l&uacute;c bạn n&ecirc;n bắt đầu huấn luyện ch&oacute; con đi vệ sinh đ&uacute;ng nơi đ&uacute;ng chỗ, những hiệu lệnh cơ bản. Đ&acirc;y cũng l&agrave; độ tuổi ch&oacute; con đ&atilde; bộc lộ ra những n&eacute;t t&iacute;nh c&aacute;ch rất c&aacute; nh&acirc;n, bạn n&ecirc;n để &yacute;, thấu hiểu v&agrave; y&ecirc;u thương, huấn luyện c&aacute;c em ấy theo t&iacute;nh c&aacute;ch kh&aacute;c nhau.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c lưu &yacute; về c&aacute;ch chăm s&oacute;c ch&oacute; con</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ocirc;ng thường, ch&oacute; con được c&aacute;c trại ch&oacute;, cửa h&agrave;ng th&uacute; cưng xuất chuồng khi đủ 2 &ndash; 3 th&aacute;ng tuổi, v&agrave;o độ tuổi n&agrave;y th&igrave; ch&uacute;ng đ&atilde; bộc lộ một số đặc điểm về thể trạng. Khi đi mua ch&oacute; cần lưu &yacute; chọn ch&oacute; nhanh nhẹn, c&oacute; thể hơi nghịch ngợm một ch&uacute;t. Tốt nhất bạn n&ecirc;n t&igrave;m mua từ c&aacute;c địa chỉ&nbsp;<a href=\"https://www.chotot.com/mua-ban-cho\" rel=\"noreferrer noopener\" target=\"_blank\">mua b&aacute;n ch&oacute; cảnh</a>&nbsp;c&oacute; nguồn gốc, giấy tờ v&agrave; được ti&ecirc;m vaccine, tẩy giun s&aacute;n đầy đủ để đảm bảo đ&oacute;n được một b&eacute; th&uacute; cưng khỏe mạnh về nh&agrave;. Nếu vậy, việc chăm s&oacute;c sẽ đơn giản hơn hẳn.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/e733e191899d66062696b8423c293a2c-e1539246069670.jpg\"><img alt=\"Huấn luyện chó con từ nhỏ giúp chăm sóc chó con dễ dàng hơn\" height=\"408\" src=\"https://static.chotot.com/storage/chotot-kinhnghiem/c2c/2018/10/cach-cham-soc-cho-con-mau-lon-khoe-manh-cho-nguoi-moi-nuoi-cho-13487.jpg\" width=\"600\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Huấn luyện ch&oacute; con từ nhỏ gi&uacute;p chăm s&oacute;c ch&oacute; con dễ d&agrave;ng hơn</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuy nhi&ecirc;n, trong nhiều trường hợp nếu bạn sở hữu những b&eacute; ch&oacute; con nhỏ tuổi hơn, v&iacute; dụ mua từ những người nu&ocirc;i c&oacute; ch&oacute; mẹ đẻ, hay được cho (kh&ocirc;ng dưới 3 tuần tuổi),&hellip; th&igrave; cần t&igrave;m hiểu kỹ hơn về c&aacute;c c&aacute;ch chăm s&oacute;c ch&oacute; con mới t&aacute;ch mẹ, c&aacute;ch chăm s&oacute;c ch&oacute; con mới đẻ. Đặc biệt phải theo d&otilde;i thường xuy&ecirc;n trong giai đoạn đầu.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ecirc;m một lưu &yacute; l&agrave; c&aacute;c giống ch&oacute; nhỏ b&eacute; như&nbsp;<a href=\"https://www.chotot.com/mua-ban-cho-chihuahua-sddp5\" rel=\"noreferrer noopener\" target=\"_blank\">ch&oacute; Chihuahua</a>, Toy Poodle, Ch&oacute; Phốc,&hellip; thường c&oacute; sức đề kh&aacute;ng kh&ocirc;ng được tốt như ch&oacute; lớn, v&igrave; vậy phải chăm s&oacute;c kỹ lưỡng hơn. Tuy nhi&ecirc;n vẫn c&oacute; một v&agrave;i giống ch&oacute; lớn cũng cần được chăm s&oacute;c đặc biệt, điển h&igrave;nh như lo&agrave;i ch&oacute; tuyết th&iacute;ch hợp với kh&iacute; hậu lạnh như&nbsp;<a href=\"https://www.chotot.com/mua-ban-cho-husky-sddp12\" rel=\"noreferrer noopener\" target=\"_blank\">ch&oacute; Husky</a>, Alaska.</span></span></p>\r\n', './image/cach-cham-soc-cho-con-moi-sinh.jpg', '2022-12-16', 1, '');

