--
-- Cấu trúc bảng cho bảng `sevice`
--

CREATE TABLE `sevice` (
  `id` int(11) NOT NULL,
  `title` varchar(255) NOT NULL,
  `image` varchar(255) NOT NULL,
  `content` longtext NOT NULL,
  `price` decimal(8,3) NOT NULL,
  `status_sevice` int(11) NOT NULL,
  `slug` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `sevice`
--

INSERT INTO `sevice` (`id`, `title`, `image`, `content`, `price`, `status_sevice`, `slug`) VALUES
(16, 'Dịch vụ tiêm phòng cho mèo', './image/bang-gia-tiem-phong-cho-meo-1-e1571448830509.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ti&ecirc;m vaccine đặc biệt cần thiết đối với m&egrave;o con</strong>, gi&uacute;p cho hệ miễn dịch của m&egrave;o ngăn ngừa sự x&acirc;m nhập của c&aacute;c virus g&acirc;y bệnh nguy hiểm. C&oacute; vai tr&ograve; quan trọng như vậy đối với sự sống của m&egrave;o n&ecirc;n chủ nu&ocirc;i cần d&agrave;nh sự quan t&acirc;m cho vấn đề n&agrave;y.&nbsp;<em>B&agrave;i viết sẽ giới thiệu đến bạn th&ocirc;ng tin về lịch ti&ecirc;m vaccine</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Vaccine được chia ra hai loại:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Loại phải ti&ecirc;m (được xem như quan trọng đối với tất cả c&aacute;c giống m&egrave;o).</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">loại n&ecirc;n ti&ecirc;m (ti&ecirc;m cho m&egrave;o dựa tr&ecirc;n lối sống v&agrave; c&aacute;c l&yacute; do li&ecirc;n quan kh&aacute;c).</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&aacute;c loại vaccine &ldquo;n&ecirc;n ti&ecirc;m&rdquo; bao gồm:</strong>&nbsp;vaccine ph&ograve;ng vi r&uacute;t g&acirc;y bệnh bạch cầu, vi r&uacute;t g&acirc;y suy giảm miễn dịch, Bordetella,v.v.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Bốn loại vaccine nhất định phải ti&ecirc;m l&agrave;:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vaccine ph&ograve;ng bệnh Dại (1)</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vaccine ph&ograve;ng bệnh Giảm Bạch Cầu (2)</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vaccine ph&ograve;ng bệnh Vi&ecirc;m mũi &ndash; kh&iacute; quản truyền nhiễm (3)</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vaccine ph&ograve;ng bệnh do Herpervirus (4)</span></span></li>\r\n</ul>\r\n\r\n<h4><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Lịch ti&ecirc;m vaccine cho m&egrave;o</strong></span></span></h4>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Mũi thứ nhất</strong>&nbsp;n&ecirc;n ti&ecirc;m khi m&egrave;o con bắt đầu được 2 th&aacute;ng tuổi v&agrave; ti&ecirc;m trước 4 th&aacute;ng tuổi. Đ&acirc;y l&agrave; mũi 4 trong 1, ph&ograve;ng n&agrave;y&nbsp;ph&ograve;ng c&aacute;c bệnh giảm bạch cầu, vi&ecirc;m mũi &ndash; kh&iacute; quản truyền nhiễm v&agrave; bệnh do Herpervirus.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Mũi ti&ecirc;m thứ hai,</strong>&nbsp;cũng để ph&ograve;ng c&ugrave;ng c&aacute;c bệnh n&agrave;y, ti&ecirc;m c&aacute;ch mũi thứ nhất từ 4 &ndash; 6 tuần.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Mũi thứ ba</strong>&nbsp;ti&ecirc;m c&aacute;ch mũi thứ hai 4 tuần.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Khi m&egrave;o được 12 tuần</strong>&nbsp;tuổi l&agrave; l&uacute;c bạn cần ti&ecirc;m vaccine ph&ograve;ng bệnh dại cho m&egrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">N&ecirc;n ho&agrave;n th&agrave;nh việc ti&ecirc;m vaccine n&oacute;i tr&ecirc;n trước khi m&egrave;o tr&ograve;n một năm tuổi. Về sau, ti&ecirc;m nhắc lại hằng năm 1 lần với tất cả c&aacute;c loại vaccine. Tốt hơn hết, bạn cũng n&ecirc;n hỏi &yacute; kiến b&aacute;c sĩ th&uacute; y trước khi tiến h&agrave;nh việc ti&ecirc;m vaccine cho m&egrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Tiêm phòng cho mèo và những thông tin cần lưu ý - PetCare\" src=\"https://www.chamsocpet.com/wp-content/uploads/2020/10/thong-tin-can-luu-y-ve-tiem-phong-meo-2.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Vacine rất quan trọng trong việc chăm s&oacute;c sức khỏe của m&egrave;o.</strong><br />\r\nTuy nhi&ecirc;n, bạn cần thảo luận với b&aacute;c sĩ th&uacute; y về phương ph&aacute;p ti&ecirc;m ph&ograve;ng tốt nhất cho m&egrave;o của bạn.<br />\r\nNhững nh&acirc;n tố cần được xem x&eacute;t khi ti&ecirc;m ph&ograve;ng vaccine cho m&egrave;o bao gồm: loại vaccine, độ tuổi, tiền sử bệnh, m&ocirc;i trường, lối sống của m&egrave;o,&hellip;<br />\r\nLi&ecirc;n hệ&nbsp;<strong>Myvet</strong>&nbsp;để được tư vấn trực tiếp khi ti&ecirc;m ph&ograve;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>ĐỘI NGŨ NH&Acirc;N VI&Ecirc;N L&Agrave;M VIỆC BẰNG CHUY&Ecirc;N M&Ocirc;N, SỰ NHIỆT T&Igrave;NH V&Agrave; TR&Aacute;CH NHIỆM</strong><br />\r\nVới đội ngũ nh&acirc;n vi&ecirc;n c&oacute; tinh thần tr&aacute;ch nhiệm cao,&nbsp;<strong>Myvet</strong>&nbsp;lu&ocirc;n sẵn l&ograve;ng lắng nghe, tư vấn, b&ecirc;n cạnh bạn trong mọi vấn đề khi chăm s&oacute;c v&agrave; nu&ocirc;i dạy th&uacute; cưng.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><strong>*Danh s&aacute;ch một số kh&aacute;ch h&agrave;ng đ&atilde; sử dụng dịch vụ ti&ecirc;m ph&ograve;ng tại Myvet</strong></em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://myvet.vn/wp-content/uploads/2021/09/z2822160749303_f4948e57dce2bb0926624319daa6c4d6.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/09/z2822160749303_f4948e57dce2bb0926624319daa6c4d6.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/09/z2822160749303_f4948e57dce2bb0926624319daa6c4d6.jpg 956w, https://myvet.vn/wp-content/uploads/2021/09/z2822160749303_f4948e57dce2bb0926624319daa6c4d6-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/09/z2822160749303_f4948e57dce2bb0926624319daa6c4d6-600x801.jpg 600w\" width=\"956\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><em>Ngo&agrave;i dịch vụ ti&ecirc;m ph&ograve;ng cho m&egrave;o, Myvet c&ograve;n cung cấp c&aacute;c dịch vụ kh&aacute;c như:</em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Spa Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Chụp X-Quang Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Si&ecirc;u &Acirc;m Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phẫu Thuật Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Triệt Sản Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Xuất Nhập Cảnh Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỏa T&aacute;ng Th&uacute; Cưng</span></span></li>\r\n</ul>\r\n', '500.000', 1, ''),
(17, 'Dịch vụ triệt sản cho chó', './image/unnamed.jpg', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Mypet</strong>&nbsp;sẽ giải đ&aacute;p một số thắc mắc xung quanh vấn đề triệt sản cho th&uacute; cưng, cụ thể l&agrave; ch&oacute; m&egrave;o trong b&agrave;i viết n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>1. Triệt sản l&agrave; g&igrave;?</em></strong><br />\r\nTriệt sản được hiểu đơn giản l&agrave; loại bỏ cơ quan sinh dục của con vật.<br />\r\nỞ con đực l&agrave; tinh ho&agrave;n, c&ograve;n ở con c&aacute;i ch&iacute;nh l&agrave; buồng trứng.<br />\r\nTriệt sản l&agrave; một qu&aacute; tr&igrave;nh phẫu thuật hết sức phức tạp, nhất l&agrave; đối với con c&aacute;i. V&igrave; vậy cần c&oacute; chuy&ecirc;n m&ocirc;n thực sự về th&uacute; y mới c&oacute; thể thực hiện được c&ocirc;ng việc n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>2- Tại sao lại triệt sản ch&oacute;, m&egrave;o?</strong></em><br />\r\nViệc triệt sản ch&oacute;, m&egrave;o đ&atilde; được giới chức tr&aacute;ch v&agrave; th&uacute; y nghi&ecirc;n cứu đưa ra những kết luận v&agrave; đ&aacute;nh gi&aacute; l&agrave; cần thiết:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Khống chế sinh sản ngo&agrave;i &yacute; muốn, giảm số lượng ch&oacute; m&egrave;o v&ocirc; gia cư. Ch&oacute;, m&egrave;o đẻ nhiều kh&ocirc;ng quản l&yacute; được ch&oacute;, m&egrave;o con, kh&ocirc;ng chăm s&oacute;c được tốt nếu chủ th&uacute; cưng bận rộn.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kiềm chế c&aacute;c h&agrave;nh vi xấu: Ở ch&oacute;, m&egrave;o đực kh&ocirc;ng được triệt sản thường c&oacute; h&agrave;nh vi tiểu bậy khắp nơi nhằm mục đ&iacute;ch &ldquo;đ&aacute;nh dấu&rdquo; điều n&agrave;y đ&atilde; g&acirc;y kh&aacute; nhiều phiền to&aacute;i cho những người chủ của ch&uacute;ng. Triệt sản c&oacute; thể giảm được t&igrave;nh trạng n&agrave;y hoặc kết th&uacute;c n&oacute; ho&agrave;n to&agrave;n.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hạn chế t&igrave;nh trạng m&egrave;o bỏ nh&agrave; ra đi khi đến kỳ động dục. Ch&oacute;, m&egrave;o c&oacute; thể k&ecirc;u la g&agrave;o th&eacute;t t&igrave;m bạn khi đến thời kỳ động dục, g&acirc;y mất trật tự, phiền to&aacute;i cho x&oacute;m giềng. Đặc biệt khi ch&oacute; m&egrave;o bỏ nh&agrave; đi t&igrave;m bạn c&oacute; thể bị bắt trộm v&agrave; tạo nguy cơ l&acirc;y nhiễm bệnh Dại nguy hiểm cho người nếu kh&ocirc;ng được ti&ecirc;m vaccine ph&ograve;ng bệnh Dại.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Triệt sản cho ch&oacute;, m&egrave;o c&oacute; thể l&agrave;m tăng tuổi thọ v&agrave; tr&aacute;nh được nguy cơ của c&aacute;c bệnh ung thư tinh ho&agrave;n, ung thư tuyến tiền liệt ở ch&oacute;, m&egrave;o đực v&agrave; giảm c&aacute;c bệnh ung thư, vi&ecirc;m tử cung, vi&ecirc;m hệ thống sinh sản ở ch&oacute; m&egrave;o c&aacute;i.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>3. Khi n&agrave;o c&oacute; thể triệt sản cho ch&oacute;, m&egrave;o?</strong></em><br />\r\nĐối với ch&oacute;, độ tuổi căn bản ph&ugrave; hợp cho việc triệt sản l&agrave; từ 6-9 th&aacute;ng tuổi. Với ch&oacute; c&oacute; độ tuổi nhiều hơn 6-9 th&aacute;ng, việc triệt sản sẽ kh&oacute; khăn hơn một ch&uacute;t v&igrave; ch&uacute;ng c&oacute; thể bị thừa c&acirc;n.<br />\r\nĐối với m&egrave;o tốt nhất trước kỳ động dục đầu ti&ecirc;n, khoảng 6 th&aacute;ng tuổi<br />\r\nTriệt sản m&egrave;o mẹ khi m&egrave;o con được 1 th&aacute;ng tuổi, biết ăn v&agrave; chơi đ&ugrave;a. V&igrave; nếu l&acirc;u hơn, m&egrave;o mẹ nhanh ch&oacute;ng mang thai lại mặc d&ugrave; con vẫn theo b&uacute; mẹ.<br />\r\nKh&ocirc;ng n&ecirc;n triệt sản khi m&egrave;o đang động dục (g&agrave;o đực) v&igrave; đường sinh dục sung huyết, dễ mất m&aacute;u. Phải chắc chắn m&egrave;o kh&ocirc;ng mang thai khi đưa đi triệt sản.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Với những l&yacute; do tr&ecirc;n, bạn đ&atilde; c&oacute; thể thấy được&nbsp;<strong>những l&yacute; do phải triệt sản ch&oacute; &amp; m&egrave;o</strong>.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Tuy nhi&ecirc;n,Triệt sản l&agrave; một qu&aacute; tr&igrave;nh phẫu thuật hết sức phức tạp, nhất l&agrave; đối với con c&aacute;i. V&igrave; vậy cần c&oacute; chuy&ecirc;n m&ocirc;n thực sự về th&uacute; y mới c&oacute; thể thực hiện được c&ocirc;ng việc n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>4. Địa chỉ ph&ograve;ng kh&aacute;m th&uacute; cưng uy t&iacute;n: Mypet</strong></em><br />\r\n<strong>Mypet</strong>&nbsp;với đầy đủ c&aacute;c trang thiết bị m&aacute;y m&oacute;c hỗ trợ c&ugrave;ng chuy&ecirc;n m&ocirc;n cao v&agrave; nhiều năm kinh nghiệm trong việc triệt sản ch&oacute; m&egrave;o.<br />\r\nH&atilde;y li&ecirc;n lạc để ch&uacute;ng t&ocirc;i c&oacute; thể triệt sản cho th&uacute; cưng của bạn tại nh&agrave; hoặc mang th&uacute; cưng đến với ch&uacute;ng t&ocirc;i để việc triệt sản ch&oacute; m&egrave;o trở n&ecirc;n dễ d&agrave;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>*Một số h&igrave;nh ảnh triệt sản thực tế tại Mypet</em></strong></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd-600x801.jpg 600w\" width=\"956\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8.jpg\"><img alt=\"\" height=\"1280\" sizes=\"(max-width: 960px) 100vw, 960px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8.jpg 960w, https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8-768x1024.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8-600x800.jpg 600w\" width=\"960\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b-600x801.jpg 600w\" width=\"956\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>Ngo&agrave;i dịch vụ triệt sản cho th&uacute; cưng, Mypet c&ograve;n cung cấp c&aacute;c dịch vụ kh&aacute;c như:</em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Spa Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Ti&ecirc;m Ph&ograve;ng Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Chụp X-Quang Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Si&ecirc;u &Acirc;m Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Phẫu Thuật Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Triệt Sản Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Xuất Nhập Cảnh Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hỏa T&aacute;ng Th&uacute; Cưng</span></span></li>\r\n</ul>\r\n', '750.000', 1, ''),
(18, 'Dịch vụ triệt sản cho mèo', './image/630x420-crcac-buoc-triet-san-cho-meo-min.jpg', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Myvet</strong>&nbsp;sẽ giải đ&aacute;p một số thắc mắc xung quanh vấn đề triệt sản cho th&uacute; cưng, cụ thể l&agrave; ch&oacute; m&egrave;o trong b&agrave;i viết n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>1. Triệt sản l&agrave; g&igrave;?</em></strong><br />\r\nTriệt sản được hiểu đơn giản l&agrave; loại bỏ cơ quan sinh dục của con vật.<br />\r\nỞ con đực l&agrave; tinh ho&agrave;n, c&ograve;n ở con c&aacute;i ch&iacute;nh l&agrave; buồng trứng.<br />\r\nTriệt sản l&agrave; một qu&aacute; tr&igrave;nh phẫu thuật hết sức phức tạp, nhất l&agrave; đối với con c&aacute;i. V&igrave; vậy cần c&oacute; chuy&ecirc;n m&ocirc;n thực sự về th&uacute; y mới c&oacute; thể thực hiện được c&ocirc;ng việc n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>2- Tại sao lại triệt sản ch&oacute;, m&egrave;o?</strong></em><br />\r\nViệc triệt sản ch&oacute;, m&egrave;o đ&atilde; được giới chức tr&aacute;ch v&agrave; th&uacute; y nghi&ecirc;n cứu đưa ra những kết luận v&agrave; đ&aacute;nh gi&aacute; l&agrave; cần thiết:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Khống chế sinh sản ngo&agrave;i &yacute; muốn, giảm số lượng ch&oacute; m&egrave;o v&ocirc; gia cư. Ch&oacute;, m&egrave;o đẻ nhiều kh&ocirc;ng quản l&yacute; được ch&oacute;, m&egrave;o con, kh&ocirc;ng chăm s&oacute;c được tốt nếu chủ th&uacute; cưng bận rộn.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kiềm chế c&aacute;c h&agrave;nh vi xấu: Ở ch&oacute;, m&egrave;o đực kh&ocirc;ng được triệt sản thường c&oacute; h&agrave;nh vi tiểu bậy khắp nơi nhằm mục đ&iacute;ch &ldquo;đ&aacute;nh dấu&rdquo; điều n&agrave;y đ&atilde; g&acirc;y kh&aacute; nhiều phiền to&aacute;i cho những người chủ của ch&uacute;ng. Triệt sản c&oacute; thể giảm được t&igrave;nh trạng n&agrave;y hoặc kết th&uacute;c n&oacute; ho&agrave;n to&agrave;n.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hạn chế t&igrave;nh trạng m&egrave;o bỏ nh&agrave; ra đi khi đến kỳ động dục. Ch&oacute;, m&egrave;o c&oacute; thể k&ecirc;u la g&agrave;o th&eacute;t t&igrave;m bạn khi đến thời kỳ động dục, g&acirc;y mất trật tự, phiền to&aacute;i cho x&oacute;m giềng. Đặc biệt khi ch&oacute; m&egrave;o bỏ nh&agrave; đi t&igrave;m bạn c&oacute; thể bị bắt trộm v&agrave; tạo nguy cơ l&acirc;y nhiễm bệnh Dại nguy hiểm cho người nếu kh&ocirc;ng được ti&ecirc;m vaccine ph&ograve;ng bệnh Dại.</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Triệt sản cho ch&oacute;, m&egrave;o c&oacute; thể l&agrave;m tăng tuổi thọ v&agrave; tr&aacute;nh được nguy cơ của c&aacute;c bệnh ung thư tinh ho&agrave;n, ung thư tuyến tiền liệt ở ch&oacute;, m&egrave;o đực v&agrave; giảm c&aacute;c bệnh ung thư, vi&ecirc;m tử cung, vi&ecirc;m hệ thống sinh sản ở ch&oacute; m&egrave;o c&aacute;i.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>3. Khi n&agrave;o c&oacute; thể triệt sản cho ch&oacute;, m&egrave;o?</strong></em><br />\r\nĐối với ch&oacute;, độ tuổi căn bản ph&ugrave; hợp cho việc triệt sản l&agrave; từ 6-9 th&aacute;ng tuổi. Với ch&oacute; c&oacute; độ tuổi nhiều hơn 6-9 th&aacute;ng, việc triệt sản sẽ kh&oacute; khăn hơn một ch&uacute;t v&igrave; ch&uacute;ng c&oacute; thể bị thừa c&acirc;n.<br />\r\nĐối với m&egrave;o tốt nhất trước kỳ động dục đầu ti&ecirc;n, khoảng 6 th&aacute;ng tuổi<br />\r\nTriệt sản m&egrave;o mẹ khi m&egrave;o con được 1 th&aacute;ng tuổi, biết ăn v&agrave; chơi đ&ugrave;a. V&igrave; nếu l&acirc;u hơn, m&egrave;o mẹ nhanh ch&oacute;ng mang thai lại mặc d&ugrave; con vẫn theo b&uacute; mẹ.<br />\r\nKh&ocirc;ng n&ecirc;n triệt sản khi m&egrave;o đang động dục (g&agrave;o đực) v&igrave; đường sinh dục sung huyết, dễ mất m&aacute;u. Phải chắc chắn m&egrave;o kh&ocirc;ng mang thai khi đưa đi triệt sản.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Với những l&yacute; do tr&ecirc;n, bạn đ&atilde; c&oacute; thể thấy được&nbsp;<strong>những l&yacute; do phải triệt sản ch&oacute; &amp; m&egrave;o</strong>.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Tuy nhi&ecirc;n,Triệt sản l&agrave; một qu&aacute; tr&igrave;nh phẫu thuật hết sức phức tạp, nhất l&agrave; đối với con c&aacute;i. V&igrave; vậy cần c&oacute; chuy&ecirc;n m&ocirc;n thực sự về th&uacute; y mới c&oacute; thể thực hiện được c&ocirc;ng việc n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>4. Địa chỉ ph&ograve;ng kh&aacute;m th&uacute; cưng uy t&iacute;n: Myvet</strong></em><br />\r\n<strong>Myvet</strong>&nbsp;với đầy đủ c&aacute;c trang thiết bị m&aacute;y m&oacute;c hỗ trợ c&ugrave;ng chuy&ecirc;n m&ocirc;n cao v&agrave; nhiều năm kinh nghiệm trong việc triệt sản ch&oacute; m&egrave;o.<br />\r\nH&atilde;y li&ecirc;n lạc để ch&uacute;ng t&ocirc;i c&oacute; thể triệt sản cho th&uacute; cưng của bạn tại nh&agrave; hoặc mang th&uacute; cưng đến với ch&uacute;ng t&ocirc;i để việc triệt sản ch&oacute; m&egrave;o trở n&ecirc;n dễ d&agrave;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>*Một số h&igrave;nh ảnh triệt sản thực tế tại Myvet</em></strong></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822159587884_e48cf87ea7478c6a7100e2ac8c0fffbd-600x801.jpg 600w\" width=\"956\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8.jpg\"><img alt=\"\" height=\"1280\" sizes=\"(max-width: 960px) 100vw, 960px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8.jpg 960w, https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8-768x1024.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822159591602_cff19db83c1995523ec5a0d5ce2e60d8-600x800.jpg 600w\" width=\"960\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822159592418_2e5089ee2109eadd9844b49b27bae37b-600x801.jpg 600w\" width=\"956\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>Ngo&agrave;i dịch vụ triệt sản cho th&uacute; cưng, Myvet c&ograve;n cung cấp c&aacute;c dịch vụ kh&aacute;c như:</em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Spa Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Ti&ecirc;m Ph&ograve;ng Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Chụp X-Quang Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Si&ecirc;u &Acirc;m Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Phẫu Thuật Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Triệt Sản Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Xuất Nhập Cảnh Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hỏa T&aacute;ng Th&uacute; Cưng</span></span></li>\r\n</ul>\r\n', '700.000', 1, ''),
(19, 'Phẫu thuật ngoại khoa', './image/co-dinh-chu-cho-khi-phau-thuat-compressed.jpg', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Th&uacute; y Mypet</strong>&nbsp;cung cấp một số dịch vụ phẫu thuật ngoại khoa:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>B&oacute; bột</strong></span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Kh&acirc;u ngo&agrave;i da, kh&acirc;u vết thương hở</strong></span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Triệt sản, mổ đẻ</strong></span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Cắt đu&ocirc;i, cắt tai, cắt mộng mắt, cắt h&eacute;c ni</strong></span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Th&ocirc;ng tiểu</strong></span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Phẫu thuật sỏi b&agrave;ng quang</strong></span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong>Đ&oacute;ng đinh nội tuỷ</strong></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b-600x801.jpg 600w\" width=\"956\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174608232_98f29136085a22fd20e3be8f593f6f55.jpg\"><img alt=\"\" height=\"1280\" sizes=\"(max-width: 960px) 100vw, 960px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174608232_98f29136085a22fd20e3be8f593f6f55.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174608232_98f29136085a22fd20e3be8f593f6f55.jpg 960w, https://myvet.vn/wp-content/uploads/2021/10/z2822174608232_98f29136085a22fd20e3be8f593f6f55-768x1024.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174608232_98f29136085a22fd20e3be8f593f6f55-600x800.jpg 600w\" width=\"960\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174612066_072a796193ff422dfd74862b2129c9f1.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174612066_072a796193ff422dfd74862b2129c9f1.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174612066_072a796193ff422dfd74862b2129c9f1.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822174612066_072a796193ff422dfd74862b2129c9f1-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174612066_072a796193ff422dfd74862b2129c9f1-600x801.jpg 600w\" width=\"956\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174618984_b04a9566924645d40f2249e97665b7d4.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174618984_b04a9566924645d40f2249e97665b7d4.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174618984_b04a9566924645d40f2249e97665b7d4.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822174618984_b04a9566924645d40f2249e97665b7d4-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174618984_b04a9566924645d40f2249e97665b7d4-600x801.jpg 600w\" width=\"956\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174618415_6ce0e22a4619521f6dffc6bac949342f.jpg\"><img alt=\"\" height=\"1280\" sizes=\"(max-width: 724px) 100vw, 724px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174618415_6ce0e22a4619521f6dffc6bac949342f.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174618415_6ce0e22a4619521f6dffc6bac949342f.jpg 724w, https://myvet.vn/wp-content/uploads/2021/10/z2822174618415_6ce0e22a4619521f6dffc6bac949342f-600x1061.jpg 600w\" width=\"724\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174626507_e9a51000f7e0d341eca56e9bd24ac11c.jpg\"><img alt=\"\" height=\"1280\" sizes=\"(max-width: 960px) 100vw, 960px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174626507_e9a51000f7e0d341eca56e9bd24ac11c.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174626507_e9a51000f7e0d341eca56e9bd24ac11c.jpg 960w, https://myvet.vn/wp-content/uploads/2021/10/z2822174626507_e9a51000f7e0d341eca56e9bd24ac11c-768x1024.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174626507_e9a51000f7e0d341eca56e9bd24ac11c-600x800.jpg 600w\" width=\"960\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174634732_5b62ac8ea2c6bb907a4e5d72fce0a66b.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174634732_5b62ac8ea2c6bb907a4e5d72fce0a66b.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174634732_5b62ac8ea2c6bb907a4e5d72fce0a66b.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822174634732_5b62ac8ea2c6bb907a4e5d72fce0a66b-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174634732_5b62ac8ea2c6bb907a4e5d72fce0a66b-600x801.jpg 600w\" width=\"956\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174636302_dbb52ae8aebd4e53122adb4370a51f18.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174636302_dbb52ae8aebd4e53122adb4370a51f18.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174636302_dbb52ae8aebd4e53122adb4370a51f18.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822174636302_dbb52ae8aebd4e53122adb4370a51f18-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174636302_dbb52ae8aebd4e53122adb4370a51f18-600x801.jpg 600w\" width=\"956\" /></a><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174643522_c37772e5340226fb719b229c46b56c84.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174643522_c37772e5340226fb719b229c46b56c84.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174643522_c37772e5340226fb719b229c46b56c84.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822174643522_c37772e5340226fb719b229c46b56c84-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174643522_c37772e5340226fb719b229c46b56c84-600x801.jpg 600w\" width=\"956\" /></a></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>Nếu th&uacute; cưng của bạn gặp c&aacute;c vấn đề cần kh&aacute;m, chữa bệnh, h&atilde;y li&ecirc;n hệ trực tiếp với Myvet để được tư vấn v&agrave; hỗ trợ kịp thời 24/24.<br />\r\nVới đội ngũ nh&acirc;n vi&ecirc;n c&oacute; tinh thần tr&aacute;ch nhiệm cao, Myvet lu&ocirc;n sẵn l&ograve;ng lắng nghe, tư vấn, b&ecirc;n cạnh bạn trong mọi vấn đề khi chăm s&oacute;c v&agrave; nu&ocirc;i dạy th&uacute; cưng.</strong></em></span></span></p>\r\n', '1200.000', 1, ''),
(20, 'Phẫu thuật đẻ cho chó mèo', './image/pet-surgery-17.png', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>Tại sao c&aacute;c con ch&oacute;, m&egrave;o mẹ cần được sinh mổ?</strong></em></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Ch&oacute; m&egrave;o mẹ kh&ocirc;ng thể tự sinh ch&oacute; con được v&igrave; nhiều l&yacute; do kh&aacute;c nhau như k&iacute;ch thước con ch&oacute; m&egrave;o con qu&aacute; lớn, k&iacute;ch thước v&agrave; h&igrave;nh dạng v&ugrave;ng xương chậu của ch&oacute; m&egrave;o mẹ bất thường, &hellip;</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&oacute; c&aacute;c hiện tượng bất thường trong giai đoạn thai k&igrave; như thai bị căng thẳng &acirc;m hộ tiết ra chất dịch m&agrave;u đỏ đen hoặc m&agrave;u xanh l&aacute; c&oacute; nghĩa rằng nhau thai bị t&aacute;ch ra hoặc bất thường của thai trong cơ thể k&iacute;ch th&iacute;ch tiết dịch</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nếu bạn quan s&aacute;t thấy th&uacute; cưng của m&igrave;nh c&oacute; những biểu hiện trong l&uacute;c mang thai kh&ocirc;ng được b&igrave;nh thường h&atilde;y nhờ đến sự tư vấn cửa b&aacute;c sĩ th&uacute; y. N&ecirc;n thường xuy&ecirc;n đến gặp b&aacute;c sĩ th&uacute; y để si&ecirc;u &acirc;m kiểm tra định k&igrave; cũng như tr&aacute;nh c&aacute;c căng thẳng kh&ocirc;ng đ&aacute;ng c&oacute; cho con ch&oacute; m&egrave;o của bạn.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em><strong>Địa chỉ ph&ograve;ng kh&aacute;m th&uacute; y uy t&iacute;n tại H&agrave; Nội: Th&uacute; Y Myvet</strong></em></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Với đội ngũ b&aacute;c sỹ gi&agrave;u kinh nghiệm v&agrave; hệ thống trang thiết bị hiện đại,&nbsp;<strong>Th&uacute; y Myvet</strong>&nbsp;tự h&agrave;o l&agrave; cơ sở th&uacute; y h&agrave;ng đầu khu vực H&agrave; Nội trong chuy&ecirc;n m&ocirc;n chẩn đo&aacute;n bệnh v&agrave; tư vấn chăm s&oacute;c sức khỏe th&uacute; cưng.<br />\r\nDịch vụ si&ecirc;u &acirc;m tại Ph&ograve;ng kh&aacute;m th&uacute; y Myvet gi&uacute;p chẩn đo&aacute;n ph&aacute;t hiện sớm c&aacute;c trường hợp sỏi đường tiết niệu, si&ecirc;u &acirc;m kiểm tra t&igrave;nh trạng thai v&agrave; số lượng thai.<br />\r\nTừ đ&oacute; c&aacute;c b&aacute;c sỹ c&oacute; thể tư vấn c&aacute;ch chăm s&oacute;c cho th&uacute; cưng của bạn.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><a href=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b-1.jpg\"><img alt=\"\" height=\"1276\" sizes=\"(max-width: 956px) 100vw, 956px\" src=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b-1.jpg\" srcset=\"https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b-1.jpg 956w, https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b-1-768x1025.jpg 768w, https://myvet.vn/wp-content/uploads/2021/10/z2822174603193_79ffca2d5c0072248d6254054a87be6b-1-600x801.jpg 600w\" width=\"956\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><strong><em>Ngo&agrave;i dịch vụ phẫu thuật, mổ đẻ cho th&uacute; cưng, Myvet c&ograve;n cung cấp c&aacute;c dịch vụ kh&aacute;c như:</em></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Spa Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Ti&ecirc;m Ph&ograve;ng Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Chụp X-Quang Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Si&ecirc;u &Acirc;m Cho Ch&oacute; M&egrave;o</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Phẫu Thuật Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Triệt Sản Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Xuất Nhập Cảnh Cho Th&uacute; Cưng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hỏa T&aacute;ng Th&uacute; Cưng</span></span></li>\r\n</ul>\r\n', '1000.000', 1, ''),
(21, 'Dịch vụ hỏa táng thú cưng', './image/dich-vu-mai-tang-thu-cung-burial-amp-cremation-dreampet-278109.jpg', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Với mong muốn l&agrave; cầu nối gi&uacute;p gia đ&igrave;nh, chủ nh&acirc;n c&aacute;c b&eacute; mang đến lời tạm biệt cuối c&ugrave;ng, ch&acirc;n th&agrave;nh, trọn vẹn nhất,<br />\r\n<strong>Mypet cung cấp dịch vụ Hỏa T&aacute;ng Th&uacute; Cưng</strong>&nbsp;bằng phương ph&aacute;p hiện đại v&agrave; đảm bảo nhất.<br />\r\n<img alt=\"Top 3 Dịch Vụ Hoả Táng Chó Mèo, Thú Cưng Uy Tín Tại TpHCM, Hà Nội • HAKU  Scent Marketing\" src=\"https://haku.vn/wp-content/uploads/2020/12/hinh-lam-hoa-hoa-tang-thu-cung.jpg\" /></span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\"><em>Đ&ocirc;i lời&nbsp;<strong>Mypet</strong>&nbsp;muốn nhắn gửi</em>:<br />\r\nMọi sinh vật đều c&oacute; vai tr&ograve; ri&ecirc;ng v&agrave; c&aacute;c đặc t&iacute;nh ri&ecirc;ng.Tạo h&oacute;a tạo ra c&aacute;c b&eacute;, qua c&aacute;c qu&aacute; tr&igrave;nh tiến h&oacute;a đ&atilde; trở th&agrave;nh bạn của con người, nhưng chỉ cho c&aacute;c b&eacute; một khoảng thời gian ngắn hơn con người rất nhiều.Từ hư kh&ocirc;ng, những người bạn nhỏ xuất hiện trong cuộc sống của ch&uacute;ng ta, mang lại cho ch&uacute;ng ta những hạnh ph&uacute;c, niềm vui, v&agrave; đến một thời điểm, c&aacute;c b&eacute; lại đi về chốn hư kh&ocirc;ng.<br />\r\nTa biết thời gian của ch&uacute;ng ta l&agrave; hữu hạn, v&agrave; ta đ&atilde; d&agrave;nh cho nhau những g&igrave; tốt đẹp nhất trong mối nh&acirc;n duy&ecirc;n n&agrave;y. D&ugrave; c&aacute;c b&eacute; rời xa ch&uacute;ng ta theo c&aacute;ch n&agrave;y hay c&aacute;ch kh&aacute;c, th&igrave; xin bạn h&atilde;y đừng qu&aacute; đau khổ.<br />\r\nSau n&agrave;y, mong bạn h&atilde;y mỉm cười mỗi khi nhớ về người bạn nhỏ của m&igrave;nh, v&igrave; đ&oacute; l&agrave; c&aacute;ch tuyệt vời nhất để nhớ thương c&aacute;c b&eacute;.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">H&atilde;y để&nbsp;<strong>Mypet</strong>&nbsp;đồng h&agrave;nh c&ugrave;ng c&aacute;c b&eacute; tr&ecirc;n chặng đường cuối c&ugrave;ng&hellip;</span></span></p>\r\n', '1150.000', 1, ''),
(26, 'Dịch vụ tắm thú cưng', './image/tam.png', '<h2><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>1. Tại sao n&ecirc;n tắm cho ch&oacute; m&egrave;o?</strong></span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Giữ bộ l&ocirc;ng lu&ocirc;n sạch sẽ</strong>: Như c&aacute;c bạn đ&atilde; biết th&igrave; đa số ch&oacute; m&egrave;o đều th&iacute;ch chạy nhảy, ưa vận động n&ecirc;n bộ l&ocirc;ng của ch&uacute;ng dễ bị d&iacute;nh vết bẩn. Do đ&oacute; việc tắm sẽ gi&uacute;p bạn loại bỏ bụi bẩn, m&ugrave;i h&ocirc;i kh&oacute; chịu tr&ecirc;n bộ l&ocirc;ng của th&uacute; cưng.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Giảm t&igrave;nh trạng rụng l&ocirc;ng ở ch&oacute; m&egrave;o</strong>: Điều n&agrave;y rất hữu &iacute;ch với những th&uacute; cưng hay bị rụng l&ocirc;ng. Việc tắm sẽ gi&uacute;p loại bỏ những phần l&ocirc;ng bị xơ rối, l&ocirc;ng thừa, tr&aacute;nh t&igrave;nh trạng l&ocirc;ng rơi khắp nh&agrave;.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ph&ograve;ng chống c&aacute;c bệnh về đường ruột</strong>: Khi l&ocirc;ng của ch&oacute; m&egrave;o vương v&atilde;i khắp nơi, việc ch&uacute;ng v&ocirc; t&igrave;nh ăn phải l&ocirc;ng rồi t&iacute;ch tụ lại trong ruột l&agrave; trường hợp đ&atilde; xảy ra. Nếu kh&ocirc;ng ph&aacute;t hiện v&agrave; xử l&yacute; kịp thời th&igrave; rất nguy hiểm.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ph&ograve;ng chống vật k&yacute; sinh v&agrave; c&aacute;c bệnh ngo&agrave;i da ở ch&oacute; m&egrave;o</strong>: Thời tiết ở nước ta l&agrave; n&oacute;ng ẩm, l&agrave; điều kiện th&iacute;ch hợp để vi khuẩn v&agrave; vật k&yacute; sinh như ve, rận&hellip; ph&aacute;t triển. Do đ&oacute; khi tắm th&igrave; th&uacute; cưng của bạn sẽ được loại bỏ c&aacute;c vi khuẩn, ve rận nguy hiểm b&aacute;m tr&ecirc;n da.</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>2. Khi n&agrave;o cần tắm cho th&uacute; cưng?</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Với ch&oacute; th&igrave; thời gian th&iacute;ch hợp nhất để tắm cho ch&uacute;ng l&agrave; từ 2 &ndash; 3 th&aacute;ng tuổi, tr&aacute;nh tắm cho b&eacute; dưới 6 tuần tuổi v&igrave; sức đề kh&aacute;ng c&ograve;n yếu. Đ&acirc;y ch&iacute;nh l&agrave; thời điểm v&agrave;ng để th&uacute; cưng của bạn l&agrave;m quen với nước, tr&aacute;nh t&igrave;nh trạng sợ nước, sau n&agrave;y sẽ rất kh&oacute; tắm, vệ sinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trung b&igrave;nh th&igrave; bạn n&ecirc;n tắm cho th&uacute; cưng của m&igrave;nh 1 lần/tuần. Ngo&agrave;i ra tần suất tắm cho ch&uacute;ng c&ograve;n phụ thuộc v&agrave;o nhiều yếu tố kh&aacute;c nhau như giống, độ tuổi, mức độ vận động v&agrave; m&ocirc;i trường sống. V&agrave; bạn h&atilde;y tắm cho ch&uacute;ng ngay khi xuất hiện những trường hợp sau:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi thấy cơ thể ch&oacute; m&egrave;o xuất hiện m&ugrave;i h&ocirc;i, g&acirc;y kh&oacute; chịu.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&ocirc;ng v&agrave; da d&iacute;nh nhiều bụi, vết bẩn.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi th&uacute; cưng c&oacute; biểu hiện g&atilde;i, ngứa nhiều.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave;o thời điểm rụng l&ocirc;ng.</span></span></li>\r\n</ul>\r\n', '150.000', 0, '');

