) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `shop`
--

INSERT INTO `shop` (`id_shop`, `title`, `content`, `price`, `promotion`, `image`, `id_category`, `status_shop`, `slug`) VALUES
(7, 'Rọ mõm hình vịt cho chó', '<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phần mỏ vịt được thiết kế với chất liệu silicon mềm dẻo, sắc m&agrave;u tươi s&aacute;ng tạo hiệu ứng thẩm mỹ tốt</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phần quai g&agrave;i được may bằng chất d&acirc;y đai chắc chắn, đảm bảo độ bền sử dụng l&acirc;u d&agrave;i</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Rọ m&otilde;m h&igrave;nh mỏ vịt cho ch&oacute; với nhiều m&agrave;u sắc đa dạng đ&aacute;ng y&ecirc;u sẽ biến ch&uacute; c&uacute;n nh&agrave; bạn th&agrave;nh một bạn vịt lai c&uacute;n v&ocirc; c&ugrave;ng đ&aacute;ng y&ecirc;u, thu h&uacute;t &aacute;nh nh&igrave;n của tất cả mọi người.</span></span></li>\r\n</ul>\r\n', '18.000', '15.000', './image/1227-2629539331-1837168188.jpg', 4, 1, ''),
(8, 'Lược innox chải lông thú cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Nu&ocirc;i ch&oacute; m&egrave;o, ai cũng phải gặp những con &aacute;c mộng về l&ocirc;ng khi đến m&ugrave;a thay l&ocirc;ng hay những khi ch&oacute; m&egrave;o đ&ugrave;a giỡn l&ocirc;ng bay lả tả h&agrave;ng ng&agrave;y. Thậm ch&iacute; những giống ch&oacute; cỏ hay cocker l&ocirc;ng nh&igrave;n ngắn vậy nhưng đến m&ugrave;a thay l&ocirc;ng, bạn vẫn thấy l&ocirc;ng ch&oacute; b&aacute;m đầy tr&ecirc;n giường nệm, b&agrave;n ghế...<br />\r\n+ Với những b&agrave;n chải hay lược b&igrave;nh thường, c&aacute;c bạn chải xong sờ v&agrave;o vẫn thấy l&ocirc;ng rụng ra v&igrave; vẫn chưa loại bỏ hết.<br />\r\n+ Lược chải l&ocirc;ng inox l&agrave; dụng cụ l&agrave;m đẹp kh&ocirc;ng thể thiếu cho vật nu&ocirc;i<br />\r\n+ Lược chải l&ocirc;ng cho ch&oacute; m&egrave;o, một mặt răng thưa, một mặt răng kh&iacute;t, th&iacute;ch hợp với từng loại ch&oacute; m&egrave;o<br />\r\n+ Được l&agrave;m từ chất liệu bền, chắc chắn</span></span></p>\r\n', '31.000', '0.000', './image/48e2c105948959327a74907103f42382.jpg', 4, 1, ''),
(9, 'Tấm nhựa lót sàn chồng cho chó', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&ndash; Được sản xuất bằng c&ocirc;ng nghệ hiện đại, sử dụng 100% từ nhựa nguy&ecirc;n sinh, kh&ocirc;ng tạp chất v&agrave; kh&ocirc;ng c&oacute; th&agrave;nh phần độc hại, c&oacute; độ bền cực cao.<br />\r\n&ndash; M&agrave;u sắc tươi mới, kh&ocirc;ng bị phai m&agrave;u theo thời gian, mặt s&agrave;n nhẵn đẹp được thiết kế c&oacute; độ nh&aacute;m gi&uacute;p việc đi lại dễ d&agrave;ng kh&ocirc;ng trơn trượt, xầy xước.<br />\r\n&ndash; Chịu được nắng mưa cực tốt, d&ugrave;ng h&agrave;ng chục năm s&agrave;n tr&ocirc;ng l&uacute;c n&agrave;o cũng như mới, c&aacute;c tấm được thiết kế m&oacute;c li&ecirc;n kết th&ocirc;ng minh, chỉ gh&eacute;p v&agrave;o nhau l&agrave; được.</span></span></p>\r\n', '56.000', '0.000', './image/c156661193436730f7d6aec9db0968e8.jpg', 3, 1, ''),
(10, 'Sữa tắm cho chó trị ve rận BBN Killing Mites Shampoo', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sữa tắm cho ch&oacute; trị ve rận BBN Killing Mites Shampoo&nbsp;đặc biệt tốt cho những ch&uacute; ch&oacute; bị ve v&agrave; nấm.&nbsp;Gi&uacute;p cơ thể c&uacute;n cưng tr&uacute;t bỏ c&aacute;c tế b&agrave;o da sần s&ugrave;i hoặc tế b&agrave;o chết.&nbsp;Dầu tự nhi&ecirc;n 100% gi&uacute;p l&agrave;m sạch s&acirc;u v&agrave; giữ ẩm cho l&ocirc;ng v&agrave; da ch&oacute; của bạn.&nbsp;Giảm men, nguồn thức ăn của ve tối đa.Tất cả dầu gội tự nhi&ecirc;n v&agrave; hữu cơ Pet Wash, c&ocirc;ng thức đặc biệt d&agrave;nh cho da kh&ocirc;, nhạy cảm, da v&agrave; l&ocirc;ng của ch&uacute; ch&oacute; của bạn. L&agrave; sản phẩm ho&agrave;n hảo cho những ch&uacute; ch&oacute; bị ngứa da do dị ứng hoặc da nhạy cảm. Khử m&ugrave;i v&agrave; gi&uacute;p l&agrave;m giảm nhiễm tr&ugrave;ng da do nấm v&agrave; vi khuẩn như vi&ecirc;m da &amp; vi&ecirc;m da mủ.</span></span></p>\r\n', '150.000', '0.000', './image/sua-tam-cho-cho-tri-ve-ran-bbn-killing-mites-shampoo-400x400.jpg', 4, 1, ''),
(11, 'Sữa tắm cho chó mèo hương hoa quýt JOYCE & DOLLS Sexy Bloom', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sữa tắm cho ch&oacute; m&egrave;o hương hoa qu&yacute;t JOYCE &amp; DOLLS Sexy Bloom được để thanh tẩy l&ocirc;ng, chống vi&ecirc;m v&agrave; ph&aacute;t triển c&aacute;c tế b&agrave;o mới. Sản phẩm n&agrave;y c&oacute; khả năng cải thiện lưu th&ocirc;ng m&aacute;u v&agrave; tạo sức đề kh&aacute;ng với c&aacute;c t&aacute;c động b&ecirc;n ngo&agrave;i, tạo cho th&uacute; cưng cảm gi&aacute;c trầm tĩnh khi sử dụng. Sản phẩm chứa hương thơm hoa tươi, giống như cảm thấy một th&ocirc;ng điệp tươi mới c&ugrave;ng hương thơm c&acirc;y hợp hoan gợi cảm, th&ecirc;m v&agrave;o tinh dầu hoa qu&yacute;t c&oacute; thể l&agrave;m sạch l&ocirc;ng. Với c&ocirc;ng thức hương thơm</span></span></p>\r\n', '250.000', '0.000', './image/sua-tam-cho-cho-meo-huong-hoa-quyt-joyce-dolls-sexy-bloom-0-400x400.jpg', 4, 1, ''),
(17, 'Áo bóng đá mùa world cup cho chó mới', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Kiểu d&aacute;ng trẻ trung v&agrave; thời trang cho c&uacute;n y&ecirc;u.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Vải mỏng, chất liệu tho&aacute;ng m&aacute;t.</span></span></p>\r\n', '21.000', '0.000', './image/ao-bong-da-thu-cung.png', 5, 1, ''),
(18, 'Áo Khoác Vải Lông Cừu Mùa Đông In Họa Tiết Chó Poodle Dễ Thương Cho Thú Cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">🐱🐶Ch&agrave;o mừng bạn đến với cửa h&agrave;ng của ch&uacute;ng t&ocirc;i. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💕T&ecirc;n sản phẩm: &aacute;o len cho th&uacute; cưng </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💕Chất liệu sản phẩm: polyester </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💕M&agrave;u sắc: x&aacute;m nhạt, n&acirc;u nhạt </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💕K&iacute;ch thước sản phẩm: </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">S: V&ograve;ng ngực 32 cm, chiều d&agrave;i lưng 20 cm, trọng lượng khuyến nghị 1,2-2kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M: V&ograve;ng ngực 37cm, chiều d&agrave;i lưng 25cm, c&acirc;n nặng khuyến nghị 2-3kg</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">L: V&ograve;ng ngực 42cm, chiều d&agrave;i lưng 30 cm, c&acirc;n nặng khuyến nghị 3-4,2kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Xl: v&ograve;ng ngực 47cm, chiều d&agrave;i lưng 35cm, trọng lượng khuyến nghị 4,2-6kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">2xl: v&ograve;ng ngực 52cm, chiều d&agrave;i lưng 40cm, c&acirc;n nặng khuyến nghị 6-9kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">🥰Vui l&ograve;ng chọn k&iacute;ch thước theo v&ograve;ng ngực của th&uacute; cưng của bạn. 🌻🌻🌻</span></span></p>\r\n', '75.000', '0.000', './image/screenshot-2022-12-12-015331.png', 5, 1, ''),
(19, 'Thức ăn Hạt ganador cho chó con', '<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">🌿 Ch&oacute; con 1 năm đầu mới ph&aacute;t triển n&ecirc;n cho ăn bổ sung thức ăn kh&ocirc; để đảm bảo đủ Canxi, kho&aacute;ng v&agrave; Vitamin. Khẩu phần thức ăn v&agrave; chế độ dinh dưỡng cho c&uacute;n từ 3- 7 th&aacute;ng tuổi l&agrave; quan trọng nhất v&igrave; đ&acirc;y l&agrave; thời kỳ tạo xương v&agrave; c&uacute;n con lớn rất nhanh. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ Ganador Sữa &amp; DHA được lập c&ocirc;ng thức v&agrave; thiết kế tại Ph&aacute;p cung ứng nhu cầu tăng trưởng cho ch&oacute; con tất cả c&aacute;c giống v&agrave; k&iacute;ch cỡ trong năm đầu. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ Chứa c&aacute;c chất kho&aacute;ng v&agrave; vitamin ho&agrave;n to&agrave;n c&acirc;n đối để bảo vệ v&agrave; gi&uacute;p th&uacute; cưng mạnh khỏe. Cung cấp calium, DHA v&agrave; Omega 3&amp;6 đầy đủ gi&uacute;p boss nhỏ c&oacute; thể tăng trưởng c&acirc;n đối. C&oacute; đ&uacute;ng số phần trăm DHA gi&uacute;p n&atilde;o ph&aacute;t triển tốt. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ Cũng được chỉ định d&ugrave;ng cho ch&oacute; c&aacute;i v&agrave;o cuối thai kỳ v&agrave; trong thời kỳ cho con b&uacute;.</span></span></p>\r\n', '150.000', '120.000', './image/thuc-an-cho-cho-ganador.jpg', 2, 1, ''),
(21, 'Royal Canin Mini Adult 2kg cho chó trưởng thành giống nhỏ - Thức ăn cho chó trên 10 tháng tuổi', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&ocirc;ng thức của loại thức ăn n&agrave;y được thiết kế để đ&aacute;p ứng c&aacute;c nhu cầu dinh dưỡng đặc th&ugrave; của giống ch&oacute; nhỏ trong độ tuổi dưới 10 th&aacute;ng tuổi.T&ugrave;y v&agrave;o từng giống ch&oacute; m&agrave; đặc t&iacute;nh sinh l&yacute; của ch&uacute;ng sẽ kh&aacute;c nhau, cấu tr&uacute;c xương, thiết kế h&agrave;m, hệ thống ti&ecirc;u h&oacute;a v&agrave; nhu cầu năng lượng cao.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Gi&uacute;p duy tr&igrave; sức khoẻ ti&ecirc;u h&oacute;a của ch&oacute;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Hỗ trợ nhu cầu năng lượng cao</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Thiết kế hạt gi&uacute;p giảm sự h&igrave;nh th&agrave;nh cao răng</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Hỗ trợ hệ miễn dịch khỏe mạnh</span></span></p>\r\n', '320.000', '0.000', './image/thuc-an-cho-cho-truong-thanh-co-nho-royal-canin-mini-adult2.png', 2, 1, ''),
(22, '[Hộp 12 gói] Pate Pedigree Cho Chó Con Vị Gà Nấu Sốt 130g', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Bạn c&oacute; biết, ch&oacute; cần gấp 3 lần Protein so với con người để ph&aacute;t triển, c&uacute;n con c&oacute; tốc độ ph&aacute;t triển nhanh gấp 12 lần so với trẻ em.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">V&igrave; thế, bổ sung đầy đủ dinh dưỡng bằng sản phẩm từ Pedigree cho c&uacute;n l&agrave; việc l&agrave;m cần thiết.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sản phẩm Sốt Pedigree Puppy vị g&agrave; nấu sốt sẽ cung cấp cho c&uacute;n con những lợi &iacute;ch sau: </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Trộn với cơm hoặc hạt kh&ocirc; để tạo bữa ăn dinh dưỡng với thịt nguy&ecirc;n chất đậm đ&agrave; v&agrave; nước sốt thơm ngon Cung cấp đủ dinh dưỡng, vitamin v&agrave; kho&aacute;ng chất, tăng cường hệ thống miễn dịch của c&uacute;n, gi&uacute;p c&uacute;n cưng ph&aacute;t triển khỏe mạnh, Sản phẩm thức ăn tiện lợi cho việc cai sữa của c&uacute;n trở n&ecirc;n dễ d&agrave;ng. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Gi&uacute;p c&uacute;n con dễ ti&ecirc;u h&oacute;a, bảo vệ đường ruột.</span></span></p>\r\n', '225.000', '0.000', './image/d29dcc165225376f38dd627cf5af890f.jpg-720x720q80.jpg-.png', 2, 1, ''),
(23, 'Pate Royal Canin RECOVERY Canine Feline 195g - Thức ăn phục hồi sức khoẻ chó mèo Petmall', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">H&agrave;m lượng năng lượng của ROYAL CANIN RECOVERY gi&uacute;p b&ugrave; đắp sự sụt giảm lượng thức ăn đối với vật nu&ocirc;i k&eacute;n chọn ăn uống.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Chỉ định:</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Biếng ăn &ndash; suy dinh dưỡng</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hỗ trợ dinh dưỡng hậu phẫu thuật v&agrave; chăm s&oacute;c đặc biệt</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Điều dưỡng; Ăn bằng tube</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Rối loạn chuyển h&oacute;a lipid gan ở m&egrave;o</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Mang thai, cho con b&uacute;, tăng trưởng</span></span></p>\r\n', '99.000', '79.000', './image/d29708ab325cba6466fdc4a269700426.jpg', 2, 1, ''),
(24, 'combo 12 gói sốt chó Pedigree 130gr mix vị', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Trộn với cơm hoặc hạt kh&ocirc; để tạo bữa ăn dinh dưỡng với thịt nguy&ecirc;n chất đậm đ&agrave; v&agrave; nước sốt thơm ngon.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ C&oacute; nhiều vị cho ch&oacute; thay đổi: vị g&agrave;, vị b&ograve;, vị gan.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Cung cấp đủ dinh dưỡng, vitamin v&agrave; kho&aacute;ng chất, gi&uacute;p tăng cường hệ thống miễn dịch của c&uacute;n, gi&uacute;p c&uacute;n cưng khỏe mạnh, năng động v&agrave; th&acirc;n thiện.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Gi&uacute;p c&uacute;n dễ ti&ecirc;u h&oacute;a, bảo vệ đường ruột. Kiểm so&aacute;t được khẩu phần ăn h&agrave;ng ng&agrave;y, tr&aacute;nh việc qu&aacute; nhiều để c&uacute;n bỏ lại thức ăn thừa.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Nhanh ch&oacute;ng, tiện lợi, kh&ocirc;ng tốn nhiều thời gian chuẩn bị.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ L&agrave;m đa dạng bữa ăn của c&uacute;n với c&aacute;c c&ocirc;ng thức: thức ăn hạt + sốt, cơm + Sốt</span></span></p>\r\n', '135.000', '0.000', './image/9627f469584973fb4f47f15ef5133ed0.jpg', 2, 1, ''),
(25, 'Thức Ăn Cho Chó Lớn Pedigree Vị Bò Lon 700g (Hàng nhập khẩu)', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sản phẩm với nhiều chất dinh dưỡng như canxi gi&uacute;p xương chắc khỏe, c&aacute;c chất vitamin E gi&uacute;p hệ ti&ecirc;u h&oacute;a hoạt động tốt hơn, tăng khả năng hấp thu c&aacute;c chất v&agrave;o cơ thể, bảo đảm cho sự ph&aacute;t triển khỏe mạnh của th&uacute; cưng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&aacute;c chất vitam C, A, D hay dầu c&aacute;, dầu thực vật, c&aacute;c chất chứa nhiều độ b&eacute;o sẽ gi&uacute;p da v&agrave; l&ocirc;ng của ch&oacute; trở n&ecirc;n mềm mượt hơn. Đem đến một ch&uacute; ch&oacute; vừa khỏe mạnh, vừa c&oacute; vẻ ngo&agrave;i đ&aacute;ng y&ecirc;u.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sản phẩm ph&ugrave; hợp với</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&aacute;c giống ch&oacute; lớn với nhiều độ tuổi kh&aacute;c nhau</span></span></p>\r\n', '150.000', '79.000', './image/1456f2e5c04973bf74fd863cb28a56a0.jpg', 2, 1, ''),
(26, 'Hạt hữu cơ cho chó ANF 6 Free Organic túi 400g đủ vị', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ ANF 6 Free được l&agrave;m từ nguy&ecirc;n liệu hữu cơ tự nhi&ecirc;n tốt cho sức khỏe, hạn chế t&igrave;nh trạng dị ứng thức ăn. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ Sản phẩm chứa thịt cừu gi&agrave;u protein v&agrave; vitamin (A; B6; B3; B12); bổ sung kho&aacute;ng chất (kẽm; sắt; photpho; canxi&hellip;) tăng cường sức khỏe.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ Chất đạm cao cấp trong thịt cừu rất dễ ti&ecirc;u h&oacute;a đối với th&uacute; cưng. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ C&aacute;c th&agrave;nh phần tr&ecirc;n gi&uacute;p chắc khỏe xương khớp v&agrave; cơ; tăng cường hệ miễn dịch v&agrave; mang lại l&agrave;n da khỏe đẹp.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">✅ Chứa rau quả hữu cơ như khoai t&acirc;y; l&uacute;a m&igrave;; b&iacute; đỏ; c&agrave; rốt; đậu xanh; gạo.</span></span></p>\r\n', '75.000', '0.000', './image/f0c61bd9f930df2e97886265c1a70589.jpg', 2, 1, ''),
(27, '[Mã PET80K giảm 8% tối đa 80K đơn 350K] Thức ăn chó con Pedigree vị gà & trứng bao 8kg', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hương vị g&agrave; v&agrave; trứng thơm ngon với nh&acirc;n sữa b&ecirc;n trong, gi&uacute;p c&uacute;n ăn ngon miệng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cung cấp đủ vitamin v&agrave; kho&aacute;ng chất, gi&uacute;p tăng cường hệ thống miễn dịch của c&uacute;n, gii&uacute;p c&uacute;n cưng ph&aacute;t triển khỏe mạnh, Bổ sung DHA v&agrave; Choline rất cần thiết cho sự ph&aacute;t triển của n&atilde;o bộ, để c&uacute;n thật th&ocirc;ng minh, lanh lợi. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sản phẩm chứa nhiều canxi v&agrave; phốt pho, gi&uacute;p cho răng v&agrave; xương c&uacute;n chắc khỏe. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Hạt nhỏ, vỏ mỏng gi&ograve;n, nh&acirc;n sữa thơm ngon gi&uacute;p c&uacute;n với h&agrave;m răng nhỏ chưa ph&aacute;t triển cứng c&aacute;p cũng c&oacute; thể ăn dễ d&agrave;ng. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kiểm so&aacute;t được khẩu phần ăn h&agrave;ng ng&agrave;y, tr&aacute;nh việc qu&aacute; nhiều để c&uacute;n bỏ lại thức ăn thừa. Nhanh ch&oacute;ng, tiện lợi, kh&ocirc;ng tốn nhiều thời gian chuẩn bị, Nhiều định lượng để bạn lựa chọn t&ugrave;y theo nhu cầu của c&uacute;n.</span></span></p>\r\n', '350.000', '325.000', './image/ff7abee6622343afa41192d22d682287.jpg', 2, 1, ''),
(28, 'Túi 15 Thanh Nhai Sạch Răng, Thơm Miệng Cho Chó VIRBAC Veggiedent Fresh, Hỗ Trợ Tiêu Hóa, Sạch Mảng Bám, Cao Răng', '<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Lợi &iacute;ch</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Chắc răng thơm miệng, tạm biệt cao răng ở c&uacute;n</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sạch s&acirc;u: Xử l&yacute; nguy&ecirc;n nh&acirc;n g&acirc;y h&ocirc;i miệng</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Thơm m&aacute;t: Hơi thở thơm m&aacute;t với cơ chế l&agrave;m lạnh</span></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&acirc;n bằng ti&ecirc;u h&oacute;a: Hỗ trợ ti&ecirc;u h&oacute;a to&agrave;n diện</span></span></li>\r\n</ul>\r\n', '200.000', '0.000', './image/c9bc8c8e075be2b00f79e389bbfd11e6.jpg', 2, 1, ''),
(29, 'Hanpet - (combo 3 gói) gói 1,5kg thức ăn dạng hạt cao cấp Smartheart Puppy (dành cho chó dưới 1 năm tuổi) ( 215c)', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Thức ăn d&agrave;nh cho ch&oacute; conđ&aacute;p ứng được nhu cầu dinh dưỡng cho ch&uacute; ch&oacute; con của bạn bằng c&aacute;ch sử dụng những th&agrave;nh phần c&oacute; chất lượng tốt nh ất, c&oacute; bổ sung, g&igrave;auv&agrave;, gi&agrave;u, gi&uacute;p tăng cường sự ph&aacute;t triển tr&iacute; n&atilde;o v&agrave; hệ thần kinh, tăng cường sức khỏe tim mạch.</span></span></p>\r\n', '450.000', '365.000', './image/combo3goithucandanghatcaocapsm-6901315a-80d6-4f19-9c5d-0b8b5591b275.png', 2, 1, ''),
(30, ' [1,5 kg] Thức ăn cho chó trưởng thành Ganador Fib\'s', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&ndash; Fib&rsquo;s l&agrave; một sản phẩm thức ăn đầy đủ v&agrave; c&acirc;n đối, được ph&aacute;t triển để đ&aacute;p ứng to&agrave;n bộ nhu cầu dinh dưỡng của Ch&oacute; trưởng th&agrave;nh, mọi giống v&agrave; mọi k&iacute;ch cỡ. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&ndash; Fib&rsquo;s c&oacute; c&ocirc;ng thức độc quyền gồm hỗn hợp c&aacute;c loại thịt kho&aacute;i khẩu của Ch&oacute;. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Fib&#39;s của c&oacute; hai h&igrave;nh dạng của xương &amp; tr&ograve;n cũng được thiết kế cho ch&oacute; trưởng th&agrave;nh m&agrave; kh&ocirc;ng c&oacute; chất tạo m&agrave;u nh&acirc;n tạo.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Lợi &iacute;ch ch&iacute;nh Fib&#39;s cho ch&oacute; trưởng th&agrave;nh l&agrave;: </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&acirc;n bằng dinh dưỡng</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cho da khỏe mạnh Xương khỏe mạnh</span></span></p>\r\n', '55.000', '0.000', './image/9bec6decf84fc96046a1e28b8d0472a8.jpg', 2, 1, ''),
(31, 'Thức ăn hạt cho Chó trưởng thành cỡ lớn | Royal Canin 1kg/ túi', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Royal Canin - Thức ăn hạt cho ch&oacute; cỡ lớn trưởng th&agrave;nh 1kg v&agrave; 4kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Royal Canin Maxi Adult Khả năng ti&ecirc;u h&oacute;a cao </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Gi&uacute;p th&uacute;c đẩy khả năng ti&ecirc;u h&oacute;a tốt với c&ocirc;ng thức độc quyền bao gồm protein chất lượng rất cao v&agrave; nguồn c&acirc;n bằng chất xơ trong khẩu phần ăn. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sức khoẻ xương khớp Gi&uacute;p xương hỗ trợ cho xương v&agrave; khớp khỏe mạnh của giống ch&oacute; lớn dưới sức nặng cơ thể.</span></span></p>\r\n', '210.000', '175.000', './image/bab35e9dc9238c509520b2c32e985e32.jpg', 2, 1, ''),
(32, 'Thức ăn hạt cho mèo trưởng thành Me-O Gold 400g', '', '59.000', '0.000', './image/hat-me-o-gold-indoor-cao-cap-cho-meo-truong-thanh-nuoi-trong-nha-paddy-3-800x.png', 2, 1, ''),
(33, 'HẠT ROYAL CANIN POODLE PUPPY 500G - THỨC ĂN CHÓ MÈO', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">ROYAL CANIN POODLE JUNIOR - D&Agrave;NH CHO CH&Oacute; DƯỚI 10 TH&Aacute;NG TUỔI </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">L&agrave; loại thức ăn kh&ocirc; cho ch&oacute; Poodle dưới 10 th&aacute;ng tuổi Thức ăn gi&uacute;p:</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">B&oacute;ng m&agrave;u l&ocirc;ng</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Th&agrave;nh phần Omega3 (EPA/DHA) v&agrave; Omega6 axit b&eacute;o tăng cường l&agrave;m đẹp l&ocirc;ng hiệu quả, tr&aacute;nh c&aacute;c bệnh vi&ecirc;m da. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Tăng cường Protein v&agrave; h&agrave;m lượng axit amin lưu huỳnh để đảm bảo sự tăng trưởng của l&ocirc;ng c&uacute;n</span></span></p>\r\n', '165.000', '0.000', './image/royal-canin-poodle-puppy-768-min.png', 2, 1, ''),
(34, 'Thức ăn chó Royal Canin CARDIAC Hổ Trợ Tim Mạch Cho Chó 2kg', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Polyphenol g&oacute;p phần l&agrave;m gi&atilde;n mạch v&agrave; gi&uacute;p trung h&ograve;a c&aacute;c gốc tự do.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Một nội dung phophorus vừa phải c&oacute; t&iacute;nh đến nguy cơ li&ecirc;n quan đến suy thận mạn t&iacute;nh. Hạn chế natri l&agrave;m giảm khối lượng c&ocirc;ng việc tr&ecirc;n tim.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nồng độ kali v&agrave; magi&ecirc; được điều chỉnh theo mức tối ưu. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Taurine v&agrave; L-Carnitine rất cần thiết cho chức năng tế b&agrave;o cơ v&agrave; tăng cường khả năng co b&oacute;p của tim.</span></span></p>\r\n', '574.000', '0.000', './image/2aed53024f0d1e6a4733f0ecf2526cb9.jpg', 2, 1, ''),
(35, 'Pate Royal Canin Renal Hỗ Trợ Chức Năng Thận Cho Chó (Lon 410g)', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">ROYAL CANIN RENAL CANINE WET - HỖ TRỢ CHỨC NĂNG THẬN CHO CH&Oacute; </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Thức ăn ướt hỗ trợ chức năng thận cho ch&oacute;. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sử dụng sản phẩm khi c&oacute; chỉ định từ B&aacute;c Sĩ Th&uacute; Y / Đọc kỹ hướng dẫn trước khi sử dụng. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kết hợp sử dụng với để đạt được kết quả tốt nhất.</span></span></p>\r\n', '99.000', '0.000', './image/petmall-t11-0711-3-renal-canine-loaf-60dfe2ac284349a1a81efaa8d0f1248a-master.png', 2, 1, ''),
(36, 'Hạt Royal Canin Medium Adult cho chó trưởng thành cỡ vừa', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">THỨC ĂN CHO CH&Oacute; ROYAL CANIN MEDIUM ADULT<br />\r\nD&agrave;nh cho ch&oacute; k&iacute;ch cỡ Medium (c&acirc;n nặng tối đa từ 11 - 25 kg) v&agrave; đang trong lứa tuổi Puppy từ 12 th&aacute;ng tuổi trở l&ecirc;n.<br />\r\nNhững giống ch&oacute; size Medium được biết đến c&oacute; nguồn năng lượng dồi d&agrave;o v&igrave; đa số giống ch&oacute; n&agrave;y được sử dụng hỗ trợ trong c&aacute;c c&ocirc;ng việc của con người từ rất l&acirc;u. Cho d&ugrave; giống ch&oacute; size Medium n&agrave;y được nu&ocirc;i trong nh&agrave; hay d&agrave;nh thời gian hoạt động ngo&agrave;i trời, với ROYAL CANIN Medium Adult sẽ gi&uacute;p giống ch&oacute; n&agrave;y duy tr&igrave; khả năng ph&ograve;ng thủ tự nhi&ecirc;n trong suốt v&ograve;ng đời của ch&uacute;ng, đồng thời cung cấp năng lượng c&acirc;n bằng cho giống ch&oacute; n&agrave;y gi&uacute;p duy tr&igrave; trọng lượng khỏe mạnh.<br />\r\n&bull; Gi&uacute;p duy tr&igrave; sức khoẻ ti&ecirc;u h&oacute;a<br />\r\n&bull; Hỗ trợ nhu cầu năng lượng cao cho sự tăng trưởng mạnh mẽ<br />\r\n&bull; Hỗ trợ sự ph&aacute;t triển xương<br />\r\n&bull; Hỗ trợ sức khỏe hệ thống miễn dịch<br />\r\n&bull; 100% dinh dưỡng c&acirc;n bằng<br />\r\n&bull; 100% đảm bảo an to&agrave;n</span></span></p>\r\n', '190.000', '139.000', './image/3260b42c45068aad43f528cd3cfdb34e.jpg', 2, 1, ''),
(37, 'LƯỚI THÉP SƠN TĨNH ĐIỆN LẮP GHÉP CHUỒNG CHÓ MÈO THÚ CƯNG ĐA NĂNG', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💫💫Một kh&ocirc;ng gian cho b&eacute; cưng NGỦ NGHỈ THOẢI M&Aacute;I </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💫💫Một nơi cho b&eacute; cưng c&oacute; thể ĐI VỆ SINH SẠCH SẼ m&agrave; kh&ocirc;ng ảnh hưởng đến kh&ocirc;ng gian ở của bạn </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💫💫Một nơi b&eacute; cưng c&oacute; thể CHẠY NHẢY CHƠI Đ&Ugrave;A c&ugrave;ng c&aacute;c bạn của m&igrave;nh m&agrave; bạn kh&ocirc;ng phải lo lắng mọi thứ trong nh&agrave; bị ph&aacute; tung</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">💫💫Hay đơn giản b&eacute; cưng qu&aacute; quấn bạn, bạn muốn t&igrave;m c&aacute;ch CHO B&Eacute; TỰ LẬP một t&iacute;</span></span></p>\r\n', '225.000', '199.000', './image/fc635b96f9091543c76d5440b0c0f799.jpg', 3, 1, ''),
(38, 'Nệm chó mèo  hình chữ nhật có thành', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nệm ch&oacute; m&egrave;o (3 SIZE) H&igrave;nh chữ nhật c&oacute; th&agrave;nh (M&agrave;u ngẫu nhi&ecirc;n) Nệm cho ch&oacute; / nệm ngủ cho ch&oacute; / nệm ngủ th&uacute; cưng / nệm ngủ cho m&egrave;o / thảm ch&oacute; / nệm th&uacute; cưng / thảm th&uacute; cưng / thảm ngủ / giường ch&oacute; / thảm l&oacute;t chuồng ch&oacute; / thảm nệm gi&aacute; rẻ cho ch&oacute; m&egrave;o</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">3 size</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Large - Đại (L) ch&oacute; dưới 15kg</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Medium - trung (M) ch&oacute; dưới 10kg</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Small - Tiểu (S) Ch&oacute; m&egrave;o dưới 5 kg</span></span></p>\r\n', '199.000', '0.000', './image/88ee81fda28ec9ff674c971c153802be.jpg-1200x1200q80.jpg-.png', 3, 1, ''),
(39, 'Võng mèo treo chuồng thú cưng 2 lớp nhiều màu', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">V&otilde;ng m&egrave;o treo chuồng th&uacute; cưng 2 lớp nhiều m&agrave;u, k&iacute;ch cỡ </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">V&otilde;ng c&oacute; 3 k&iacute;ch cỡ S (37x37cm); M (35x35cm) v&agrave; L (53x35cm) V&otilde;ng c&oacute; 2 lớp: </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">1 lớp b&ocirc;ng trắng v&agrave; 1 lớp vải in m&agrave;u họa tiết kh&aacute;c nhau </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&acirc;n nặng của m&egrave;o ph&ugrave; hợp như sau </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cỡ S d&agrave;nh cho b&eacute; &lt;1.5kg Cỡ </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M d&agrave;nh cho b&eacute; &lt;2.5kg Cỡ</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">L d&agrave;nh cho b&eacute; &lt;6kg</span></span></p>\r\n', '56.000', '0.000', './image/577c18e190bad7c2ad483cdfc3e9e487.jpg', 3, 1, ''),
(40, 'Bịch tã lót cho chó đi vệ sinh, tã giấy khay, lồng chuồng thú cưng, mèo siêu thấm hút 100 miếng/50 miếng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Thấm h&uacute;t nước tiểu của th&uacute; cưng rất nhanh ch&oacute;ng v&agrave; đ&oacute;ng kết lại</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Diệt khuẩn tốt, ngăn chặn sự ph&aacute;t triển của vi khuẩn </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Ph&ograve;ng chống nước tiểu (chất lỏng) tr&agrave;o ngược trở lại</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Bề mặt tr&ecirc;n của miếng l&oacute;t chuồng l&agrave; vải kh&ocirc;ng dệt, mềm mại, thấm h&uacute;t nhanh , lu&ocirc;n duy tr&igrave; được bề mặt kh&ocirc; tho&aacute;ng tạo cảm gi&aacute;c dễ chịu v&agrave; thoải m&aacute;i chc th&uacute; cưng của bạn </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Khử m&ugrave;i hiệu quả, gi&uacute;p ph&ograve;ng lu&ocirc;n sạch sẽ kh&ocirc;ng c&oacute; m&ugrave;i h&ocirc;i do th&uacute; cưng đi vệ sinh</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Luyện ch&oacute; m&egrave;o đi vệ sinh đ&uacute;ng chỗ, do m&ugrave;i hương thu h&uacute;t bản năng của ch&oacute; m&egrave;o</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Ph&ugrave; hợp cho tất cả c&aacute;c loại ch&oacute; m&egrave;o trong việc tập th&oacute;i quen đi vệ sinh đ&uacute;ng c&aacute;ch</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- C&oacute; thể trải dưới khay vệ sinh, dưới đ&aacute;y lồng, s&agrave;n xe hoặc trực tiếp tại nơi ch&oacute;/m&egrave;o đi vệ sinh</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Thuận tiện chăm s&oacute;c cho c&aacute;c b&eacute; mới sinh, c&ograve;n nhỏ</span></span></p>\r\n', '115.000', '99.000', './image/9971694c086fcfc789a538b49ee18dd9.jpg', 3, 1, ''),
(41, 'Tả Lót Chuồng thú cưng Than Hoạt tính dành cho ( chó & mèo ) 4 lốp 2.4kg', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">T&atilde; bỉm l&oacute;t vệ sinh cho ch&oacute; m&egrave;o than hoạt t&iacute;nh </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- miếng l&oacute;t vệ sinh ch&oacute; m&egrave;o Thấm h&uacute;t</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Kiểm so&aacute;t m&ugrave;i tr&ecirc;n 96 tiếng. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">T&Iacute;NH NĂNG: </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- H&uacute;t mạnh v&agrave; nhanh, biến nước tiểu th&agrave;nh gel, trong 2.5 gi&acirc;y. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Giữ kh&ocirc; sạch lu&ocirc;n. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Thấm h&uacute;t v&agrave; giữ nước b&ecirc;n trong với dung t&iacute;ch 350ml. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Kiểm so&aacute;t m&ugrave;i tr&ecirc;n 96 tiếng nhờ động t&aacute;c k&eacute;p của than hoạt t&iacute;nh v&agrave; bột tre. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Th&iacute;ch hợp cho việc huấn luyện c&uacute;n đi vệ sinh v&agrave; hỗ trợ đi vệ sinh cho ch&oacute; gi&agrave;.</span></span></p>\r\n', '245.000', '0.000', './image/106fdc4ae40b982ca10242596aa87cdb.jpg-720x720q80.jpg-.png', 3, 1, ''),
(42, 'chuồng Thú Cưng dành cho mèo', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Được l&agrave;m thủ c&ocirc;ng bằng lục b&igrave;nh ( b&egrave;o ) </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">bao gồm 2 đệm m&uacute;t đi k&egrave;m k&iacute;ch thước chiều cao 30 cm chiều d&agrave;i 34 cm chiều rộng 34 cm</span></span></p>\r\n', '220.000', '0.000', './image/screenshot-2022-12-12-125312.png', 3, 1, ''),
(43, 'BHFRF Nệm Thú Cưng Thoải Mái 80X50Cm Cho 5-20KG Đệm Thú Cưng Màu Xám Họa Tiết Hoa Hồng Cho Chó Mèo Trong Nhà', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">được l&agrave;m từ chất liệu nhung ngắn chất lượng cao, Nệm th&uacute; cưng c&oacute; chức năng thấm mồ h&ocirc;i, hơi thở để th&uacute; cưng của bạn kh&ocirc;ng oi bức trong khi nghỉ ngơi v&agrave; cung cấp một m&ocirc;i trường ngủ thoải m&aacute;i.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;Miếng l&oacute;t cho th&uacute; cưng chứa đầy b&ocirc;ng PP c&oacute; độ đ&agrave;n hồi cao chất lượng cao, rất mềm v&agrave; đ&agrave;n hồi, cho ph&eacute;p th&uacute; cưng của bạn ngủ thoải m&aacute;i hơn, mang đến cho th&uacute; cưng của bạn một ng&ocirc;i nh&agrave; thoải m&aacute;i v&agrave; an to&agrave;n, v&agrave; l&agrave;m cho ch&uacute;ng cảm thấy an to&agrave;n.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Mặt dưới của thảm m&egrave;o được l&agrave;m bằng vải oxford, chống thấm nước v&agrave; chống ẩm, v&igrave; vậy, đ&aacute;y kh&ocirc;ng dễ bị mốc hoặc ẩm ướt khi chạm đất bằng những giọt nước, k&eacute;o d&agrave;i tuổi thọ của thảm th&uacute; cưng một c&aacute;ch hiệu quả.</span></span></p>\r\n', '70.000', '0.000', './image/2382b8f3d837c180b83af2cfbf6387ff.jpg-360x360q75.jpg-.png', 3, 1, ''),
(44, 'Ổ Nệm Cho Thú cưng', '<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">☘ Ổ nệm cho ch&oacute; được thiết kế bằng chất liệu l&ocirc;ng nhung v&ocirc; c&ugrave;ng mềm mại v&agrave; ấm &aacute;p. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">☘ D&agrave;nh cho ch&oacute; m&egrave;o dưới 8kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">☘ M&agrave;u sắc: N&acirc;u. đỏ rượu, Hồng, Cam sữa </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">☘ Ổ nệm dễ giặt, mau kh&ocirc;</span></span></p>\r\n', '152.000', '126.000', './image/screenshot-2022-12-12-130130.png', 3, 1, ''),
(45, 'chuồng thú cưng lồng mèo lắp ghép', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">CHUỒNG CH&Oacute; M&Egrave;O LẮP GH&Eacute;P ĐA NĂNG GI&Aacute; RẺ</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">-K&iacute;ch thước chuồng th&uacute; cưng: ch&oacute;, m&egrave;o, chim, r&ugrave;a, bọ, thỏ, s&oacute;c, rồng,&hellip; :d&agrave;i 75 x rộng 75 x cao 140 cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">-Gồm:</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&bull;Lưới sắt 35x35cm: 27 tấm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&bull;Cửa lưới sắt m&oacute;c 35x35cm: 2 c&aacute;i</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&bull;Tấm nhựa 35x35cm: 9 tấm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&bull;Khung sắt 35x35cm (khung đỡ tầng b&ecirc;n trong chuồng, b&eacute; nằm k bị sập tầng) :3 c&aacute;i</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&bull; Thang:2 c&aacute;i</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&bull;Chốt nối :38 c&aacute;i</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&bull;Tặng k&egrave;m, b&uacute;a gỗ, d&acirc;y r&uacute;tLưới sắt c&oacute; 3 m&agrave;u: đen, trắng, hồng</span></span></p>\r\n', '699.000', '599.000', './image/199760d1989c8c8f237e4062423e6c26.jpg', 3, 1, ''),
(46, 'Cũi gỗ ngoài trời không thấm nước mưa bằng rắn nhà cho chó lạnh và ấm lớn Chuồng thú cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sau khi đặt h&agrave;ng, bạn c&oacute; thể thay đổi v&agrave; hủy đơn h&agrave;ng t&ugrave;y &yacute;, cửa h&agrave;ng sẽ kh&ocirc;ng phục vụ bạn nữa!</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Do &aacute;nh s&aacute;ng chụp v&agrave; sản phẩm thực tế, sản phẩm sẽ c&oacute; một ch&uacute;t sắc sai v&agrave; sẽ bị m&ograve;n một ch&uacute;t sau khi vận chuyển. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Cảm ơn bạn đ&atilde; gh&eacute; thăm cửa h&agrave;ng của ch&uacute;ng t&ocirc;i! ch&uacute;c bạn một cuộc sống hạnh ph&uacute;c! !</span></span></p>\r\n', '999.000', '799.000', './image/screenshot-2022-12-12-130906.png', 3, 1, ''),
(47, 'Ổ nệm cho thú cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&Ocirc; đệm được thiết kế v&oacute;i 2 m&agrave;u xanh v&agrave; x&aacute;m đặc biệt d&agrave;nh cho th&uacute; cưng nh&agrave; bạn </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kich thuoc 58*48 Ph&ugrave; hợp chi poodle,chihuahua,meo,nh&acirc;t...</span></span></p>\r\n', '250.000', '0.000', './image/screenshot-2022-12-12-131946.png', 3, 1, ''),
(48, 'Chuồng Thú 12 Tấm Tặng Kèm 24 Chốt, Tấm Lưới Thép Lắp Chuồng Cho Thú Cưng Chó Mèo', '<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kh&aacute;ch c&oacute; thể mở rộng chuồng bằng c&aacute;ch mua th&ecirc;m tấm lưới 1 c&aacute;ch dễ d&agrave;ng -K&iacute;ch thước mỗi tấm lưới l&agrave; 35x35cm, khi c&oacute; chốt th&agrave;nh 37x37cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">-Tấm lưới hợp kim phun sơn tĩnh điện c&oacute; đường k&iacute;nh 4mm, bảo đảm độ d&agrave;y chắc chắn v&agrave; ko rỉ set, chịu được pet c&agrave;o,cắn,... Lưới c&oacute; 2 m&agrave;u: ĐEN, TRẮNG K&iacute;ch thước: 37 x 37cm</span></span></p>\r\n', '275.000', '0.000', './image/screenshot-2022-12-12-132254.png', 3, 1, ''),
(49, 'Chén ăn cho chó mèo bát đôi ba nhựa đựng thức ăn khay có chai nước, phụ kiện thú cưng Con Mèo Xiêm', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">TH&Ocirc;NG TIN SẢN PHẨM Ch&eacute;n ăn cho ch&oacute; m&egrave;o b&aacute;t đ&ocirc;i nhựa đựng thức ăn c&oacute; khay nước, phụ kiện th&uacute; cưng </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Chất liệu: Nhựa</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- M&agrave;u sắc: Cam, Xanh lam, Đỏ, Đỏ Hồng, Xanh lục</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- K&iacute;ch thước: 34 x 22 x 27 cm </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- K&egrave;m chai nước h&igrave;nh m&egrave;o </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">ƯU ĐIỂM SẢN PHẨM Ch&eacute;n ăn cho ch&oacute; m&egrave;o b&aacute;t đ&ocirc;i nhựa đựng thức ăn c&oacute; khay nước, phụ kiện th&uacute; cưng </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Cung cấp nước sạch li&ecirc;n tục cho th&uacute; cưng </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- B&aacute;t lớn v&agrave; c&oacute; thể th&aacute;o rời để l&agrave;m sạch dễ d&agrave;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Van chống tr&agrave;n gi&uacute;p ngăn tr&agrave;n trong khi đổ đầy.</span></span></p>\r\n', '125.000', '115.000', './image/screenshot-2022-12-12-132437.png', 4, 1, ''),
(50, 'Đai yếm cho chó, đai lưng K9 - Phụ kiện thú cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Đai lưng K9 chuy&ecirc;n d&ugrave;ng cho ch&oacute; nghiệp vụ - CutePets </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- K&iacute;ch thước đai lưng cho ch&oacute;: </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Size S: V&ograve;ng ngực 43 - 55cm (Khoảng 4-12kg) </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Size M: V&ograve;ng ngực 50 - 60cm (Khoảng 12-20kg) </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Size L: V&ograve;ng ngực 65 - 85cm (Khoảng 20-25kg)</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Size XL: V&ograve;ng ngực 65 - 90cm (Khoảng 25-35kg) </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">+ Size XXL V&ograve;ng ngực 91-120cm (Khoảng 35-50kg) </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- m&agrave;u sắc: Đỏ, Đen</span></span></p>\r\n', '64.000', '0.000', './image/4ff03d7a1b32aaac6113ca6b5b922062.jpg', 4, 1, ''),
(51, 'Balo phi thuyền cho chó mèo chất da dành cho thú cưng - Phụ kiện thú cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Balo phi thuyền cho ch&oacute; m&egrave;o chất da d&agrave;nh cho th&uacute; cưng</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Balo cho ch&oacute; m&egrave;o chất liệu hỗ trợ chống thấm nước.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Thiết kế rộng, c&oacute; c&aacute;c lỗ tho&aacute;t kh&iacute; tho&aacute;ng m&aacute;t. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Trọng lượng balo t&uacute;i: 1 Kg</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Mức chứa tối đa: 8kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Th&ocirc;ng số kỹ thuật sản phẩm: 33x18x43cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Mặt k&iacute;nh trong suốt, vải Oxford, l&oacute;t nỉ b&ocirc;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Balo phi h&agrave;nh gia l&agrave; mẫu t&uacute;i, balo vận chuyển được sản xuất trong năm 2016 d&agrave;nh ri&ecirc;ng cho ch&oacute; m&egrave;o phảng phất h&igrave;nh d&aacute;ng của t&ecirc;n lửa.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Với sản phẩm n&agrave;y, ch&oacute; m&egrave;o cưng của bạn c&oacute; thể nh&igrave;n ra thế giới b&ecirc;n ngo&agrave;i dưới lăng k&iacute;nh của một phi h&agrave;nh gia v&agrave; tận hưởng phong cảnh, nh&igrave;n ngắm b&ecirc;n ng&ograve;ai, tương t&aacute;c với thế giới b&ecirc;n ngo&agrave;i</span></span></p>\r\n', '399.000', '316.000', './image/screenshot-2022-12-12-133039.png', 4, 1, '');
INSERT INTO `shop` (`id_shop`, `title`, `content`, `price`, `promotion`, `image`, `id_category`, `status_shop`, `slug`) VALUES
(52, 'Bát Ăn Dành Cho Chó Mèo – Mã BABS109 - Phụ Kiện Thú Cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sản phẩm: B&aacute;t Ăn D&agrave;nh Cho Ch&oacute; M&egrave;o </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&ndash; M&atilde; BABS109 </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&rarr; Chất liệu: Nhựa + với th&eacute;p kh&ocirc;ng gỉ </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&rarr; Th&ocirc;ng số kỹ thuật: D&agrave;i 33cm, rộng 18,8cm, cao 9,7cm, miệng b&aacute;t d&agrave;i 11cm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&rarr; M&agrave;u: Hồng / v&agrave;ng / xanh (trộn ngẫu nhi&ecirc;n)</span></span></p>\r\n', '64.000', '0.000', './image/screenshot-2022-12-12-133410.png', 4, 1, ''),
(53, 'DADAGOU Khăn Rằn Cho Chó Yếm Cho Chó Mèo Ông Già Noel Bắt Mắt Phụ Kiện Thú Cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Th&ocirc;ng qua thiết kế d&acirc;y đeo điều chỉnh được, chiếc yếm nước bọt cho th&uacute; cưng n&agrave;y c&oacute; thể ph&ugrave; hợp với hầu hết c&aacute;c họa tiết c&uacute;n cưng để mặc m&agrave; kh&ocirc;ng cảm thấy chật, g&aacute;nh nặng hoặc hạn chế hoạt động b&igrave;nh thường của ch&uacute;ng</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kh&ocirc;ng cảm thấy căng, g&aacute;nh nặng hoặc hạn chế c&aacute;c hoạt động b&igrave;nh thường của họa tiết c&uacute;n cưng khi đeo chiếc yếm nước bọt cho th&uacute; cưng n&agrave;y do đi k&egrave;m với d&acirc;y đeo điều chỉnh được.</span></span></p>\r\n', '56.000', '0.000', './image/ec198f92c768c7a21164d84851539416.jpg-340x340q80.jpg', 4, 1, ''),
(54, 'Vòng Cổ Nơ Hình Bông Tuyết Giáng Sinh Cho Thú Cưng Vòng Cổ Kẻ Sọc Phụ Kiện Thú Cưng Cho Chó Mèo Nhỏ Vừa Lớn', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Thiết kế với kh&ocirc;ng kh&iacute; gi&aacute;ng sinh mạnh mẽ c&oacute; thể th&ecirc;m phần th&uacute; vị v&agrave; l&agrave;m cho ch&uacute; m&egrave;o của bạn hấp dẫn hơn.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Thiết kế nơ, thời trang v&agrave; tiện lợi</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Được l&agrave;m từ chất liệu cao cấp, mềm mại v&agrave; thoải m&aacute;i, tay nghề tinh tế v&agrave; bền bỉ.</span></span></p>\r\n', '67.000', '49.000', './image/05bfa7c5fda5d35c384309279c80fe71-tn.jpg', 4, 1, ''),
(55, '20 Cái Túi Bọc Bảo Vệ Móng Cho Thú Cưng Tại Nhà Mũ Móng Vuốt Chó Nhiều Kích Cỡ Bảo Vệ Chăm Sóc Móng Cho Mèo', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">khi n&agrave;o sẽ bị tổn thương, h&atilde;y bảo vệ bản th&acirc;n hoặc người bạn đời kh&aacute;c bị tổn thương. l&agrave;m cho th&uacute; cưng dễ thương v&agrave; ngầu hơn v&agrave; bảo vệ thời trang cho chủ th&uacute; cưng khi-2. kh&ocirc;ng bao giờ lo lắng rằng bạn sẽ bị tổn thương nếu kh&ocirc;ng n&oacute; sẽ ph&aacute; hủy quần &aacute;o của anh ấy tr&aacute;nh trầy xước. Tr&ecirc;n s&agrave;n gỗ nội thất ph&aacute; hoại v&agrave; định nghĩa nh&agrave; ở-3. Chức năng mũ l&agrave;m m&oacute;ng cho th&uacute; cưng d&agrave;nh cho ch&oacute; v&agrave; m&egrave;o [bao gồm G&oacute;i]: 20 c&aacute;i-T&uacute;i x Mũ l&agrave;m m&oacute;ng cho ch&oacute; m&egrave;o </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">[LƯU &Yacute;] - 1. Do c&aacute;c l&ocirc; sản xuất kh&aacute;c nhau Chi tiết sản phẩm c&oacute; thể kh&aacute;c nhau một ch&uacute;t. Nếu bạn quan t&acirc;m đến sự kh&aacute;c biệt xin vui l&ograve;ng mua n&oacute; một c&aacute;ch cẩn thận. -2. Vui l&ograve;ng cho ph&eacute;p ch&ecirc;nh lệch 1-3cm do đo bằng tay</span></span></p>\r\n', '213.000', '199.000', './image/a40fb912294add3aff78ce918c5b0e61.jpg-525x525q80.jpg', 4, 1, ''),
(56, 'Balo Phi hành gia trong suốt dành cho thú cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Balo Phi h&agrave;nh gia trong suốt d&agrave;nh cho th&uacute; cưng </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- CutePets - K&iacute;ch thước: 34 x 25 x42 cm - Trọng lượng t&uacute;i: 1.2kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Chất lượng: nhựa PVC nguy&ecirc;n chất kh&ocirc;ng c&oacute; chất g&acirc;y hại</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- M&agrave;u sắc: đỏ,v&agrave;ng,xanh l&aacute;, xanh biển, đen ( inbox shop check m&agrave;u sen nh&eacute;) L&agrave; mẫu mới nhất 2018 được thiết kế nhiều lỗ th&ocirc;ng kh&iacute; v&agrave; lưới cho th&uacute; cưng một kh&ocirc;ng gian thoải m&aacute;i. Mặt trước balo bằng nhựa trong suốt gi&uacute;p th&uacute; cưng dễ d&agrave;ng tương t&aacute;c với thế giới b&ecirc;n ngo&agrave;i. Đặc biệt với 5 mầu thời trang, trẻ trung. Ch&oacute; th&iacute;ch n&oacute;, m&egrave;o th&iacute;ch n&oacute; v&agrave; bạn cũng sẽ th&iacute;ch n&oacute;!</span></span></p>\r\n', '210.000', '0.000', './image/78597dcad65b6faa29ff6997b9343884.jpg', 4, 1, ''),
(57, 'Nhuộm lông cho chó mèo Brooklyn 80g', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Brooklyn l&agrave; nhuộm l&ocirc;ng cho ch&oacute; cao cấp.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Sản phẩm c&oacute; m&agrave;u sắc tươi s&aacute;ng, thời trang v&agrave; độc đ&aacute;o c&oacute; độ giữ m&agrave;u si&ecirc;u bền giữ m&agrave;u sau khoảng hơn 50 lần tắm rửa cho ch&uacute; ch&oacute; của bạn. Sản phẩm ho&agrave;n to&agrave;n kh&ocirc;ng g&acirc;y độc hại v&agrave; ảnh hưởng tới sức khỏe của c&uacute;n cưng. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Với thu&ocirc;́c nhu&ocirc;̣m l&ocirc;ng Brooklyn bạn c&oacute; thể tự mua v&agrave; tự nhuộm l&ocirc;ng cho th&uacute; cưng, cực kỳ dễ d&agrave;ng với nhuộm l&ocirc;ng c&oacute; sẵn. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&aacute;ch nhuộm v&agrave; thời gian ủ giống như người. Cực kỳ an to&agrave;n v&agrave; th&acirc;n thiện cho ch&oacute;. Bạn c&oacute; thể tự mua v&agrave; tự nhuộm l&ocirc;ng cho th&uacute; cưng, cực kỳ dễ d&agrave;ng với nhuộm l&ocirc;ng c&oacute; sẵn.</span></span></p>\r\n', '85.000', '0.000', './image/a6c8af5d40ed159ac9f71def42feb102.jpg', 4, 1, ''),
(58, 'Áo thun sát nách - đồ cho chó mèo ', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">-Chất liệu vải: vải thun tho&aacute;ng m&aacute;t, &iacute;t b&aacute;m l&ocirc;ng th&iacute;ch hợp mặc h&egrave;, mặc đi chơi, đi ngủ,... </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Chất H&igrave;nh in/ h&igrave;nh th&ecirc;u: Chi tiết chắc chắn giặt m&aacute;y, v&ograve; tay kh&ocirc;ng hỏng, h&igrave;nh lỗi 1 đổi 1 freeship cho kh&aacute;ch </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- C&aacute;ch d&ugrave;ng: &Aacute;o thun s&aacute;t n&aacute;ch dễ d&agrave;ng sử dụng độ bền l&acirc;u - Sản phẩm c&oacute; size từ 1kg đến 17-18kg.</span></span></p>\r\n', '59.000', '0.000', './image/740898ce115a9a31d16fc55830553b5f.jpg', 5, 1, ''),
(59, 'ÁO CHÓ MÈO - Áo Hoodie Suprem cho Chó Mèo', '<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&Aacute;o Hoodie Supreme si&ecirc;u ngầu... si&ecirc;u chất 2 m&agrave;u đỏ - đen như h&igrave;nh </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">SIZE S: 2 - 3kg v&ograve;ng ngực 35cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">SIZE M: 3 - 4KG v&ograve;ng ngực 40cm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">SIZE L: 5 - 6KG v&ograve;ng ngực 45cm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">SIZE XL 7 - 8KG v&ograve;ng ngực 50cm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">SIZE XXL 9 - 11KG v&ograve;ng ngực 55cm</span></span></p>\r\n', '99.000', '69.000', './image/screenshot-2022-12-12-135436.png', 5, 1, ''),
(60, 'QUẦN ÁO CHÓ MÈO - ÁO mưa cho chó mèo', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- S : cho c&uacute;n từ 1-2,5kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- M : cho c&uacute;n từ 2-4kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- L : cho c&uacute;n từ 3,5-6kg</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- XL : cho c&uacute;n từ 5,5-8kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- XXL : cho c&uacute;n từ 8-11kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Lưu &yacute;: Số liệu tr&ecirc;n chỉ mang t&iacute;nh chất tương đối. Để chọn ch&iacute;nh x&aacute;c cỡ &aacute;o cho ch&oacute; m&egrave;o</span></span></p>\r\n', '89.000', '79.000', './image/d28b699219c2aa36aac9deaa5a6719b6.jpg', 5, 1, ''),
(61, 'Áo Chó Mèo Thu Đông, Áo Khủng Long Cho Chó Mèo Cực Ấm', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&Aacute;o Ch&oacute; M&egrave;o Thu Đ&ocirc;ng, &Aacute;o Khủng Long Ch&oacute; M&egrave;o Cực Ấm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">TH&Ocirc;NG TIN CHI TIẾT SẢN PHẨM </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&Aacute;o c&oacute; size S, M, l, XL,XXL cho c&aacute;c b&eacute; từ 1-10kg C&aacute;c b đo d&agrave;i lưng của b&eacute; để chọn size nha</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- S : 1-2,5kg d&agrave;i lưng ~25cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- M : 2.5kg-3.5kg d&agrave;i lưng ~30cm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- L : 3,5kg-4.5kg d&agrave;i lưng ~35cm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- XL : 4,5kg-7kg d&agrave;i lưng ~40cm </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- XXL : 7-10kg d&agrave;i lưng ~45cm</span></span></p>\r\n', '119.000', '99.000', './image/screenshot-2022-12-12-135828.png', 5, 1, ''),
(62, 'NCE7182 Trang Phục Thú Cưng Mềm Mại Ấm Áp Quần Áo Cho Mèo Chó Con Áo Khoác Cho Thú Cưng Áo Len', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">M&agrave;u sắc: cam, đỏ</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Chất liệu: Polyester</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">K&iacute;ch thước: XS/S/M/L/XL</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Loại th&uacute; cưng ph&ugrave; hợp: ch&oacute;, m&egrave;o</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">G&oacute;i bao gồm: 1 chiếc &aacute;o len cho th&uacute; cưng</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Phong c&aacute;ch: thời trang</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">C&aacute;c t&iacute;nh năng:</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Thiết kế nh&acirc;n bản l&agrave;m cho th&uacute; cưng của bạn đ&aacute;ng y&ecirc;u hơn, thời trang v&agrave; kh&aacute;c biệt.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">H&igrave;nh in cho th&uacute; cưng của bạn nhiều lời khen ngợi hơn.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Th&iacute;ch hợp cho du lịch m&ugrave;a đ&ocirc;ng, quần &aacute;o ở nh&agrave;.</span></span></p>\r\n', '129.000', '0.000', './image/d07e68b2906fee6cf913d6e7c15bc818.jpg', 5, 1, ''),
(63, 'QUẦN ÁO CHÓ MÈO - Áo Cho Chó Mèo Totoro 4 Chân', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- XS : &lt;1,2kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- S : cho c&uacute;n từ 1-2,5kg</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- M : cho c&uacute;n từ 2-4kg</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- L : cho c&uacute;n từ 3,5-6kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- XL : cho c&uacute;n từ 5,5-8kg </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- XXL : cho c&uacute;n từ 8-11kg</span></span></p>\r\n', '79.000', '0.000', './image/screenshot-2022-12-12-140216.png', 5, 1, ''),
(64, 'áo chó mèo cao cấp kiểu áo sơ mi sọc size l ( dành cho chó mèo 3 - 5kg)', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Chất liệu mềm mại, &ecirc;m &aacute;i, kh&ocirc;ng k&iacute;ch ứng da, ph&ugrave; hợp cho c&uacute;n y&ecirc;u nh&agrave; bạn</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">c&oacute;&nbsp;nhiều size cho mọi d&ograve;ng ch&oacute;, m&egrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">XS：&nbsp;&nbsp;Ngực 27cm - Lưng 15cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;S：&nbsp; Ngực 30cm - Lưng 18cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;M： &nbsp;Ngực 35cm - Lưng 23cm&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;L：&nbsp; Ngực 42cm - Lưng 28cm</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;XL：Ngực 47cm - Lưng 33cm&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">&nbsp;XXL：Ngực 52cm - Lưng 38cm</span></span></p>\r\n', '77.000', '0.000', './image/3264319adec95f85c05d4819affec0aa.jpg', 5, 1, ''),
(65, 'Áo nỉ chó mèo, áo chó mèo mùa đông', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Áo nỉ hoạ ti&ecirc;́t si&ecirc;u d&ecirc;̃ thương</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thi&ecirc;́t k&ecirc;́ áo 2 ch&acirc;n.Lớp ngoài nỉ dày dặn, b&ecirc;n trong lót b&ocirc;ng, thích hợp cho mùa thu đ&ocirc;ng</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Màu sắc và họa ti&ecirc;́t bắt mắt Th&ocirc;ng s&ocirc;́ size tham khảo:</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Size S: chi&ecirc;̀u dài lưng 22-25cm, vòng ngực 33-35cm</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Size M: chi&ecirc;̀u dài lưng 28-30cm, vòng ngực 37-39cm</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Size L: chi&ecirc;̀u dài lưng 35-37cm, vòng ngực 42-45cm</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Size XL: chi&ecirc;̀u dài lưng 38-41cm, vòng ngực 47-49cm</span></span></p>\r\n', '210.000', '0.000', './image/851c31022b091ad5d5856b58f8bbce50.jpg', 5, 1, ''),
(66, 'Quần Áo Cho Mèo Con Chó Con Thú Cưng Vòng Cổ Có Thể Điều Chỉnh Dây Dắt Dây Đai', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">K&iacute;ch thước (XS, ngực, chiều rộng, chiều d&agrave;i):24-27 cm,1.5 cm/120 cm;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">K&iacute;ch thước (S, ngực, chiều rộng, chiều d&agrave;i):29-33 cm,1.5 cm/120 cm;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">K&iacute;ch thước (M, ngực, chiều rộng, chiều d&agrave;i) 35-39 cm,1.5 cm/120 cm;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">K&iacute;ch thước (L, ngực, chiều rộng, chiều d&agrave;i):40-44 cm,1.5 cm/120 cm;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">K&iacute;ch thước (XL, ngực, rộng, d&agrave;i): 45-50 cm,1.5 cm/120 cm;</span></span></p>\r\n', '215.000', '199.000', './image/d3385c2a61ad079cbae74f50c11d264e.jpg-1200x1200q80.jpg-.png', 5, 1, ''),
(67, 'ÁO CHÓ MÈO - Áo nỉ có túi thắt nơ thỏ siêu đáng yêu cho chó mèo thú cưng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- C&uacute;n l&ocirc;ng d&agrave;y, th&acirc;n d&agrave;i th&igrave; 1-1.2kg-1.5kg c&oacute; thể vừa. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Nhưng nếu 2kg l&ocirc;ng d&agrave;y lưng d&agrave;i th&igrave; phải tăng l&ecirc;n 1 size l&agrave; size M. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- C&uacute;n người ngắn lưng ngắn l&ocirc;ng s&aacute;t th&igrave; c&oacute; thể 1,5-2kg vừa. </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Với m&egrave;o th&acirc;n d&agrave;i ngực nhỏ sẽ lấy size nhỏ hơn c&uacute;n v&agrave; chấp nhận ngắn lưng.</span></span></p>\r\n', '125.000', '0.000', './image/screenshot-2022-12-12-141054.png', 5, 1, ''),
(68, 'Áo Chó Mèo - Đầm Tiểu Thư Dễ Thương', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Chất liệu vải: vải &iacute;t b&aacute;m l&ocirc;ng th&iacute;ch hợp mặc đi chơi tết, lễ hội, chụp h&igrave;nh, d&atilde; ngoại, đi tiệc, mặc đi chơi, đi ngủ tuỳ th&iacute;ch... </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- C&aacute;ch d&ugrave;ng: Đầm c&oacute; n&uacute;t bấm dễ d&agrave;ng sử dụng độ bền l&acirc;u</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Sản phẩm ph&ugrave; hợp tất cả c&aacute;c d&ograve;ng ch&oacute;, m&egrave;o</span></span></p>\r\n', '226.000', '0.000', './image/149e843745fe69b2a8f7236e8826befe.jpg', 5, 1, ''),
(69, 'Áo chó mèo, áo dài tết vải gấm cho chó mèo họa tiết ngẫu nhiên', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Kh&aacute;ch chọn m&agrave;u, họa tiết của từng m&agrave;u được giao ngẫu nhi&ecirc;n </span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">Chất vải phi b&oacute;ng, m&aacute;t th&iacute;ch hợp mang v&agrave;o m&ugrave;a thu, m&ugrave;a đ&ocirc;ng cho c&aacute;c b&eacute;.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:16px\">- Size 2: dưới 2kg - Size 4: dưới 4kg - Size 6: dưới 6kg - Size 8: dưới 8kg - Size 10: dưới 10kg - Size 12: dưới 12kg</span></span></p>\r\n', '225.000', '205.000', './image/screenshot-2022-12-12-141505.png', 5, 1, '');

